import React, { useState, useEffect, useRef } from "react";

const Chat = ({ messages, onSendMessage, currentUser }) => {
    const [input, setInput] = useState("");
    const chatEndRef = useRef(null);

    const scrollToBottom = () => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (input.trim()) {
            onSendMessage(input.trim());
            setInput("");
        }
    };

    return (
        <div className="flex flex-col h-full bg-white">
            {/* Messages Area */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.length === 0 ? (
                    <div className="h-full flex flex-col items-center justify-center text-center opacity-40 px-6 mt-20">
                        <div className="w-16 h-16 bg-slate-100 rounded-full flex items-center justify-center mb-4">
                            <span className="text-2xl">ðŸ’¬</span>
                        </div>
                        <h4 className="font-bold text-slate-800 text-sm mb-1">Collaborative Chat</h4>
                        <p className="text-[11px] font-medium text-slate-500">Messages are synced with everyone in the room.</p>
                    </div>
                ) : (
                    messages.map((msg, index) => {
                        const isOwn = msg.sender === currentUser._id;
                        return (
                            <div
                                key={index}
                                className={`flex flex-col ${isOwn ? "items-end" : "items-start"}`}
                            >
                                <div className={`flex items-baseline gap-2 mb-1 ${isOwn ? "flex-row-reverse" : "flex-row"}`}>
                                    <span className="text-[10px] font-bold text-slate-400 uppercase tracking-wider">
                                        {msg.senderName}
                                    </span>
                                    <span className="text-[10px] text-slate-300 font-mono">
                                        {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                    </span>
                                </div>
                                <div
                                    className={`max-w-[90%] px-4 py-2 rounded-xl text-sm shadow-sm border
                                              ${isOwn
                                            ? "bg-violet-600 text-white border-violet-500 rounded-tr-none"
                                            : "bg-slate-50 text-slate-800 border-slate-100 rounded-tl-none"}`}
                                >
                                    {msg.text}
                                </div>
                            </div>
                        );
                    })
                )}
                <div ref={chatEndRef} />
            </div>

            {/* Input Area */}
            <div className="p-4 border-t border-slate-100 bg-slate-50/50">
                <form
                    className="flex bg-white border border-slate-200 rounded-xl overflow-hidden focus-within:ring-2 focus-within:ring-violet-500/20 focus-within:border-violet-500 transition-all"
                    onSubmit={handleSubmit}
                >
                    <input
                        type="text"
                        placeholder="Type a message..."
                        className="flex-1 bg-transparent px-4 py-2.5 text-sm text-slate-800 focus:outline-none placeholder:text-slate-400"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                    />
                    <button
                        type="submit"
                        disabled={!input.trim()}
                        className="px-4 text-violet-600 hover:text-violet-700 disabled:opacity-30 transition-colors"
                    >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2.5" d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8"></path>
                        </svg>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Chat;
