import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { roomService } from "../services/api";
import toast from "react-hot-toast";

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        const res = await roomService.getUserRooms();
        setRooms(res.data.rooms);
      } catch (err) {
        toast.error("Failed to load your rooms.");
      } finally {
        setLoading(false);
      }
    };
    fetchRooms();
  }, []);

  const handleCreateRoom = async () => {
    const roomName = prompt("Enter a name for your workspace:");
    if (!roomName) return;
    try {
      const res = await roomService.createRoom({ roomName });
      toast.success("Workspace created!");
      navigate(`/room/${res.data.room.roomId}`);
    } catch (err) {
      toast.error(err.response?.data?.message || "Failed to create workspace.");
    }
  };

  const handleJoinRoom = async () => {
    const roomId = prompt("Paste the Workspace ID:");
    if (!roomId) return;
    try {
      await roomService.joinRoom({ roomId });
      toast.success("Joined successfully!");
      navigate(`/room/${roomId}`);
    } catch (err) {
      toast.error(err.response?.data?.message || "Failed to join workspace.");
    }
  };

  return (
    <div className="min-h-screen bg-slate-950 flex flex-col">
      {/* Premium Navbar */}
      <nav className="glass sticky top-0 z-50 px-6 py-4 flex items-center justify-between border-b border-white/5">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 rounded-xl bg-gradient-to-tr from-primary-600 to-indigo-600 flex items-center justify-center shadow-lg shadow-primary-900/40">
            <span className="text-xl font-bold text-white">C</span>
          </div>
          <span className="text-xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-white to-slate-400">
            CollabBoard
          </span>
        </div>

        <div className="flex items-center gap-4">
          <button
            onClick={() => navigate("/profile")}
            className="flex items-center gap-3 px-3 py-1.5 rounded-xl hover:bg-white/5 transition-all text-slate-300 hover:text-white"
          >
            <div className="w-8 h-8 rounded-full bg-primary-600 flex items-center justify-center text-sm font-bold shadow-md">
              {user?.name?.[0]}
            </div>
            <span className="hidden sm:inline font-medium">{user?.name}</span>
          </button>
          <div className="w-px h-6 bg-white/10 mx-2"></div>
          <button
            onClick={logout}
            className="text-sm font-medium text-slate-400 hover:text-red-400 transition-colors"
          >
            Logout
          </button>
        </div>
      </nav>

      <main className="flex-1 max-w-7xl w-full mx-auto p-6 md:p-10">
        <header className="mb-12">
          <h1 className="text-4xl font-bold text-white mb-2 tracking-tight">Your Workspaces</h1>
          <p className="text-slate-400 text-lg">Create, manage, and collaborate in real-time.</p>
        </header>

        {/* Action Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-12">
          <div
            onClick={handleCreateRoom}
            className="group card-premium flex flex-col items-center justify-center text-center p-10 gap-4 cursor-pointer hover:scale-[1.02] active:scale-[0.98]"
          >
            <div className="w-16 h-16 rounded-2xl bg-primary-600/10 text-primary-500 flex items-center justify-center text-3xl group-hover:bg-primary-600 group-hover:text-white transition-all duration-300 shadow-inner">
              üìù
            </div>
            <div>
              <h2 className="text-xl font-bold text-white mb-1">Create Workspace</h2>
              <p className="text-slate-400 text-sm">Start a fresh canvas and invite others</p>
            </div>
          </div>

          <div
            onClick={handleJoinRoom}
            className="group card-premium flex flex-col items-center justify-center text-center p-10 gap-4 cursor-pointer hover:scale-[1.02] active:scale-[0.98]"
          >
            <div className="w-16 h-16 rounded-2xl bg-indigo-600/10 text-indigo-500 flex items-center justify-center text-3xl group-hover:bg-indigo-600 group-hover:text-white transition-all duration-300 shadow-inner">
              üîó
            </div>
            <div>
              <h2 className="text-xl font-bold text-white mb-1">Join via ID</h2>
              <p className="text-slate-400 text-sm">Jump into an existing collaboration</p>
            </div>
          </div>
        </div>

        {/* Content Section */}
        <div className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-2xl font-bold text-white flex items-center gap-2">
              Recent Projects
              {!loading && rooms.length > 0 && (
                <span className="text-sm font-medium bg-white/5 border border-white/10 px-2 py-0.5 rounded-full text-slate-400">
                  {rooms.length}
                </span>
              )}
            </h2>
          </div>

          {loading ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {[1, 2, 3].map(i => (
                <div key={i} className="card-premium h-48 animate-pulse bg-white/5 border-white/5"></div>
              ))}
            </div>
          ) : rooms.length === 0 ? (
            <div className="card-premium py-20 flex flex-col items-center justify-center text-center">
              <div className="text-6xl mb-6 opacity-20">üé®</div>
              <h3 className="text-xl font-bold text-slate-300 mb-2">No workspaces yet</h3>
              <p className="text-slate-500 mb-8 max-w-xs">Create your first collaborative whiteboard to start bringing ideas to life.</p>
              <button onClick={handleCreateRoom} className="btn-premium">Start Creating</button>
            </div>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {rooms.map((room) => (
                <div
                  className="group card-premium p-0 overflow-hidden cursor-pointer hover:scale-[1.02]"
                  key={room.roomId}
                  onClick={() => navigate(`/room/${room.roomId}`)}
                >
                  <div className="h-32 bg-slate-800 relative">
                    {/* Placeholder for real canvas preview if implemented */}
                    <div className="absolute inset-0 bg-gradient-to-br from-primary-600/20 to-indigo-600/20 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                      <span className="text-4xl opacity-50 bg-white/10 p-4 rounded-3xl blur-sm group-hover:blur-0 transition-all">üñºÔ∏è</span>
                    </div>
                  </div>
                  <div className="p-5">
                    <h3 className="text-lg font-bold text-white mb-1 line-clamp-1">{room.roomName}</h3>
                    <p className="text-xs font-mono text-slate-500 mb-4 opacity-70 group-hover:opacity-100 transition-opacity">ID: {room.roomId}</p>

                    <div className="flex items-center justify-between pt-4 border-t border-white/5">
                      <div className="flex -space-x-3 overflow-hidden">
                        {room.participants.slice(0, 3).map((p, i) => (
                          <div key={i} className="inline-block h-7 w-7 rounded-full ring-2 ring-slate-900 bg-slate-700 flex items-center justify-center text-[10px] font-bold text-white uppercase border border-white/20">
                            {p.name?.[0]}
                          </div>
                        ))}
                        {room.participants.length > 3 && (
                          <div className="inline-block h-7 w-7 rounded-full ring-2 ring-slate-900 bg-slate-800 flex items-center justify-center text-[10px] font-bold text-slate-400 border border-white/10">
                            +{room.participants.length - 3}
                          </div>
                        )}
                      </div>
                      <span className="text-xs text-slate-500 font-medium">
                        {room.host?._id === user._id ? "You are host" : `Host: ${room.host?.name}`}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
