import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { roomService } from "../services/api";
import toast from "react-hot-toast";
import {
  Layout,
  Plus,
  Users,
  Compass,
  Settings,
  LogOut,
  Search,
  Grid,
  List,
  ArrowRight,
  Activity,
  Clock,
  MoreVertical,
  User,
  Folder,
  Share2,
  Trash2,
  Bell,
  HardDrive
} from "lucide-react";

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newRoomName, setNewRoomName] = useState("");
  const [joinRoomId, setJoinRoomId] = useState("");
  const [isJoining, setIsJoining] = useState(false);
  const [deletingId, setDeletingId] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState("grid"); // 'grid' | 'list'

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const res = await roomService.getUserRooms();
      setRooms(res.data.rooms);
    } catch (err) {
      toast.error("Failed to load boards");
    } finally {
      setLoading(false);
    }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    if (!newRoomName.trim()) return;
    try {
      const res = await roomService.createRoom({ roomName: newRoomName });
      navigate(`/room/${res.data.room.roomId}`);
      toast.success("Board created!");
    } catch (err) {
      toast.error("Creation failed");
    }
  };

  const handleJoinRoom = async (e) => {
    e.preventDefault();
    if (!joinRoomId.trim()) return;

    setIsJoining(true);
    try {
      const roomFormatted = joinRoomId.trim().toUpperCase();
      await roomService.joinRoom({ roomId: roomFormatted });
      toast.success("Joined board!");
      navigate(`/room/${roomFormatted}`);
    } catch (err) {
      toast.error(err.response?.data?.message || "Board not found or access denied");
    } finally {
      setIsJoining(false);
    }
  };

  const handleDeleteRoom = async (roomId) => {
    setDeletingId(roomId);
    try {
      await roomService.deleteRoom(roomId);
      setRooms((prev) => prev.filter((r) => r.roomId !== roomId));
      toast.success("Board deleted");
    } catch (err) {
      toast.error(err.response?.data?.message || "Failed to delete");
    } finally {
      setDeletingId(null);
      setShowDeleteModal(null);
    }
  };

  const filteredRooms = rooms.filter((r) =>
    r.roomName.toLowerCase().includes(searchQuery.toLowerCase()) ||
    r.roomId.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const isHost = (room) =>
    room.host?.toString() === user?._id?.toString() || room.host === user?._id;

  const timeAgo = (date) => {
    const seconds = Math.floor((Date.now() - new Date(date)) / 1000);
    if (seconds < 60) return "just now";
    const minutes = Math.floor(seconds / 60);
    if (minutes < 60) return `${minutes}m ago`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h ago`;
    const days = Math.floor(hours / 24);
    if (days < 7) return `${days}d ago`;
    return new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  // Color palette for board preview backgrounds
  const boardColors = [
    "from-violet-500/20 to-purple-500/10",
    "from-blue-500/20 to-cyan-500/10",
    "from-emerald-500/20 to-teal-500/10",
    "from-amber-500/20 to-orange-500/10",
    "from-rose-500/20 to-pink-500/10",
    "from-indigo-500/20 to-sky-500/10",
  ];

  return (
    <div className="flex h-screen bg-[#F8FAFC]">
      {/*  Sidebar  */}
      <aside className="w-64 bg-[#1E293B] text-slate-300 flex flex-col shrink-0 border-r border-slate-800">
        <div className="p-6 flex items-center gap-3">
          <div className="w-9 h-9 rounded-xl bg-blue-600 flex items-center justify-center shadow-lg shadow-blue-500/20">
            <Layout className="text-white w-5 h-5" />
          </div>
          <span className="text-xl font-bold text-white tracking-tight">SyncSpace</span>
        </div>

        <nav className="flex-1 px-4 mt-4 space-y-1">
          <div className="text-[10px] font-bold text-slate-500 uppercase tracking-widest px-4 mb-2">Workspace</div>
          <button className="w-full flex items-center gap-3 px-4 py-2.5 bg-blue-600 text-white rounded-lg font-medium transition-all shadow-md shadow-blue-500/20">
            <Folder size={18} />
            Boards
          </button>
          <button className="w-full flex items-center gap-3 px-4 py-2.5 hover:bg-slate-800 hover:text-white rounded-lg font-medium transition-all group">
            <Users size={18} className="text-slate-400 group-hover:text-white" />
            My Team
          </button>
          <button className="w-full flex items-center gap-3 px-4 py-2.5 hover:bg-slate-800 hover:text-white rounded-lg font-medium transition-all group">
            <Activity size={18} className="text-slate-400 group-hover:text-white" />
            Recent Activity
          </button>

          <div className="pt-6 text-[10px] font-bold text-slate-500 uppercase tracking-widest px-4 mb-2">Account</div>
          <button className="w-full flex items-center gap-3 px-4 py-2.5 hover:bg-slate-800 hover:text-white rounded-lg font-medium transition-all group">
            <User size={18} className="text-slate-400 group-hover:text-white" />
            Profile
          </button>
          <button className="w-full flex items-center gap-3 px-4 py-2.5 hover:bg-slate-800 hover:text-white rounded-lg font-medium transition-all group">
            <Settings size={18} className="text-slate-400 group-hover:text-white" />
            Settings
          </button>
        </nav>

        <div className="p-4 mt-auto">
          <div className="bg-slate-800/50 rounded-xl p-4 border border-slate-700/50">
            <div className="flex items-center gap-3 mb-3">
              <div className="w-10 h-10 rounded-lg bg-blue-600/10 border border-blue-500/30 flex items-center justify-center text-blue-400 font-bold">
                {user?.name?.[0]?.toUpperCase()}
              </div>
              <div className="min-w-0">
                <div className="text-sm font-bold text-white truncate">{user?.name}</div>
                <div className="text-[10px] text-slate-500 truncate">{user?.email || 'Free Member'}</div>
              </div>
            </div>
            <button
              onClick={logout}
              className="w-full flex items-center justify-center gap-2 py-2 text-xs font-bold text-red-400 hover:text-red-300 bg-red-500/10 hover:bg-red-500/20 rounded-lg transition-all"
            >
              <LogOut size={14} />
              Sign Out
            </button>
          </div>
        </div>
      </aside>

      {/*  Main Content  */}
      <main className="flex-1 overflow-y-auto no-scrollbar">
        {/* Top bar */}
        <div className="h-16 bg-white border-b border-slate-200 sticky top-0 z-40 px-8 flex items-center justify-between">
          <div className="relative w-96">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-4 h-4" />
            <input
              type="text"
              placeholder="Search for boards, people or files..."
              className="w-full pl-10 pr-4 py-2 bg-slate-50 border border-slate-200 rounded-lg text-sm focus:outline-none focus:border-blue-500 focus:ring-4 focus:ring-blue-500/5 transition-all"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
          <div className="flex items-center gap-4">
            <button className="p-2 text-slate-400 hover:text-blue-600 transition-colors relative">
              <Bell size={20} />
              <span className="absolute top-2 right-2 w-2 h-2 bg-red-500 rounded-full border-2 border-white"></span>
            </button>
            <div className="w-px h-6 bg-slate-200 mx-2"></div>
            <div className="flex items-center gap-3">
              <div className="flex flex-col items-end">
                <span className="text-sm font-bold text-slate-900">{user?.name}</span>
                <span className="text-[10px] font-bold text-blue-600">Active Now</span>
              </div>
              <div className="w-10 h-10 rounded-full bg-slate-200 border-2 border-white shadow-sm overflow-hidden">
                <div className="w-full h-full flex items-center justify-center bg-blue-600 text-white font-bold text-sm">
                  {user?.name?.[0]?.toUpperCase()}
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="p-8 max-w-[1400px] mx-auto space-y-8">
          {/* Welcome Card */}
          <div className="relative bg-gradient-to-r from-blue-700 to-indigo-800 rounded-3xl p-10 overflow-hidden shadow-2xl shadow-blue-900/10">
            <div className="absolute right-0 top-0 bottom-0 w-1/2 bg-[url('https://www.transparenttextures.com/patterns/cubes.png')] opacity-10"></div>
            <div className="absolute -right-20 -top-20 w-80 h-80 bg-white/10 rounded-full blur-3xl"></div>
            <div className="relative z-10">
              <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-blue-500/20 text-blue-100 text-[10px] font-bold uppercase tracking-wider mb-4 border border-white/10 backdrop-blur-md">
                <Activity size={12} />
                Beta Preview 2.0
              </div>
              <h1 className="text-4xl font-black text-white mb-3">
                Good morning, {user?.name?.split(' ')[0]} 
              </h1>
              <p className="text-blue-100 text-lg font-medium opacity-90 max-w-xl">
                Create spaces where your team can collaborate, ideate, and build something amazing together in real-time.
              </p>
            </div>
          </div>

          {/* Stats Bar */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            {[
              { label: 'Total Boards', value: rooms.length, icon: HardDrive, color: 'text-blue-600', bg: 'bg-blue-100' },
              { label: 'My Boards', value: rooms.filter(isHost).length, icon: User, color: 'text-indigo-600', bg: 'bg-indigo-100' },
              { label: 'Shared with Me', value: rooms.filter(r => !isHost(r)).length, icon: Share2, color: 'text-cyan-600', bg: 'bg-cyan-100' },
              { label: 'Collaborators', value: [...new Set(rooms.flatMap(r => r.participants.map(p => p.userId)))].length, icon: Users, color: 'text-emerald-600', bg: 'bg-emerald-100' },
            ].map((stat, i) => (
              <div key={i} className="bg-white p-6 rounded-2xl border border-slate-200 shadow-sm hover:shadow-md transition-all flex items-center justify-between">
                <div>
                  <div className="text-[11px] font-bold text-slate-500 uppercase tracking-wider mb-1">{stat.label}</div>
                  <div className="text-2xl font-black text-slate-900">{stat.value}</div>
                </div>
                <div className={`w-12 h-12 ${stat.bg} ${stat.color} rounded-xl flex items-center justify-center shadow-lg shadow-inner`}>
                  <stat.icon size={24} />
                </div>
              </div>
            ))}
          </div>

          {/* Quick Actions Card Row */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mt-12">
            <div className="bg-white rounded-3xl p-10 border border-slate-200 shadow-xl shadow-slate-200/40 relative group overflow-hidden">
              <div className="absolute inset-0 bg-blue-600 translate-y-full group-hover:translate-y-[98%] transition-transform duration-500"></div>
              <h2 className="text-2xl font-black text-slate-900 mb-2 relative z-10">New Board</h2>
              <p className="text-slate-500 font-medium mb-8 relative z-10">Start from a clean canvas or a template</p>

              <form onSubmit={handleCreateRoom} className="flex gap-3 relative z-10">
                <input
                  type="text"
                  className="flex-1 px-6 py-4 bg-slate-50 border-2 border-slate-100 rounded-2xl text-base font-bold text-slate-800 placeholder:text-slate-400 focus:outline-none focus:border-blue-500 focus:bg-white transition-all shadow-inner"
                  placeholder="Enter board name..."
                  value={newRoomName}
                  onChange={(e) => setNewRoomName(e.target.value)}
                />
                <button
                  type="submit"
                  className="px-8 py-4 bg-blue-600 text-white rounded-2xl font-black text-sm uppercase tracking-widest shadow-lg shadow-blue-500/30 hover:bg-blue-700 active:scale-95 transition-all flex items-center gap-2"
                >
                  <Plus size={20} />
                  Create New
                </button>
              </form>
            </div>

            <div className="bg-blue-600 rounded-3xl p-10 border border-blue-500 shadow-xl shadow-blue-600/30 relative group overflow-hidden">
              <div className="absolute -right-20 -bottom-20 w-80 h-80 bg-white/10 rounded-full blur-3xl"></div>
              <h2 className="text-2xl font-black text-white mb-2 relative z-10">Join a Room</h2>
              <p className="text-blue-100 font-medium opacity-80 mb-8 relative z-10">Enter an existing workspace invitation</p>

              <form onSubmit={handleJoinRoom} className="flex gap-3 relative z-10">
                <input
                  type="text"
                  className="flex-1 px-6 py-4 bg-white/10 border-2 border-white/20 rounded-2xl text-base font-bold text-white placeholder:text-blue-200/50 focus:outline-none focus:border-white focus:bg-white/20 transition-all font-mono"
                  placeholder="Enter Room ID..."
                  value={joinRoomId}
                  onChange={(e) => setJoinRoomId(e.target.value)}
                />
                <button
                  type="submit"
                  disabled={isJoining || !joinRoomId.trim()}
                  className="px-8 py-4 bg-white text-blue-600 rounded-2xl font-black text-sm uppercase tracking-widest shadow-xl shadow-black/10 hover:bg-blue-50 active:scale-95 transition-all flex items-center gap-2 disabled:opacity-50"
                >
                  <ArrowRight size={20} />
                  {isJoining ? "Joining..." : "Enter Room ID"}
                </button>
              </form>
            </div>
          </div>

          {/* Boards Section */}
          <div className="pt-8">
            <div className="flex items-center justify-between mb-8">
              <div className="flex items-center gap-4">
                <h3 className="text-xl font-black text-slate-900 tracking-tight">Recent Boards</h3>
                <div className="h-6 w-px bg-slate-300"></div>
                <div className="flex items-center gap-3">
                  <button
                    onClick={() => setViewMode('grid')}
                    className={`p-2 rounded-lg transition-all ${viewMode === 'grid' ? 'bg-white text-blue-600 shadow-sm border border-slate-200' : 'text-slate-400 hover:text-slate-600'}`}
                  >
                    <Grid size={18} />
                  </button>
                  <button
                    onClick={() => setViewMode('list')}
                    className={`p-2 rounded-lg transition-all ${viewMode === 'list' ? 'bg-white text-blue-600 shadow-sm border border-slate-200' : 'text-slate-400 hover:text-slate-600'}`}
                  >
                    <List size={18} />
                  </button>
                </div>
              </div>
              <Link to="#" className="text-sm font-bold text-blue-600 hover:text-blue-700 flex items-center gap-1 group">
                View all boards
                <ArrowRight size={16} className="group-hover:translate-x-1 transition-transform" />
              </Link>
            </div>

            {loading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {[1, 2, 3, 4].map(i => (
                  <div key={i} className="h-60 bg-white rounded-3xl border border-slate-200 animate-pulse"></div>
                ))}
              </div>
            ) : filteredRooms.length === 0 ? (
              <div className="bg-white rounded-3xl border border-slate-200 p-20 flex flex-col items-center text-center shadow-sm">
                <div className="w-20 h-20 bg-slate-50 rounded-2xl flex items-center justify-center mb-6 border border-slate-100">
                  <Folder className="text-slate-300 w-10 h-10" />
                </div>
                <h4 className="text-xl font-bold text-slate-900 mb-2">No boards found</h4>
                <p className="text-slate-500 font-medium mb-8 max-w-sm">
                  {searchQuery ? "We couldn't find any boards matching your search. Try a different keyword." : "You haven't created or joined any boards yet. Start by creating one above!"}
                </p>
              </div>
            ) : viewMode === 'grid' ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {filteredRooms.map((room, idx) => (
                  <div key={room.roomId} className="group relative">
                    <Link to={`/room/${room.roomId}`}>
                      <div className="bg-white rounded-3xl border border-slate-200 shadow-sm hover:shadow-2xl hover:shadow-blue-500/10 hover:-translate-y-2 transition-all duration-300 overflow-hidden flex flex-col h-full border-b-[6px] border-b-blue-600">
                        <div className={`h-32 bg-gradient-to-br ${boardColors[idx % boardColors.length]} relative`}>
                          <div className="absolute inset-0 opacity-10 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')]"></div>
                          {isHost(room) && (
                            <div className="absolute top-4 left-4 px-2 py-1 bg-white/90 backdrop-blur-md rounded-lg text-[9px] font-black text-blue-600 uppercase tracking-widest shadow-sm">
                              Owner
                            </div>
                          )}
                          <div className="absolute bottom-[-16px] left-6 w-12 h-12 rounded-2xl bg-white shadow-lg flex items-center justify-center border border-slate-100">
                            <Layout size={24} className="text-blue-600" />
                          </div>
                        </div>
                        <div className="p-8 pt-10 flex-1 flex flex-col">
                          <h4 className="font-black text-slate-900 text-lg mb-2 group-hover:text-blue-600 transition-colors truncate">
                            {room.roomName}
                          </h4>
                          <div className="flex items-center gap-2 mb-6">
                            <Clock size={12} className="text-slate-400" />
                            <span className="text-xs font-bold text-slate-400 uppercase tracking-wider">{timeAgo(room.updatedAt)}</span>
                          </div>
                          <div className="mt-auto flex items-center justify-between">
                            <div className="flex -space-x-2">
                              {room.participants.slice(0, 4).map((p, i) => (
                                <div key={i} className="w-8 h-8 rounded-full border-2 border-white bg-slate-100 flex items-center justify-center text-[10px] font-bold text-slate-600 shadow-sm overflow-hidden">
                                  {p.name?.[0]?.toUpperCase()}
                                </div>
                              ))}
                              {room.participants.length > 4 && (
                                <div className="w-8 h-8 rounded-full border-2 border-white bg-blue-50 flex items-center justify-center text-[10px] font-black text-blue-600 shadow-sm">
                                  +{room.participants.length - 4}
                                </div>
                              )}
                            </div>
                            <span className="px-3 py-1 bg-slate-100 rounded-full text-[10px] font-black text-slate-500 uppercase tracking-tighter">
                              {room.roomId.slice(0, 8)}
                            </span>
                          </div>
                        </div>
                      </div>
                    </Link>
                    {isHost(room) && (
                      <button
                        onClick={(e) => { e.preventDefault(); e.stopPropagation(); setShowDeleteModal(room.roomId); }}
                        className="absolute top-4 right-4 p-2 bg-white/90 backdrop-blur-md rounded-xl text-slate-400 hover:text-red-500 hover:bg-red-50 transition-all opacity-0 group-hover:opacity-100 shadow-xl border border-white/50"
                      >
                        <Trash2 size={16} />
                      </button>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <div className="space-y-3">
                {filteredRooms.map((room) => (
                  <div key={room.roomId} className="group relative">
                    <Link to={`/room/${room.roomId}`}>
                      <div className="bg-white p-4 rounded-2xl border border-slate-200 shadow-sm hover:shadow-md hover:border-blue-500/30 transition-all flex items-center gap-6">
                        <div className="w-12 h-12 rounded-xl bg-blue-50 flex items-center justify-center text-blue-600 shadow-inner">
                          <Layout size={24} />
                        </div>
                        <div className="flex-1 min-w-0">
                          <h4 className="font-bold text-slate-900 group-hover:text-blue-600 transition-colors truncate">{room.roomName}</h4>
                          <div className="flex items-center gap-3 text-xs text-slate-400 font-bold uppercase tracking-wider">
                            <span>{room.roomId.slice(0, 8)}</span>
                            <span></span>
                            <span>{timeAgo(room.updatedAt)}</span>
                          </div>
                        </div>
                        <div className="flex -space-x-2 mr-4">
                          {room.participants.slice(0, 3).map((p, i) => (
                            <div key={i} className="w-8 h-8 rounded-full border-2 border-white bg-slate-100 flex items-center justify-center text-[10px] font-bold text-slate-600">
                              {p.name?.[0]?.toUpperCase()}
                            </div>
                          ))}
                        </div>
                        {isHost(room) && (
                          <button
                            onClick={(e) => { e.preventDefault(); e.stopPropagation(); setShowDeleteModal(room.roomId); }}
                            className="p-2 text-slate-300 hover:text-red-500 hover:bg-red-50 rounded-lg transition-all"
                          >
                            <Trash2 size={18} />
                          </button>
                        )}
                      </div>
                    </Link>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </main>

      {/*  Delete Modal  */}
      {showDeleteModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-6">
          <div className="absolute inset-0 bg-slate-900/60 backdrop-blur-md" onClick={() => setShowDeleteModal(null)}></div>
          <div className="relative bg-white rounded-[40px] p-10 max-w-md w-full shadow-2xl animate-in zoom-in-95 duration-200">
            <div className="w-20 h-20 bg-red-100 text-red-600 rounded-3xl flex items-center justify-center mb-8 mx-auto shadow-lg shadow-red-200/50">
              <Trash2 size={40} />
            </div>
            <h3 className="text-2xl font-black text-slate-900 text-center mb-4 leading-tight">Delete workspace?</h3>
            <p className="text-slate-500 text-center font-medium text-lg leading-relaxed mb-10">
              This action is <span className="text-emerald-500 font-black uppercase underline decoration-2 underline-offset-4">permanent</span>. All data in this board will be lost forever.
            </p>
            <div className="grid grid-cols-2 gap-4">
              <button
                onClick={() => setShowDeleteModal(null)}
                className="py-4 rounded-2xl bg-slate-100 text-slate-900 font-black uppercase tracking-widest text-xs hover:bg-slate-200 transition-all border-b-4 border-slate-300 active:border-b-0 active:translate-y-1"
              >
                Cancel
              </button>
              <button
                onClick={() => handleDeleteRoom(showDeleteModal)}
                disabled={deletingId === showDeleteModal}
                className="py-4 rounded-2xl bg-red-500 text-white font-black uppercase tracking-widest text-xs hover:bg-red-600 transition-all border-b-4 border-red-700 active:border-b-0 active:translate-y-1 disabled:opacity-50 shadow-lg shadow-red-200/50"
              >
                {deletingId === showDeleteModal ? "Deleting..." : "Confirm Delete"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
