import React from "react";

const Toolbar = ({ tool, setTool, color, setColor, size, setSize, onClear, isHost }) => {
    return (
        <div className="flex md:flex-col items-center justify-between md:justify-start gap-4 md:gap-6 w-full h-full px-4 md:px-0 md:py-6 overflow-x-auto md:overflow-y-auto no-scrollbar">
            {/* Tools Area */}
            <div className="flex md:flex-col gap-2 shrink-0">
                <button
                    className={`p-2.5 md:p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl
                              ${tool === "pencil" ? "bg-primary-600 text-white shadow-lg shadow-primary-900/30" : "bg-white/5 text-slate-400 hover:bg-white/10"}`}
                    onClick={() => setTool("pencil")}
                    title="Pencil"
                >
                    ‚úèÔ∏è
                </button>
                <button
                    className={`p-2.5 md:p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl
                              ${tool === "eraser" ? "bg-primary-600 text-white shadow-lg shadow-primary-900/30" : "bg-white/5 text-slate-400 hover:bg-white/10"}`}
                    onClick={() => setTool("eraser")}
                    title="Eraser"
                >
                    üßΩ
                </button>
                <button
                    className={`p-2.5 md:p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl bg-white/5 text-slate-400
                              ${isHost ? "hover:bg-red-500/20 hover:text-red-400" : "opacity-30 cursor-not-allowed"}`}
                    onClick={onClear}
                    title={isHost ? "Clear Board" : "Only host can clear"}
                    disabled={!isHost}
                >
                    üóëÔ∏è
                </button>
            </div>

            <div className="hidden md:block w-8 h-px bg-white/10 shrink-0"></div>
            <div className="block md:hidden w-px h-8 bg-white/10 shrink-0"></div>

            {/* History Area */}
            <div className="flex md:flex-col gap-2 shrink-0">
                <button
                    className="p-2.5 md:p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl bg-white/5 text-slate-400 hover:bg-white/10"
                    onClick={() => window.dispatchEvent(new CustomEvent('wb-undo'))}
                    title="Undo"
                >
                    ‚Ü©Ô∏è
                </button>
                <button
                    className="p-2.5 md:p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl bg-white/5 text-slate-400 hover:bg-white/10"
                    onClick={() => window.dispatchEvent(new CustomEvent('wb-redo'))}
                    title="Redo"
                >
                    ‚Ü™Ô∏è
                </button>
            </div>

            <div className="hidden md:block w-8 h-px bg-white/10 shrink-0"></div>
            <div className="block md:hidden w-px h-8 bg-white/10 shrink-0"></div>

            {/* Color Area */}
            <div className="flex items-center gap-4 shrink-0">
                <div className="relative group w-10 h-10 overflow-hidden rounded-full border border-white/10 bg-white/5 shrink-0">
                    <input
                        type="color"
                        value={color}
                        onChange={(e) => setColor(e.target.value)}
                        className="absolute inset-[-50%] w-[200%] h-[200%] cursor-pointer"
                        disabled={tool === "eraser"}
                    />
                    {tool === "eraser" && (
                        <div className="absolute inset-0 bg-slate-900/80 flex items-center justify-center">
                            <div className="w-4 h-0.5 bg-red-500 rotate-45"></div>
                        </div>
                    )}
                </div>

                {/* Size Area */}
                <div className="flex md:flex-col items-center gap-2">
                    <span className="text-[10px] font-bold text-slate-500 uppercase w-4 text-center">{size}</span>
                    <div className="w-20 md:w-8 h-8 md:h-20 flex items-center justify-center relative">
                        <input
                            type="range"
                            min="1"
                            max="20"
                            value={size}
                            onChange={(e) => setSize(parseInt(e.target.value))}
                            className="w-full h-1.5 accent-primary-600 bg-white/10 rounded-lg cursor-pointer appearance-none md:-rotate-90 absolute"
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Toolbar;
