import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { roomService } from "../services/api";
import toast from "react-hot-toast";

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newRoomName, setNewRoomName] = useState("");
  const [joinRoomId, setJoinRoomId] = useState("");
  const [deletingId, setDeletingId] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState("grid"); // 'grid' | 'list'

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const res = await roomService.getUserRooms();
      setRooms(res.data.rooms);
    } catch (err) {
      toast.error("Failed to load boards");
    } finally {
      setLoading(false);
    }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    if (!newRoomName.trim()) return;
    try {
      const res = await roomService.createRoom({ roomName: newRoomName });
      navigate(`/room/${res.data.room.roomId}`);
      toast.success("Board created!");
    } catch (err) {
      toast.error("Creation failed");
    }
  };

  const handleJoinRoom = async (e) => {
    e.preventDefault();
    if (!joinRoomId.trim()) return;
    try {
      await roomService.joinRoom({ roomId: joinRoomId });
      navigate(`/room/${joinRoomId}`);
    } catch (err) {
      toast.error("Board not found or access denied");
    }
  };

  const handleDeleteRoom = async (roomId) => {
    setDeletingId(roomId);
    try {
      await roomService.deleteRoom(roomId);
      setRooms((prev) => prev.filter((r) => r.roomId !== roomId));
      toast.success("Board deleted");
    } catch (err) {
      toast.error(err.response?.data?.message || "Failed to delete");
    } finally {
      setDeletingId(null);
      setShowDeleteModal(null);
    }
  };

  const filteredRooms = rooms.filter((r) =>
    r.roomName.toLowerCase().includes(searchQuery.toLowerCase()) ||
    r.roomId.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const isHost = (room) =>
    room.host?.toString() === user?._id?.toString() || room.host === user?._id;

  const timeAgo = (date) => {
    const seconds = Math.floor((Date.now() - new Date(date)) / 1000);
    if (seconds < 60) return "just now";
    const minutes = Math.floor(seconds / 60);
    if (minutes < 60) return `${minutes}m ago`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h ago`;
    const days = Math.floor(hours / 24);
    if (days < 7) return `${days}d ago`;
    return new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  // Color palette for board preview backgrounds
  const boardColors = [
    "from-violet-500/20 to-purple-500/10",
    "from-blue-500/20 to-cyan-500/10",
    "from-emerald-500/20 to-teal-500/10",
    "from-amber-500/20 to-orange-500/10",
    "from-rose-500/20 to-pink-500/10",
    "from-indigo-500/20 to-sky-500/10",
  ];

  return (
    <div className="min-h-screen bg-[#fafbfc] flex flex-col">
      {/* ─── Top Navigation ─── */}
      <nav className="sticky top-0 z-50 bg-white/80 backdrop-blur-xl border-b border-slate-200/60">
        <div className="max-w-7xl mx-auto px-6 lg:px-8 h-16 flex items-center justify-between">
          <div className="flex items-center gap-3 cursor-pointer" onClick={() => navigate('/')}>
            <div className="w-9 h-9 rounded-xl bg-gradient-to-br from-violet-600 to-indigo-600 flex items-center justify-center shadow-lg shadow-violet-200/50">
              <span className="text-white font-black text-sm">S</span>
            </div>
            <span className="text-lg font-extrabold text-slate-900 tracking-tight">SyncSpace</span>
          </div>

          <div className="flex items-center gap-4">
            <Link
              to="/profile"
              className="flex items-center gap-2.5 px-3 py-1.5 rounded-xl hover:bg-slate-100 transition-all group"
            >
              <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-violet-100 to-indigo-100 flex items-center justify-center text-xs font-bold text-violet-700 border border-violet-200/50 group-hover:from-violet-600 group-hover:to-indigo-600 group-hover:text-white group-hover:border-transparent transition-all">
                {user?.name?.[0]?.toUpperCase()}
              </div>
              <span className="text-sm font-semibold text-slate-700 hidden sm:block">{user?.name}</span>
            </Link>
            <div className="w-px h-8 bg-slate-200" />
            <button
              onClick={logout}
              className="text-xs font-bold text-slate-400 hover:text-red-500 transition-colors uppercase tracking-widest px-2 py-1.5 rounded-lg hover:bg-red-50"
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      <main className="flex-1 max-w-7xl w-full mx-auto px-6 lg:px-8 py-10">
        {/* ─── Hero Header ─── */}
        <header className="mb-10">
          <div className="flex flex-col sm:flex-row sm:items-end sm:justify-between gap-4">
            <div>
              <h1 className="text-3xl font-extrabold text-slate-900 tracking-tight mb-1">
                Welcome back, {user?.name?.split(' ')[0]}
              </h1>
              <p className="text-slate-500 font-medium text-[15px]">
                {rooms.length === 0 ? "Create your first board to get started." :
                  `You have ${rooms.length} active board${rooms.length !== 1 ? 's' : ''}.`}
              </p>
            </div>
            <div className="flex items-center gap-2 text-xs font-semibold text-slate-400">
              <div className="w-2 h-2 rounded-full bg-emerald-400 animate-pulse" />
              Online
            </div>
          </div>
        </header>

        {/* ─── Quick Actions ─── */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-5 mb-12">
          {/* Create New Board */}
          <div className="relative bg-white p-6 rounded-2xl border border-slate-200/80 shadow-sm hover:shadow-lg hover:shadow-violet-500/5 hover:border-violet-200 transition-all duration-300 group">
            <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-bl from-violet-400/5 to-transparent rounded-bl-full" />
            <div className="flex items-center gap-3 mb-5">
              <div className="w-10 h-10 bg-gradient-to-br from-violet-500 to-indigo-600 rounded-xl flex items-center justify-center shadow-md shadow-violet-200/50 group-hover:scale-105 transition-transform">
                <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2.5" d="M12 4v16m8-8H4" />
                </svg>
              </div>
              <div>
                <h2 className="text-base font-bold text-slate-900">New Board</h2>
                <p className="text-xs text-slate-400 font-medium">Start a fresh workspace</p>
              </div>
            </div>
            <form onSubmit={handleCreateRoom} className="flex gap-2.5">
              <input
                type="text"
                className="flex-1 bg-slate-50 border border-slate-200 rounded-xl px-4 py-2.5 text-sm focus:outline-none focus:border-violet-400 focus:ring-2 focus:ring-violet-100 transition-all font-medium placeholder:text-slate-400"
                placeholder="E.g. Sprint planning, Design review..."
                value={newRoomName}
                onChange={(e) => setNewRoomName(e.target.value)}
              />
              <button
                type="submit"
                className="px-5 py-2.5 bg-gradient-to-r from-violet-600 to-indigo-600 hover:from-violet-700 hover:to-indigo-700 text-white rounded-xl font-bold text-sm shadow-md shadow-violet-200/50 hover:shadow-lg hover:shadow-violet-300/50 active:scale-95 transition-all whitespace-nowrap"
              >
                Create
              </button>
            </form>
          </div>

          {/* Join Board */}
          <div className="relative bg-white p-6 rounded-2xl border border-slate-200/80 shadow-sm hover:shadow-lg hover:shadow-slate-200/50 hover:border-slate-300 transition-all duration-300 group">
            <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-bl from-slate-100/50 to-transparent rounded-bl-full" />
            <div className="flex items-center gap-3 mb-5">
              <div className="w-10 h-10 bg-gradient-to-br from-slate-100 to-slate-200 rounded-xl flex items-center justify-center shadow-sm group-hover:scale-105 transition-transform">
                <svg className="w-5 h-5 text-slate-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2.5" d="M13.828 10.172a4 4 0 00-5.656 0l-4 4a4 4 0 105.656 5.656l1.102-1.101m-.758-4.899a4 4 0 005.656 0l4-4a4 4 0 00-5.656-5.656l-1.1 1.1" />
                </svg>
              </div>
              <div>
                <h2 className="text-base font-bold text-slate-900">Join Board</h2>
                <p className="text-xs text-slate-400 font-medium">Enter with an invite code</p>
              </div>
            </div>
            <form onSubmit={handleJoinRoom} className="flex gap-2.5">
              <input
                type="text"
                className="flex-1 bg-slate-50 border border-slate-200 rounded-xl px-4 py-2.5 text-sm focus:outline-none focus:border-slate-400 focus:ring-2 focus:ring-slate-100 transition-all font-medium font-mono placeholder:font-sans placeholder:text-slate-400"
                placeholder="Paste invite code..."
                value={joinRoomId}
                onChange={(e) => setJoinRoomId(e.target.value)}
              />
              <button
                type="submit"
                className="px-5 py-2.5 bg-white border border-slate-200 hover:bg-slate-50 hover:border-slate-300 text-slate-700 rounded-xl font-bold text-sm shadow-sm active:scale-95 transition-all whitespace-nowrap"
              >
                Join
              </button>
            </form>
          </div>
        </div>

        {/* ─── Boards Section ─── */}
        <div>
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-4">
              <h3 className="text-sm font-extrabold text-slate-900 uppercase tracking-wider">
                Your Boards
              </h3>
              <span className="text-[10px] font-bold text-slate-400 bg-slate-100 px-2 py-0.5 rounded-full">
                {filteredRooms.length}
              </span>
            </div>
            <div className="flex items-center gap-3">
              {/* Search */}
              <div className="relative">
                <svg className="w-4 h-4 text-slate-400 absolute left-3 top-1/2 -translate-y-1/2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
                <input
                  type="text"
                  className="pl-9 pr-4 py-2 text-xs font-medium bg-white border border-slate-200 rounded-lg focus:outline-none focus:border-violet-400 w-52 transition-all placeholder:text-slate-400"
                  placeholder="Search boards..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
              {/* View Toggle */}
              <div className="flex items-center border border-slate-200 rounded-lg p-0.5 bg-white">
                <button
                  onClick={() => setViewMode("grid")}
                  className={`p-1.5 rounded-md transition-all ${viewMode === "grid" ? "bg-slate-100 text-slate-700" : "text-slate-400 hover:text-slate-600"}`}
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2V6zM14 6a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V6zM4 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2H6a2 2 0 01-2-2v-2zM14 16a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z" />
                  </svg>
                </button>
                <button
                  onClick={() => setViewMode("list")}
                  className={`p-1.5 rounded-md transition-all ${viewMode === "list" ? "bg-slate-100 text-slate-700" : "text-slate-400 hover:text-slate-600"}`}
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
                  </svg>
                </button>
              </div>
            </div>
          </div>

          {/* Board Cards */}
          {loading ? (
            <div className={viewMode === "grid"
              ? "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-5"
              : "flex flex-col gap-3"
            }>
              {[...Array(4)].map((_, i) => (
                <div key={i} className={`bg-white rounded-xl border border-slate-100 animate-pulse ${viewMode === "grid" ? "h-52" : "h-20"}`} />
              ))}
            </div>
          ) : filteredRooms.length === 0 ? (
            <div className="py-20 bg-white rounded-2xl border-2 border-dashed border-slate-200 flex flex-col items-center justify-center">
              <div className="w-16 h-16 bg-slate-50 rounded-2xl flex items-center justify-center mb-4">
                <svg className="w-8 h-8 text-slate-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
              </div>
              <p className="font-bold text-slate-500 mb-1">
                {searchQuery ? "No matching boards found." : "No boards yet"}
              </p>
              <p className="text-sm text-slate-400 font-medium">
                {searchQuery ? "Try a different search term." : "Create one above to start collaborating."}
              </p>
            </div>
          ) : viewMode === "grid" ? (
            /* ── Grid View ── */
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-5">
              {filteredRooms.map((room, idx) => (
                <div key={room._id} className="group relative">
                  <Link to={`/room/${room.roomId}`}>
                    <div className="bg-white rounded-2xl border border-slate-200/80 shadow-sm hover:shadow-xl hover:shadow-slate-200/50 hover:-translate-y-0.5 transition-all duration-300 h-full flex flex-col overflow-hidden">
                      {/* Preview Area */}
                      <div className={`h-28 bg-gradient-to-br ${boardColors[idx % boardColors.length]} relative overflow-hidden`}>
                        {/* Decorative Grid */}
                        <div className="absolute inset-0 opacity-[0.04]">
                          <svg width="100%" height="100%">
                            <defs>
                              <pattern id={`grid-${room._id}`} width="20" height="20" patternUnits="userSpaceOnUse">
                                <path d="M 20 0 L 0 0 0 20" fill="none" stroke="currentColor" strokeWidth="0.5" />
                              </pattern>
                            </defs>
                            <rect width="100%" height="100%" fill={`url(#grid-${room._id})`} />
                          </svg>
                        </div>
                        {/* Decorative shapes */}
                        <div className="absolute top-4 left-4 w-8 h-8 border-2 border-current opacity-[0.06] rounded-lg rotate-12" />
                        <div className="absolute bottom-4 right-6 w-12 h-6 border-2 border-current opacity-[0.06] rounded-full -rotate-6" />
                        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-5 h-5 border-2 border-current opacity-[0.06] rotate-45" />
                        {/* Host Badge */}
                        {isHost(room) && (
                          <div className="absolute top-3 right-3 px-2 py-0.5 bg-white/90 backdrop-blur-sm rounded-md text-[9px] font-bold text-violet-600 uppercase tracking-wider shadow-sm">
                            Owner
                          </div>
                        )}
                      </div>
                      {/* Details */}
                      <div className="p-4 flex-1 flex flex-col">
                        <h4 className="font-bold text-slate-800 text-sm group-hover:text-violet-600 transition-colors line-clamp-1 mb-2">
                          {room.roomName}
                        </h4>
                        <div className="mt-auto flex items-center justify-between">
                          <div className="flex items-center gap-2">
                            <span className="text-[10px] font-mono font-semibold text-slate-400 bg-slate-50 px-1.5 py-0.5 rounded">
                              {room.roomId.slice(0, 8)}
                            </span>
                          </div>
                          <div className="flex items-center gap-2">
                            <div className="flex -space-x-1.5">
                              {room.participants.slice(0, 3).map((p, i) => (
                                <div
                                  key={i}
                                  className="w-5 h-5 rounded-full bg-gradient-to-br from-slate-200 to-slate-300 border-2 border-white flex items-center justify-center text-[7px] font-bold text-slate-600"
                                >
                                  {p.name?.[0]?.toUpperCase() || '?'}
                                </div>
                              ))}
                              {room.participants.length > 3 && (
                                <div className="w-5 h-5 rounded-full bg-slate-100 border-2 border-white flex items-center justify-center text-[7px] font-bold text-slate-500">
                                  +{room.participants.length - 3}
                                </div>
                              )}
                            </div>
                            <span className="text-[10px] text-slate-400 font-medium">{timeAgo(room.updatedAt)}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </Link>
                  {/* Delete Button - only for host */}
                  {isHost(room) && (
                    <button
                      onClick={(e) => { e.preventDefault(); e.stopPropagation(); setShowDeleteModal(room.roomId); }}
                      className="absolute top-3 left-3 w-7 h-7 bg-white/90 backdrop-blur-sm rounded-lg border border-slate-200/50 flex items-center justify-center text-slate-400 hover:text-red-500 hover:bg-red-50 hover:border-red-200 opacity-0 group-hover:opacity-100 transition-all duration-200 shadow-sm"
                    >
                      <svg className="w-3.5 h-3.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                      </svg>
                    </button>
                  )}
                </div>
              ))}
            </div>
          ) : (
            /* ── List View ── */
            <div className="flex flex-col gap-2">
              {filteredRooms.map((room) => (
                <div key={room._id} className="group relative">
                  <Link to={`/room/${room.roomId}`}>
                    <div className="bg-white rounded-xl border border-slate-200/80 shadow-sm hover:shadow-md hover:border-violet-200 transition-all duration-200 p-4 flex items-center gap-4">
                      <div className="w-10 h-10 bg-gradient-to-br from-violet-100 to-indigo-100 rounded-xl flex items-center justify-center text-sm font-bold text-violet-600 shrink-0">
                        {room.roomName[0]?.toUpperCase()}
                      </div>
                      <div className="flex-1 min-w-0">
                        <h4 className="font-bold text-sm text-slate-800 group-hover:text-violet-600 transition-colors truncate">
                          {room.roomName}
                        </h4>
                        <div className="flex items-center gap-3 mt-0.5">
                          <span className="text-[10px] font-mono text-slate-400">{room.roomId.slice(0, 8)}</span>
                          {isHost(room) && (
                            <span className="text-[9px] font-bold text-violet-600 bg-violet-50 px-1.5 py-0.5 rounded uppercase">Owner</span>
                          )}
                        </div>
                      </div>
                      <div className="flex items-center gap-4 shrink-0">
                        <div className="flex -space-x-1.5">
                          {room.participants.slice(0, 3).map((p, i) => (
                            <div
                              key={i}
                              className="w-5 h-5 rounded-full bg-gradient-to-br from-slate-200 to-slate-300 border-2 border-white flex items-center justify-center text-[7px] font-bold text-slate-600"
                            >
                              {p.name?.[0]?.toUpperCase() || '?'}
                            </div>
                          ))}
                        </div>
                        <span className="text-[11px] text-slate-400 font-medium w-16 text-right">{timeAgo(room.updatedAt)}</span>
                      </div>
                    </div>
                  </Link>
                  {isHost(room) && (
                    <button
                      onClick={(e) => { e.preventDefault(); e.stopPropagation(); setShowDeleteModal(room.roomId); }}
                      className="absolute right-3 top-1/2 -translate-y-1/2 w-7 h-7 bg-white rounded-lg border border-slate-200 flex items-center justify-center text-slate-400 hover:text-red-500 hover:bg-red-50 hover:border-red-200 opacity-0 group-hover:opacity-100 transition-all duration-200"
                    >
                      <svg className="w-3.5 h-3.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                      </svg>
                    </button>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </main>

      {/* ─── Footer ─── */}
      <footer className="border-t border-slate-200/60 py-6 mt-auto">
        <div className="max-w-7xl mx-auto px-6 lg:px-8 flex items-center justify-between text-xs text-slate-400 font-medium">
          <span>&copy; {new Date().getFullYear()} SyncSpace</span>
          <div className="flex items-center gap-4">
            <span className="flex items-center gap-1.5">
              <div className="w-1.5 h-1.5 rounded-full bg-emerald-400" />
              All systems operational
            </span>
          </div>
        </div>
      </footer>

      {/* ─── Delete Confirmation Modal ─── */}
      {showDeleteModal && (
        <div className="fixed inset-0 z-[9999] flex items-center justify-center">
          <div
            className="absolute inset-0 bg-black/40 backdrop-blur-sm"
            onClick={() => setShowDeleteModal(null)}
          />
          <div className="relative bg-white rounded-2xl shadow-2xl shadow-black/10 p-6 w-full max-w-sm mx-4 animate-[modalAppear_0.2s_ease-out]">
            <div className="w-12 h-12 bg-red-50 rounded-xl flex items-center justify-center mx-auto mb-4">
              <svg className="w-6 h-6 text-red-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
              </svg>
            </div>
            <h3 className="text-lg font-bold text-slate-900 text-center mb-1">Delete Board</h3>
            <p className="text-sm text-slate-500 text-center font-medium mb-6">
              This action is permanent. All drawings, chat history, and files will be lost.
            </p>
            <div className="flex gap-3">
              <button
                onClick={() => setShowDeleteModal(null)}
                className="flex-1 py-2.5 border border-slate-200 text-sm font-bold text-slate-600 rounded-xl hover:bg-slate-50 transition-all active:scale-95"
              >
                Cancel
              </button>
              <button
                onClick={() => handleDeleteRoom(showDeleteModal)}
                disabled={deletingId === showDeleteModal}
                className="flex-1 py-2.5 bg-red-500 hover:bg-red-600 text-white text-sm font-bold rounded-xl shadow-md shadow-red-200/50 transition-all active:scale-95 disabled:opacity-60"
              >
                {deletingId === showDeleteModal ? (
                  <span className="flex items-center justify-center gap-2">
                    <svg className="w-4 h-4 animate-spin" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z" />
                    </svg>
                    Deleting...
                  </span>
                ) : "Delete Board"}
              </button>
            </div>
          </div>
        </div>
      )}

      <style>{`
        @keyframes modalAppear {
          from { opacity: 0; transform: scale(0.95) translateY(10px); }
          to { opacity: 1; transform: scale(1) translateY(0); }
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
