import React from "react";

const Toolbar = ({ tool, setTool, color, setColor, size, setSize, onClear }) => {
    return (
        <div className="toolbar">
            <div className="tool-section">
                <h3>Tools</h3>
                <div className="tool-buttons">
                    <button
                        className={`tool-btn ${tool === "pencil" ? "active" : ""}`}
                        onClick={() => setTool("pencil")}
                        title="Pencil"
                    >
                        ‚úèÔ∏è
                    </button>
                    <button
                        className={`tool-btn ${tool === "eraser" ? "active" : ""}`}
                        onClick={() => setTool("eraser")}
                        title="Eraser"
                    >
                        üßΩ
                    </button>
                    <button
                        className="tool-btn"
                        onClick={onClear}
                        title={isHost ? "Clear Board" : "Only host can clear"}
                        style={{ opacity: isHost ? 1 : 0.5, cursor: isHost ? "pointer" : "not-allowed" }}
                    >
                        üóëÔ∏è
                    </button>
                    <div className="tool-divider"></div>
                    <button
                        className="tool-btn"
                        onClick={() => window.dispatchEvent(new CustomEvent('wb-undo'))}
                        title="Undo"
                    >
                        ‚Ü©Ô∏è
                    </button>
                    <button
                        className="tool-btn"
                        onClick={() => window.dispatchEvent(new CustomEvent('wb-redo'))}
                        title="Redo"
                    >
                        ‚Ü™Ô∏è
                    </button>
                </div>
            </div>

            <div className="tool-section">
                <h3>Color</h3>
                <input
                    type="color"
                    value={color}
                    onChange={(e) => setColor(e.target.value)}
                    disabled={tool === "eraser"}
                />
            </div>

            <div className="tool-section">
                <h3>Size ({size})</h3>
                <input
                    type="range"
                    min="1"
                    max="20"
                    value={size}
                    onChange={(e) => setSize(parseInt(e.target.value))}
                />
            </div>
        </div>
    );
};

export default Toolbar;
