import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import toast from "react-hot-toast";
import { Layout, Mail, User, ShieldCheck, ArrowRight, ArrowLeft } from "lucide-react";

const Register = () => {
  const { register } = useAuth();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: ""
  });
  const [loading, setLoading] = useState(false);

  const { name, email, password, confirmPassword } = formData;

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      return toast.error("Passwords do not match");
    }

    setLoading(true);
    try {
      await register(name, email, password);
      toast.success("Account created successfully!");
      navigate("/dashboard");
    } catch (err) {
      toast.error(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleRegister = () => {
    toast.error("Google registration is currently being configured.");
  };

  return (
    <div className="h-screen w-full flex items-center justify-center bg-[#F8FAFC] overflow-hidden relative font-sans">
      {/* Abstract Background Shapes */}
      <div className="absolute top-0 left-0 w-full h-full overflow-hidden pointer-events-none">
        <div className="absolute top-[-15%] right-[-10%] w-[45%] h-[45%] bg-blue-400/10 rounded-full blur-[120px]"></div>
        <div className="absolute bottom-[-15%] left-[-10%] w-[45%] h-[45%] bg-indigo-400/10 rounded-full blur-[120px]"></div>
      </div>

      {/* Back Button */}
      <Link
        to="/"
        className="absolute top-8 left-8 flex items-center gap-2 text-slate-400 hover:text-blue-600 font-bold transition-all group z-20 hover:-translate-x-1"
      >
        <div className="w-10 h-10 rounded-xl bg-white border border-slate-200 flex items-center justify-center shadow-sm group-hover:border-blue-200 group-hover:shadow-blue-500/10">
          <ArrowLeft size={18} />
        </div>
        <span className="text-xs uppercase tracking-widest">Back</span>
      </Link>

      <div className="w-full max-w-[420px] px-6 relative z-10 -mt-12">
        <div className="text-center mb-6">
          <div className="inline-flex items-center gap-2.5 mb-4">
            <div className="w-10 h-10 rounded-xl bg-blue-600 flex items-center justify-center shadow-lg shadow-blue-500/20">
              <Layout className="text-white w-5 h-5" />
            </div>
            <span className="text-2xl font-black text-slate-900 tracking-tight">SyncSpace</span>
          </div>
          <h1 className="text-3xl font-black text-slate-900 tracking-tight mb-1">Create Account</h1>
          <p className="text-[10px] text-slate-400 font-black uppercase tracking-[0.2em]">Join high-performance teams</p>
        </div>

        <div className="bg-white p-8 rounded-[2.5rem] shadow-[0_20px_70px_-10px_rgba(37,99,235,0.1)] border border-slate-200/60 transition-all">
          <button
            onClick={handleGoogleRegister}
            className="w-full flex items-center justify-center gap-3 py-3.5 bg-white border border-slate-200 rounded-xl font-bold text-slate-700 hover:bg-slate-50 transition-all active:scale-95 group mb-6 shadow-sm"
          >
            <img src="https://www.google.com/favicon.ico" alt="Google" className="w-4 h-4 grayscale group-hover:grayscale-0 transition-all" />
            <span className="text-xs uppercase tracking-wider">Continue with Google</span>
          </button>

          <div className="relative mb-6">
            <div className="absolute inset-0 flex items-center"><div className="w-full border-t border-slate-100"></div></div>
            <div className="relative flex justify-center text-[10px] uppercase font-black tracking-widest text-slate-400">
              <span className="bg-white px-4">Direct Registration</span>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-1">
              <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Full Name</label>
              <div className="relative">
                <User className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 w-4 h-4" />
                <input
                  type="text"
                  name="name"
                  className="w-full pl-12 pr-4 py-2.5 bg-slate-50 border border-slate-200 rounded-xl text-sm font-bold text-slate-800 placeholder:text-slate-400 focus:outline-none focus:border-blue-500 focus:bg-white transition-all shadow-inner"
                  placeholder="John Doe"
                  value={name}
                  onChange={onChange}
                  required
                />
              </div>
            </div>

            <div className="space-y-1">
              <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Email Address</label>
              <div className="relative">
                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 w-4 h-4" />
                <input
                  type="email"
                  name="email"
                  className="w-full pl-12 pr-4 py-2.5 bg-slate-50 border border-slate-200 rounded-xl text-sm font-bold text-slate-800 placeholder:text-slate-400 focus:outline-none focus:border-blue-500 focus:bg-white transition-all shadow-inner"
                  placeholder="name@company.com"
                  value={email}
                  onChange={onChange}
                  required
                />
              </div>
            </div>

            <div className="grid grid-cols-2 gap-3">
              <div className="space-y-1">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Password</label>
                <input
                  type="password"
                  name="password"
                  className="w-full px-4 py-2.5 bg-slate-50 border border-slate-200 rounded-xl text-sm font-bold text-slate-800 focus:outline-none focus:border-blue-500 focus:bg-white transition-all shadow-inner"
                  placeholder="••••••••"
                  value={password}
                  onChange={onChange}
                  required
                />
              </div>
              <div className="space-y-1">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Confirm</label>
                <input
                  type="password"
                  name="confirmPassword"
                  className="w-full px-4 py-2.5 bg-slate-50 border border-slate-200 rounded-xl text-sm font-bold text-slate-800 focus:outline-none focus:border-blue-500 focus:bg-white transition-all shadow-inner"
                  placeholder="••••••••"
                  value={confirmPassword}
                  onChange={onChange}
                  required
                />
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full py-4 bg-gradient-to-r from-blue-600 to-blue-500 text-white rounded-xl font-black text-xs uppercase tracking-[0.15em] shadow-[0_10px_25px_-5px_rgba(37,99,235,0.4)] hover:shadow-[0_15px_30px_-5px_rgba(37,99,235,0.5)] hover:-translate-y-0.5 active:scale-[0.98] transition-all flex items-center justify-center gap-3 mt-4 relative overflow-hidden group"
            >
              <div className="absolute inset-0 bg-white/10 translate-y-full group-hover:translate-y-0 transition-transform duration-300"></div>
              {loading ? "Creating..." : "Create Free Account"}
              {!loading && <ShieldCheck size={18} className="group-hover:rotate-12 transition-transform" />}
            </button>
          </form>

          <p className="mt-8 text-center text-xs font-bold text-slate-500 uppercase tracking-wide">
            Have an account? <Link to="/login" className="text-blue-600 hover:text-blue-700 font-black">Sign in instead</Link>
          </p>
        </div>

        <div className="mt-8 flex items-center justify-center gap-6 opacity-30 grayscale saturate-0 pointer-events-none">
          <span className="text-[9px] font-black tracking-widest uppercase italic">Enterprise Ready</span>
          <span className="text-[9px] font-black tracking-widest uppercase">Safe & Secure</span>
        </div>
      </div>
    </div>
  );
};

export default Register;
