import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { roomService } from "../services/api";
import toast from "react-hot-toast";
import {
  Layout,
  Plus,
  Users,
  Compass,
  Settings,
  LogOut,
  Search,
  Grid,
  List,
  ArrowRight,
  Activity,
  Clock,
  MoreVertical,
  User,
  Folder,
  Share2,
  Trash2,
  Bell,
  HardDrive
} from "lucide-react";

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newRoomName, setNewRoomName] = useState("");
  const [joinRoomId, setJoinRoomId] = useState("");
  const [isJoining, setIsJoining] = useState(false);
  const [deletingId, setDeletingId] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState("grid"); // 'grid' | 'list'
  const [activeTab, setActiveTab] = useState("boards"); // 'boards' | 'team' | 'recent' | 'account' | 'profile' | 'settings'
  const [openMemberMenu, setOpenMemberMenu] = useState(null); // userId of open menu

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const res = await roomService.getUserRooms();
      setRooms(res.data.rooms);
    } catch (err) {
      toast.error("Failed to load boards");
    } finally {
      setLoading(false);
    }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    if (!newRoomName.trim()) return;
    try {
      const res = await roomService.createRoom({ roomName: newRoomName });
      navigate(`/room/${res.data.room.roomId}`);
      toast.success("Board created!");
    } catch (err) {
      toast.error("Creation failed");
    }
  };

  const handleJoinRoom = async (e) => {
    e.preventDefault();
    if (!joinRoomId.trim()) return;

    setIsJoining(true);
    try {
      const roomFormatted = joinRoomId.trim().toUpperCase();
      await roomService.joinRoom({ roomId: roomFormatted });
      toast.success("Joined board!");
      navigate(`/room/${roomFormatted}`);
    } catch (err) {
      toast.error(err.response?.data?.message || "Board not found or access denied");
    } finally {
      setIsJoining(false);
    }
  };

  const handleDeleteRoom = async (roomId) => {
    setDeletingId(roomId);
    try {
      await roomService.deleteRoom(roomId);
      setRooms((prev) => prev.filter((r) => r.roomId !== roomId));
      toast.success("Board deleted");
    } catch (err) {
      toast.error(err.response?.data?.message || "Failed to delete");
    } finally {
      setDeletingId(null);
      setShowDeleteModal(null);
    }
  };

  const filteredRooms = rooms.filter((r) =>
    r.roomName.toLowerCase().includes(searchQuery.toLowerCase()) ||
    r.roomId.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const isHost = (room) =>
    room.host?.toString() === user?._id?.toString() || room.host === user?._id;

  const timeAgo = (date) => {
    const seconds = Math.floor((Date.now() - new Date(date)) / 1000);
    if (seconds < 60) return "just now";
    const minutes = Math.floor(seconds / 60);
    if (minutes < 60) return `${minutes}m ago`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h ago`;
    const days = Math.floor(hours / 24);
    if (days < 7) return `${days}d ago`;
    return new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  // Extract unique team members from all boards
  const teamMembers = rooms.reduce((acc, room) => {
    room.participants.forEach(p => {
      if (!acc.find(m => m.userId === p.userId)) {
        acc.push({
          ...p,
          role: room.host === p.userId ? 'Owner' : 'Collaborator',
          lastSeen: room.updatedAt,
          boardsCount: rooms.filter(r => r.participants.some(part => part.userId === p.userId)).length
        });
      }
    });
    return acc;
  }, []);

  const renderTeamContent = () => (
    <div className="space-y-6 animate-in fade-in slide-in-from-bottom-4 duration-500">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-black text-slate-900 tracking-tight">Team Members</h2>
          <p className="text-sm text-slate-500 font-medium">Manage your workspace collaborators and their roles.</p>
        </div>
        <button className="px-5 py-2.5 bg-blue-600 text-white rounded-xl font-bold text-xs uppercase tracking-widest shadow-lg shadow-blue-500/20 hover:bg-blue-700 active:scale-95 transition-all flex items-center gap-2">
          <Plus size={16} />
          Invite Member
        </button>
      </div>

      <div className="bg-white rounded-3xl border border-slate-200 shadow-sm overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full text-left border-collapse">
            <thead>
              <tr className="bg-slate-50/50 border-b border-slate-100">
                <th className="px-6 py-4 text-[10px] font-black text-slate-400 uppercase tracking-widest">Member</th>
                <th className="px-6 py-4 text-[10px] font-black text-slate-400 uppercase tracking-widest">Role</th>
                <th className="px-6 py-4 text-[10px] font-black text-slate-400 uppercase tracking-widest">Boards</th>
                <th className="px-6 py-4 text-[10px] font-black text-slate-400 uppercase tracking-widest">Status</th>
                <th className="px-6 py-4 text-[10px] font-black text-slate-400 uppercase tracking-widest text-right">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-50">
              {teamMembers.length === 0 ? (
                <tr>
                  <td colSpan="5" className="px-6 py-12 text-center text-slate-400 font-medium italic">
                    No team members found. Start by creating a board!
                  </td>
                </tr>
              ) : teamMembers.map((member) => (
                <tr key={member.userId} className="hover:bg-slate-50/50 transition-colors group">
                  <td className="px-6 py-4">
                    <div className="flex items-center gap-3">
                      <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center text-blue-600 font-bold text-sm border-2 border-white shadow-sm ring-1 ring-slate-100">
                        {member.name?.[0]?.toUpperCase()}
                      </div>
                      <div>
                        <div className="text-sm font-bold text-slate-900">{member.name} {member.userId === user?._id && <span className="text-[10px] bg-slate-100 text-slate-500 px-1.5 py-0.5 rounded ml-1 font-black">YOU</span>}</div>
                        <div className="text-xs text-slate-500 font-medium">{member.email || 'No email provided'}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 text-sm font-medium">
                    <span className={`px-2.5 py-1 rounded-full text-[10px] font-black uppercase tracking-wider ${member.role === 'Owner' ? 'bg-indigo-100 text-indigo-600' : 'bg-slate-100 text-slate-600'}`}>
                      {member.role}
                    </span>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex items-center gap-2">
                      <Folder size={14} className="text-slate-400" />
                      <span className="text-sm font-bold text-slate-700">{member.boardsCount}</span>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex items-center gap-1.5">
                      <div className="w-2 h-2 rounded-full bg-emerald-500 shadow-[0_0_8px_rgba(16,185,129,0.5)]"></div>
                      <span className="text-[10px] font-black text-slate-400 uppercase tracking-wider">Active</span>
                    </div>
                  </td>
                  <td className="px-6 py-4 text-right relative">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        setOpenMemberMenu(openMemberMenu === member.userId ? null : member.userId);
                      }}
                      className="p-2 text-slate-300 hover:text-slate-600 hover:bg-white hover:shadow-md rounded-lg transition-all"
                    >
                      <MoreVertical size={16} />
                    </button>

                    {/* Floating Dropdown Menu */}
                    {openMemberMenu === member.userId && (
                      <>
                        <div className="fixed inset-0 z-[60]" onClick={() => setOpenMemberMenu(null)}></div>
                        <div className="absolute right-6 top-14 w-48 bg-white rounded-2xl shadow-2xl border border-slate-100 p-2 z-[70] animate-in fade-in zoom-in-95 duration-200">
                          <button className="w-full text-left px-3 py-2.5 hover:bg-slate-50 rounded-xl text-xs font-bold text-slate-600 flex items-center gap-2 group transition-all">
                            <User size={14} className="text-slate-400 group-hover:text-blue-600" />
                            View Profile
                          </button>
                          <button className="w-full text-left px-3 py-2.5 hover:bg-slate-50 rounded-xl text-xs font-bold text-slate-600 flex items-center gap-2 group transition-all">
                            <Settings size={14} className="text-slate-400 group-hover:text-blue-600" />
                            Change Role
                          </button>
                          <div className="h-px bg-slate-50 my-1 mx-2"></div>
                          <button className="w-full text-left px-3 py-2.5 hover:bg-red-50 rounded-xl text-xs font-bold text-red-500 flex items-center gap-2 group transition-all">
                            <Trash2 size={14} className="text-red-400 group-hover:text-red-600" />
                            Remove Member
                          </button>
                        </div>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderBoardsContent = () => (
    <div className="space-y-6 animate-in fade-in slide-in-from-bottom-4 duration-500">
      {/* Welcome Card */}
      <div className="relative bg-gradient-to-r from-blue-700 to-indigo-800 rounded-2xl p-6 overflow-hidden shadow-xl shadow-blue-900/10">
        <div className="absolute right-0 top-0 bottom-0 w-1/3 bg-[url('https://www.transparenttextures.com/patterns/cubes.png')] opacity-5"></div>
        <div className="relative z-10">
          <div className="inline-flex items-center gap-1.5 px-2 py-0.5 rounded-full bg-blue-500/20 text-blue-100 text-[9px] font-bold uppercase tracking-wider mb-2 border border-white/10 backdrop-blur-md">
            <Activity size={10} />
            Beta Preview 2.0
          </div>
          <h1 className="text-2xl font-black text-white mb-1.5">
            Good morning, {user?.name?.split(' ')[0]} 
          </h1>
          <p className="text-blue-100/80 text-sm font-medium max-w-lg">
            Create spaces where your team can collaborate and build something amazing together.
          </p>
        </div>
      </div>

      {/* Stats Bar */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
        {[
          { label: 'Total Boards', value: rooms.length, icon: HardDrive, color: 'text-blue-600', bg: 'bg-blue-100' },
          { label: 'My Boards', value: rooms.filter(isHost).length, icon: User, color: 'text-indigo-600', bg: 'bg-indigo-100' },
          { label: 'Shared', value: rooms.filter(r => !isHost(r)).length, icon: Share2, color: 'text-cyan-600', bg: 'bg-cyan-100' },
          { label: 'Collaborators', value: teamMembers.length, icon: Users, color: 'text-emerald-600', bg: 'bg-emerald-100' },
        ].map((stat, i) => (
          <div key={i} className="bg-white p-4 rounded-xl border border-slate-200 shadow-sm hover:shadow-md transition-all flex items-center justify-between">
            <div>
              <div className="text-[10px] font-bold text-slate-500 uppercase tracking-wider mb-0.5">{stat.label}</div>
              <div className="text-xl font-black text-slate-900">{stat.value}</div>
            </div>
            <div className={`w-10 h-10 ${stat.bg} ${stat.color} rounded-lg flex items-center justify-center`}>
              <stat.icon size={20} />
            </div>
          </div>
        ))}
      </div>

      {/* Quick Actions Row */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white rounded-2xl p-6 border border-slate-200 shadow-lg shadow-slate-200/30 relative group overflow-hidden">
          <h2 className="text-lg font-black text-slate-900 mb-1 relative z-10">New Board</h2>
          <p className="text-xs text-slate-500 font-medium mb-5 relative z-10">Start a fresh project from scratch</p>

          <form onSubmit={handleCreateRoom} className="flex gap-2 relative z-10">
            <input
              type="text"
              className="flex-1 px-4 py-2.5 bg-slate-50 border border-slate-200 rounded-xl text-sm font-bold text-slate-800 placeholder:text-slate-400 focus:outline-none focus:border-blue-500 focus:bg-white transition-all shadow-inner"
              placeholder="Enter name..."
              value={newRoomName}
              onChange={(e) => setNewRoomName(e.target.value)}
            />
            <button
              type="submit"
              className="px-5 py-2.5 bg-blue-600 text-white rounded-xl font-bold text-xs uppercase tracking-widest shadow-md shadow-blue-500/20 hover:bg-blue-700 active:scale-95 transition-all flex items-center gap-1.5"
            >
              <Plus size={16} />
              Create
            </button>
          </form>
        </div>

        <div className="bg-blue-600 rounded-2xl p-6 border border-blue-500 shadow-lg shadow-blue-600/20 relative group overflow-hidden">
          <h2 className="text-lg font-black text-white mb-1 relative z-10">Join Room</h2>
          <p className="text-xs text-blue-100/70 font-medium mb-5 relative z-10">Enter an existing invitation code</p>

          <form onSubmit={handleJoinRoom} className="flex gap-2 relative z-10">
            <input
              type="text"
              className="flex-1 px-4 py-2.5 bg-white/10 border border-white/20 rounded-xl text-sm font-bold text-white placeholder:text-blue-200/50 focus:outline-none focus:border-white focus:bg-white/20 transition-all font-mono"
              placeholder="Room ID..."
              value={joinRoomId}
              onChange={(e) => setJoinRoomId(e.target.value)}
            />
            <button
              type="submit"
              disabled={isJoining || !joinRoomId.trim()}
              className="px-5 py-2.5 bg-white text-blue-600 rounded-xl font-bold text-xs uppercase tracking-widest shadow-md shadow-black/10 hover:bg-blue-50 active:scale-95 transition-all flex items-center gap-1.5 disabled:opacity-50"
            >
              <ArrowRight size={16} />
              Join
            </button>
          </form>
        </div>
      </div>

      {/* Boards Section */}
      <div className="pt-2">
        <div className="flex items-center justify-between mb-5">
          <div className="flex items-center gap-3">
            <h3 className="text-lg font-black text-slate-900 tracking-tight">Recent Boards</h3>
            <div className="h-4 w-px bg-slate-300"></div>
            <div className="flex items-center gap-2">
              <button
                onClick={() => setViewMode('grid')}
                className={`p-1.5 rounded-lg transition-all ${viewMode === 'grid' ? 'bg-white text-blue-600 shadow-sm border border-slate-200' : 'text-slate-400 hover:text-slate-600'}`}
              >
                <Grid size={16} />
              </button>
              <button
                onClick={() => setViewMode('list')}
                className={`p-1.5 rounded-lg transition-all ${viewMode === 'list' ? 'bg-white text-blue-600 shadow-sm border border-slate-200' : 'text-slate-400 hover:text-slate-600'}`}
              >
                <List size={16} />
              </button>
            </div>
          </div>
          <Link to="#" className="text-xs font-bold text-blue-600 hover:text-blue-700 flex items-center gap-1 group">
            All Boards
            <ArrowRight size={14} className="group-hover:translate-x-1 transition-transform" />
          </Link>
        </div>

        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
            {[1, 2, 3, 4].map(i => (
              <div key={i} className="h-48 bg-white rounded-2xl border border-slate-200 animate-pulse"></div>
            ))}
          </div>
        ) : filteredRooms.length === 0 ? (
          <div className="bg-white rounded-2xl border border-slate-200 p-12 flex flex-col items-center text-center">
            <div className="w-14 h-14 bg-slate-50 rounded-xl flex items-center justify-center mb-4">
              <Folder className="text-slate-300 w-7 h-7" />
            </div>
            <h4 className="text-base font-bold text-slate-900">No boards</h4>
          </div>
        ) : viewMode === 'grid' ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-5">
            {filteredRooms.map((room, idx) => (
              <div key={room.roomId} className="group relative">
                <Link to={`/room/${room.roomId}`}>
                  <div className="bg-white rounded-2xl border border-slate-200 shadow-sm hover:shadow-xl hover:shadow-blue-500/10 hover:-translate-y-1 transition-all duration-300 overflow-hidden flex flex-col h-full border-b-4 border-b-blue-600">
                    <div className={`h-24 bg-gradient-to-br ${boardColors[idx % boardColors.length]} relative`}>
                      <div className="absolute inset-0 opacity-5 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')]"></div>
                      <div className="absolute bottom-[-12px] left-4 w-9 h-9 rounded-xl bg-white shadow-md flex items-center justify-center border border-slate-100">
                        <Layout size={18} className="text-blue-600" />
                      </div>
                    </div>
                    <div className="p-5 pt-6 flex-1 flex flex-col">
                      <h4 className="font-bold text-slate-800 text-sm mb-1 group-hover:text-blue-600 transition-colors truncate">
                        {room.roomName}
                      </h4>
                      <div className="flex items-center gap-1.5 mb-4">
                        <Clock size={10} className="text-slate-400" />
                        <span className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">{timeAgo(room.updatedAt)}</span>
                      </div>
                      <div className="mt-auto flex items-center justify-between">
                        <div className="flex -space-x-1.5">
                          {room.participants.slice(0, 3).map((p, i) => (
                            <div key={i} className="w-6 h-6 rounded-full border-2 border-white bg-slate-100 flex items-center justify-center text-[8px] font-bold text-slate-600 overflow-hidden">
                              {p.name?.[0]?.toUpperCase()}
                            </div>
                          ))}
                        </div>
                        <span className="px-1.5 py-0.5 bg-slate-100 rounded text-[9px] font-bold text-slate-500">
                          {room.roomId.slice(0, 6)}
                        </span>
                      </div>
                    </div>
                  </div>
                </Link>
                {isHost(room) && (
                  <button
                    onClick={(e) => { e.preventDefault(); e.stopPropagation(); setShowDeleteModal(room.roomId); }}
                    className="absolute top-2 right-2 p-1.5 bg-white/90 rounded-lg text-slate-400 hover:text-red-500 opacity-0 group-hover:opacity-100 transition-all border border-slate-100"
                  >
                    <Trash2 size={14} />
                  </button>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-2">
            {filteredRooms.map((room) => (
              <div key={room.roomId} className="group relative">
                <Link to={`/room/${room.roomId}`}>
                  <div className="bg-white p-3 rounded-xl border border-slate-200 shadow-sm hover:shadow-md hover:border-blue-500/30 transition-all flex items-center gap-4">
                    <div className="w-9 h-9 rounded-lg bg-blue-50 flex items-center justify-center text-blue-600 shrink-0">
                      <Layout size={18} />
                    </div>
                    <div className="flex-1 min-w-0">
                      <h4 className="font-bold text-sm text-slate-900 group-hover:text-blue-600 transition-colors truncate">{room.roomName}</h4>
                      <div className="flex items-center gap-2 text-[10px] text-slate-400 font-bold uppercase tracking-wider">
                        <span>{room.roomId.slice(0, 8)}</span>
                        <span></span>
                        <span>{timeAgo(room.updatedAt)}</span>
                      </div>
                    </div>
                    <div className="flex -space-x-1.5 mr-3">
                      {room.participants.slice(0, 3).map((p, i) => (
                        <div key={i} className="w-6 h-6 rounded-full border border-white bg-slate-100 flex items-center justify-center text-[8px] font-bold text-slate-600">
                          {p.name?.[0]?.toUpperCase()}
                        </div>
                      ))}
                    </div>
                    {isHost(room) && (
                      <button
                        onClick={(e) => { e.preventDefault(); e.stopPropagation(); setShowDeleteModal(room.roomId); }}
                        className="p-1.5 text-slate-300 hover:text-red-500 rounded-lg transition-all"
                      >
                        <Trash2 size={16} />
                      </button>
                    )}
                  </div>
                </Link>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

  // Color palette for board preview backgrounds
  const boardColors = [
    "from-violet-500/20 to-purple-500/10",
    "from-blue-500/20 to-cyan-500/10",
    "from-emerald-500/20 to-teal-500/10",
    "from-amber-500/20 to-orange-500/10",
    "from-rose-500/20 to-pink-500/10",
    "from-indigo-500/20 to-sky-500/10",
  ];

  return (
    <div className="flex h-screen bg-[#F8FAFC]">
      {/*  Sidebar  */}
      <aside className="w-56 bg-[#1E293B] text-slate-300 flex flex-col shrink-0 border-r border-slate-800">
        <div className="p-4 flex items-center gap-2.5">
          <div className="w-8 h-8 rounded-lg bg-blue-600 flex items-center justify-center shadow-lg shadow-blue-500/20">
            <Layout className="text-white w-4 h-4" />
          </div>
          <span className="text-lg font-bold text-white tracking-tight">SyncSpace</span>
        </div>

        <nav className="flex-1 px-3 mt-2 space-y-0.5">
          <div className="text-[9px] font-bold text-slate-500 uppercase tracking-widest px-4 mb-1.5 opacity-60">Workspace</div>
          <button
            onClick={() => setActiveTab('boards')}
            className={`w-full flex items-center gap-2.5 px-4 py-2 rounded-lg text-sm font-medium transition-all ${activeTab === 'boards' ? 'bg-blue-600 text-white shadow-md shadow-blue-500/20' : 'text-slate-400 hover:bg-slate-800 hover:text-white group'}`}
          >
            <Folder size={16} className={activeTab === 'boards' ? 'text-white' : 'text-slate-400 group-hover:text-white'} />
            Boards
          </button>
          <button
            onClick={() => setActiveTab('team')}
            className={`w-full flex items-center gap-2.5 px-4 py-2 rounded-lg text-sm font-medium transition-all ${activeTab === 'team' ? 'bg-blue-600 text-white shadow-md shadow-blue-500/20' : 'text-slate-400 hover:bg-slate-800 hover:text-white group'}`}
          >
            <Users size={16} className={activeTab === 'team' ? 'text-white' : 'text-slate-400 group-hover:text-white'} />
            My Team
          </button>
          <button
            onClick={() => setActiveTab('recent')}
            className={`w-full flex items-center gap-2.5 px-4 py-2 rounded-lg text-sm font-medium transition-all ${activeTab === 'recent' ? 'bg-blue-600 text-white shadow-md shadow-blue-500/20' : 'text-slate-400 hover:bg-slate-800 hover:text-white group'}`}
          >
            <Activity size={16} className={activeTab === 'recent' ? 'text-white' : 'text-slate-400 group-hover:text-white'} />
            Recent
          </button>

          <div className="pt-4 text-[9px] font-bold text-slate-500 uppercase tracking-widest px-4 mb-1.5 opacity-60">Personal</div>
          <button
            onClick={() => setActiveTab('account')}
            className={`w-full flex items-center gap-2.5 px-4 py-2 rounded-lg text-sm font-medium transition-all ${activeTab === 'account' ? 'bg-blue-600 text-white shadow-md shadow-blue-500/20' : 'text-slate-400 hover:bg-slate-800 hover:text-white group'}`}
          >
            <HardDrive size={16} className={activeTab === 'account' ? 'text-white' : 'text-slate-400 group-hover:text-white'} />
            Account
          </button>
          <button
            onClick={() => setActiveTab('profile')}
            className={`w-full flex items-center gap-2.5 px-4 py-2 rounded-lg text-sm font-medium transition-all ${activeTab === 'profile' ? 'bg-blue-600 text-white shadow-md shadow-blue-500/20' : 'text-slate-400 hover:bg-slate-800 hover:text-white group'}`}
          >
            <User size={16} className={activeTab === 'profile' ? 'text-white' : 'text-slate-400 group-hover:text-white'} />
            Profile
          </button>
          <button
            onClick={() => setActiveTab('settings')}
            className={`w-full flex items-center gap-2.5 px-4 py-2 rounded-lg text-sm font-medium transition-all ${activeTab === 'settings' ? 'bg-blue-600 text-white shadow-md shadow-blue-500/20' : 'text-slate-400 hover:bg-slate-800 hover:text-white group'}`}
          >
            <Settings size={16} className={activeTab === 'settings' ? 'text-white' : 'text-slate-400 group-hover:text-white'} />
            Settings
          </button>
        </nav>

        <div className="p-3 mt-auto">
          <div className="bg-slate-800/40 rounded-xl p-3 border border-slate-700/30">
            <div className="flex items-center gap-2.5 mb-2.5">
              <div className="w-8 h-8 rounded-lg bg-blue-600/10 border border-blue-500/30 flex items-center justify-center text-blue-400 font-bold text-xs shrink-0">
                {user?.name?.[0]?.toUpperCase()}
              </div>
              <div className="min-w-0">
                <div className="text-xs font-bold text-white truncate">{user?.name}</div>
                <div className="text-[9px] text-slate-500 truncate leading-none">{user?.email || 'Free Member'}</div>
              </div>
            </div>
            <button
              onClick={logout}
              className="w-full flex items-center justify-center gap-2 py-1.5 text-[10px] font-bold text-red-400 hover:text-red-300 bg-red-500/10 hover:bg-red-500/20 rounded-lg transition-all"
            >
              <LogOut size={12} />
              Sign Out
            </button>
          </div>
        </div>
      </aside>

      {/*  Main Content  */}
      <main className="flex-1 overflow-y-auto no-scrollbar">
        {/* Top bar */}
        <div className="h-14 bg-white border-b border-slate-200 sticky top-0 z-40 px-6 flex items-center justify-between">
          <div className="relative w-80">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-3.5 h-3.5" />
            <input
              type="text"
              placeholder="Search..."
              className="w-full pl-9 pr-4 py-1.5 bg-slate-50 border border-slate-200 rounded-lg text-xs focus:outline-none focus:border-blue-500 focus:ring-4 focus:ring-blue-500/5 transition-all"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
          <div className="flex items-center gap-3">
            <button className="p-1.5 text-slate-400 hover:text-blue-600 transition-colors relative">
              <Bell size={18} />
              <span className="absolute top-1.5 right-1.5 w-1.5 h-1.5 bg-red-500 rounded-full border border-white"></span>
            </button>
            <div className="w-px h-5 bg-slate-200 mx-1"></div>
            <div className="flex items-center gap-2.5">
              <div className="flex flex-col items-end leading-tight">
                <span className="text-xs font-bold text-slate-900">{user?.name}</span>
                <span className="text-[9px] font-bold text-blue-600">Active</span>
              </div>
              <div className="w-8 h-8 rounded-full bg-slate-200 border border-white shadow-sm overflow-hidden shrink-0">
                <div className="w-full h-full flex items-center justify-center bg-blue-600 text-white font-bold text-xs uppercase">
                  {user?.name?.[0]?.toUpperCase()}
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="p-6 max-w-[1280px] mx-auto">
          {activeTab === 'boards' && renderBoardsContent()}
          {activeTab === 'team' && renderTeamContent()}
          {(activeTab !== 'boards' && activeTab !== 'team') && (
            <div className="h-[60vh] flex flex-col items-center justify-center text-center animate-in fade-in duration-500">
              <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mb-6 text-slate-300">
                <Settings size={40} />
              </div>
              <h3 className="text-xl font-black text-slate-900 mb-2">Section Under Development</h3>
              <p className="text-slate-500 font-medium max-w-xs mx-auto">
                We're currently building the {activeTab} section to the highest production standards. Stay tuned!
              </p>
            </div>
          )}
        </div>
      </main>

      {/*  Delete Modal  */}
      {showDeleteModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-6">
          <div className="absolute inset-0 bg-slate-900/60 backdrop-blur-md" onClick={() => setShowDeleteModal(null)}></div>
          <div className="relative bg-white rounded-[40px] p-10 max-w-md w-full shadow-2xl animate-in zoom-in-95 duration-200">
            <div className="w-20 h-20 bg-red-100 text-red-600 rounded-3xl flex items-center justify-center mb-8 mx-auto shadow-lg shadow-red-200/50">
              <Trash2 size={40} />
            </div>
            <h3 className="text-2xl font-black text-slate-900 text-center mb-4 leading-tight">Delete workspace?</h3>
            <p className="text-slate-500 text-center font-medium text-lg leading-relaxed mb-10">
              This action is <span className="text-emerald-500 font-black uppercase underline decoration-2 underline-offset-4">permanent</span>. All data in this board will be lost forever.
            </p>
            <div className="grid grid-cols-2 gap-4">
              <button
                onClick={() => setShowDeleteModal(null)}
                className="py-4 rounded-2xl bg-slate-100 text-slate-900 font-black uppercase tracking-widest text-xs hover:bg-slate-200 transition-all border-b-4 border-slate-300 active:border-b-0 active:translate-y-1"
              >
                Cancel
              </button>
              <button
                onClick={() => handleDeleteRoom(showDeleteModal)}
                disabled={deletingId === showDeleteModal}
                className="py-4 rounded-2xl bg-red-500 text-white font-black uppercase tracking-widest text-xs hover:bg-red-600 transition-all border-b-4 border-red-700 active:border-b-0 active:translate-y-1 disabled:opacity-50 shadow-lg shadow-red-200/50"
              >
                {deletingId === showDeleteModal ? "Deleting..." : "Confirm Delete"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
