import React from 'react';
import { useParticipantTile, VideoTrack, AudioTrack } from '@livekit/components-react';
import { MicOff } from 'lucide-react';

const ParticipantTile = ({ participant }) => {
    const { elementProps, videoSource, audioSource, isSpeaking } = useParticipantTile({
        participant,
        source: 'camera',
    });

    return (
        <div
            {...elementProps}
            className={`wb-video-tile ${isSpeaking ? 'active-speaker' : ''}`}
        >
            {videoSource ? (
                <VideoTrack source={videoSource} />
            ) : (
                <div className="wb-video-placeholder">
                    <div className="wb-v-avatar">{participant.identity[0].toUpperCase()}</div>
                </div>
            )}

            {audioSource && <AudioTrack source={audioSource} />}

            <div className="wb-video-info">
                <span className="wb-video-name">
                    {participant.identity} {participant.isLocal && '(You)'}
                </span>
                {!participant.isMicrophoneEnabled && (
                    <MicOff size={10} className="text-red-500" />
                )}
            </div>
        </div>
    );
};

export default ParticipantTile;
