import React, { useState, useRef } from "react";
import ReactDOM from "react-dom";
import {
    MousePointer2,
    Hand,
    Pencil,
    Highlighter,
    Eraser,
    Type,
    StickyNote,
    Upload,
    Square,
    Circle,
    Minus,
    ArrowUpRight,
    Undo2,
    Redo2,
    Trash2,
    Plus,
    Minus as MinusCircle,
    RotateCcw,
    ChevronRight,
    Settings,
    Brush,
    Sparkles,
    ArrowLeftRight,
    Triangle,
    Database,
    FileText,
    MessageSquare,
    Cloud,
    Diamond
} from 'lucide-react';

const Toolbar = ({ activeTool, setTool, isHost, onClear, onUndo, onRedo, onDelete }) => {
    const [expandedCategory, setExpandedCategory] = useState(null);
    const [flyoutPos, setFlyoutPos] = useState({ top: 0 });
    const [tooltip, setTooltip] = useState({ text: '', visible: false, top: 0 });
    const [showAllCategories, setShowAllCategories] = useState({}); // Tracking which categories are toggled

    const categories = [
        {
            id: 'select',
            icon: MousePointer2,
            label: 'Selection & Navigation',
            tools: [
                { id: 'select', icon: MousePointer2, label: 'Select (V)' },
                { id: 'pan', icon: Hand, label: 'Pan / Move (H)' }
            ]
        },
        {
            id: 'draw',
            icon: Pencil,
            label: 'Drawing Tools',
            tools: [
                { id: 'pencil', icon: Pencil, label: 'Pencil (P)' },
                { id: 'highlighter', icon: Highlighter, label: 'Highlighter (H)' },
                { id: 'soft-brush', icon: Brush, label: 'Soft Brush' },
                { id: 'marker', icon: Brush, label: 'Marker Brush' },
                { id: 'smooth-pencil', icon: Sparkles, label: 'Smooth Stroke Mode' }
            ]
        },
        {
            id: 'eraser',
            icon: Eraser,
            label: 'Eraser Tools',
            tools: [
                { id: 'eraser', icon: Eraser, label: 'Eraser (Full Stroke Delete)' },
                { id: 'precision-eraser', icon: Eraser, label: 'Precision Eraser (Partial Erase)', isSmall: true }
            ]
        },
        {
            id: 'shapes',
            icon: Square,
            label: 'Shapes & Diagramming',
            tools: [
                { id: 'line', icon: Minus, label: 'Line (L)' },
                { id: 'arrow', icon: ArrowUpRight, label: 'Arrow (A)' },
                { id: 'double-arrow', icon: ArrowLeftRight, label: 'Double Arrow' },
                { id: 'rect', icon: Square, label: 'Rectangle (R)' },
                { id: 'rounded-rect', icon: Square, label: 'Rounded Rectangle' },
                { id: 'circle', icon: Circle, label: 'Circle (O)' },
                { id: 'ellipse', icon: Circle, label: 'Ellipse' },
                { id: 'triangle', icon: Triangle, label: 'Triangle' },
                { id: 'diamond', icon: Diamond, label: 'Diamond' },
                { id: 'sticky', icon: StickyNote, label: 'Sticky Note' },
                { id: 'speech-bubble', icon: MessageSquare, label: 'Speech Bubble' },
                { id: 'parallelogram', icon: Square, label: 'Parallelogram' },
                { id: 'cylinder', icon: Database, label: 'Cylinder (DB)' },
                { id: 'document', icon: FileText, label: 'Document' },
                { id: 'cloud', icon: Cloud, label: 'Cloud' }
            ]
        },
        {
            id: 'content',
            icon: Type,
            label: 'Content Tools',
            tools: [
                { id: 'text', icon: Type, label: 'Text Tool (T)' },
                { id: 'upload', icon: Upload, label: 'Upload Image' }
            ]
        }
    ];

    const utilities = [
        { id: 'reset', icon: RotateCcw, label: 'Reset View', action: () => window.dispatchEvent(new CustomEvent('wb-reset-view')) },
        { id: 'clear', icon: Trash2, label: 'Clear Board', action: onClear, isHostOnly: true }
    ];

    const handleMouseEnter = (text, e) => {
        const rect = e.currentTarget.getBoundingClientRect();
        setTooltip({
            text,
            visible: true,
            top: rect.top + (rect.height / 2) - 12 // Center tooltip vertically relative to button
        });
    };

    const handleMouseLeave = () => {
        setTooltip({ ...tooltip, visible: false });
    };

    const toggleCategory = (catId, e) => {
        if (expandedCategory === catId) {
            setExpandedCategory(null);
        } else {
            const rect = e.currentTarget.getBoundingClientRect();
            setFlyoutPos({ top: rect.top });
            setExpandedCategory(catId);
        }
    };

    const handleSelectTool = (toolId, categoryId) => {
        setTool(toolId);
        if (!['draw', 'shapes', 'eraser'].includes(categoryId)) {
            setExpandedCategory(null);
        }
    };

    const isToolInCategoryActive = (cat) => {
        return cat.tools.some(t => t.id === activeTool);
    };

    const toggleShowMore = (catId, e) => {
        e.stopPropagation();
        setShowAllCategories(prev => ({
            ...prev,
            [catId]: !prev[catId]
        }));
    };

    const renderFlyout = (cat) => {
        if (expandedCategory !== cat.id) return null;

        const isFullyShown = showAllCategories[cat.id];
        const visibleTools = isFullyShown || cat.tools.length <= 6
            ? cat.tools
            : cat.tools.slice(0, 6);

        return ReactDOM.createPortal(
            <div
                className={`category-flyout transition-all duration-300 ease-in-out ${isFullyShown ? 'max-h-[400px]' : 'max-h-[300px]'}`}
                style={{
                    top: `${flyoutPos.top}px`,
                    zIndex: 9999
                }}
            >
                <div className="text-[9px] font-bold text-slate-400 uppercase tracking-widest px-2 mb-2 pb-1 border-b border-slate-50 w-full text-center">
                    {cat.label}
                </div>

                <div className="flex flex-col gap-1 w-full px-1 overflow-y-auto no-scrollbar scroll-smooth">
                    {visibleTools.map((tool) => (
                        <button
                            key={tool.id}
                            onClick={() => handleSelectTool(tool.id, cat.id)}
                            onMouseEnter={(e) => handleMouseEnter(tool.label, e)}
                            onMouseLeave={handleMouseLeave}
                            className={`tool-button w-full justify-start px-3 gap-3 ${activeTool === tool.id ? 'tool-button-active' : ''}`}
                        >
                            <div className="relative">
                                <tool.icon size={tool.isSmall ? 16 : 18} />
                                {tool.isSmall && (
                                    <div className="absolute bottom-0 right-0 w-1 h-1 bg-current rounded-full" />
                                )}
                            </div>
                            <span className="text-[11px] font-semibold">{tool.label.split('(')[0].trim()}</span>
                        </button>
                    ))}
                </div>

                {cat.tools.length > 6 && (
                    <button
                        onClick={(e) => toggleShowMore(cat.id, e)}
                        className="w-full mt-2 py-1.5 flex items-center justify-center gap-2 hover:bg-slate-50 rounded-lg group transition-all"
                    >
                        <span className="text-[10px] font-bold text-slate-400 group-hover:text-violet-500 uppercase tracking-tighter">
                            {isFullyShown ? 'Show Less' : `Show All (${cat.tools.length})`}
                        </span>
                        <ChevronRight
                            size={10}
                            className={`text-slate-300 group-hover:text-violet-400 transition-transform ${isFullyShown ? '-rotate-90' : 'rotate-90'}`}
                        />
                    </button>
                )}
            </div>,
            document.body
        );
    };

    return (
        <div className="flex flex-col gap-2 items-center relative h-full">
            {/* Tooltip Portal */}
            {tooltip.visible && ReactDOM.createPortal(
                <div className="wb-tooltip" style={{ top: `${tooltip.top}px` }}>
                    {tooltip.text}
                </div>,
                document.body
            )}

            {/* Main Tool Categories */}
            <div className="flex flex-col gap-1 items-center">
                {categories.map((cat) => (
                    <div key={cat.id} className="relative">
                        <button
                            onClick={(e) => toggleCategory(cat.id, e)}
                            onMouseEnter={(e) => handleMouseEnter(cat.label, e)}
                            onMouseLeave={handleMouseLeave}
                            className={`tool-button group ${expandedCategory === cat.id || isToolInCategoryActive(cat) ? "tool-button-active" : ""}`}
                        >
                            <cat.icon size={20} strokeWidth={2.5} />
                            <ChevronRight
                                size={10}
                                className={`absolute right-0.5 transition-transform duration-200 ${expandedCategory === cat.id ? 'rotate-180' : ''}`}
                            />
                        </button>
                        {renderFlyout(cat)}
                    </div>
                ))}
            </div>

            <div className="w-8 h-px bg-slate-100 my-1" />

            {/* Undo/Redo */}
            <div className="flex flex-col gap-1 items-center">
                <button
                    onClick={onUndo}
                    onMouseEnter={(e) => handleMouseEnter("Undo (Ctrl+Z)", e)}
                    onMouseLeave={handleMouseLeave}
                    className="tool-button"
                >
                    <Undo2 size={20} strokeWidth={2} />
                </button>
                <button
                    onClick={onRedo}
                    onMouseEnter={(e) => handleMouseEnter("Redo (Ctrl+Y)", e)}
                    onMouseLeave={handleMouseLeave}
                    className="tool-button"
                >
                    <Redo2 size={20} strokeWidth={2} />
                </button>
            </div>

            <div className="w-8 h-px bg-slate-100 my-1" />

            {/* Utility Dropdown */}
            <div className="relative">
                <button
                    onClick={(e) => toggleCategory('utility', e)}
                    onMouseEnter={(e) => handleMouseEnter("Utilities", e)}
                    onMouseLeave={handleMouseLeave}
                    className={`tool-button group ${expandedCategory === 'utility' ? "tool-button-active" : ""}`}
                >
                    <Settings size={20} strokeWidth={2} />
                    <ChevronRight
                        size={10}
                        className={`absolute right-0.5 transition-transform duration-200 ${expandedCategory === 'utility' ? 'rotate-180' : ''}`}
                    />
                </button>

                {expandedCategory === 'utility' && ReactDOM.createPortal(
                    <div className="category-flyout" style={{ top: `${flyoutPos.top}px`, zIndex: 9999 }}>
                        <div className="text-[9px] font-bold text-slate-400 uppercase tracking-widest px-2 mb-2 pb-1 border-b border-slate-50 w-full text-center">Utilities</div>
                        <div className="flex flex-col gap-1 w-full px-1">
                            {utilities.map((tool) => {
                                if (tool.isHostOnly && !isHost) return null;
                                return (
                                    <button
                                        key={tool.id}
                                        onClick={() => { tool.action(); setExpandedCategory(null); }}
                                        onMouseEnter={(e) => handleMouseEnter(tool.label, e)}
                                        onMouseLeave={handleMouseLeave}
                                        className="tool-button w-full justify-start px-3 gap-3"
                                    >
                                        <tool.icon size={18} />
                                        <span className="text-[11px] font-semibold">{tool.label}</span>
                                    </button>
                                );
                            })}
                        </div>
                    </div>,
                    document.body
                )}
            </div>
        </div>
    );
};

export default Toolbar;
