import React from "react";
import {
    MousePointer2,
    Pencil,
    Highlighter,
    Eraser,
    Type,
    StickyNote,
    Square,
    Circle,
    Minus,
    ArrowUpRight,
    Undo2,
    Redo2,
    Trash2,
    Plus,
    Minus as MinusCircle,
    RotateCcw
} from 'lucide-react';

const Toolbar = ({ activeTool, setTool, isHost, onClear, onUndo, onRedo, onDelete }) => {

    const ToolBtn = ({ id, icon: Icon, label }) => (
        <button
            onClick={() => setTool(id)}
            className={`tool-button ${activeTool === id ? "tool-button-active" : ""}`}
            title={label}
        >
            <Icon size={20} strokeWidth={2.5} />
        </button>
    );

    return (
        <div className="flex flex-col gap-1 items-center">
            {/* Selection & Base Tools */}
            <div className="sidebar-group">
                <ToolBtn id="select" icon={MousePointer2} label="Select (V)" />
                <ToolBtn id="pencil" icon={Pencil} label="Pencil (P)" />
                <ToolBtn id="highlighter" icon={Highlighter} label="Highlighter (H)" />
                <ToolBtn id="eraser" icon={Eraser} label="Eraser (E)" />
                <button
                    onClick={() => setTool('precision-eraser')}
                    className={`tool-button relative ${activeTool === 'precision-eraser' ? "tool-button-active" : ""}`}
                    title="Precision Eraser"
                >
                    <Eraser size={18} strokeWidth={2.5} />
                    <div className="absolute bottom-1 right-1 w-1.5 h-1.5 bg-current rounded-full" />
                </button>
            </div>

            {/* Shapes */}
            <div className="sidebar-group">
                <ToolBtn id="line" icon={Minus} label="Line (L)" />
                <ToolBtn id="arrow" icon={ArrowUpRight} label="Arrow (A)" />
                <ToolBtn id="rect" icon={Square} label="Rectangle (R)" />
                <ToolBtn id="circle" icon={Circle} label="Circle (O)" />
            </div>

            {/* Content */}
            <div className="sidebar-group">
                <ToolBtn id="text" icon={Type} label="Text (T)" />
                <ToolBtn id="sticky" icon={StickyNote} label="Sticky Note (S)" />
            </div>

            {/* Editing */}
            <div className="sidebar-group">
                <button onClick={onUndo} className="tool-button" title="Undo (Ctrl+Z)"><Undo2 size={20} /></button>
                <button onClick={onRedo} className="tool-button" title="Redo (Ctrl+Y)"><Redo2 size={20} /></button>
                <button onClick={onDelete} className="tool-button hover:text-red-500" title="Delete Selected"><Trash2 size={20} /></button>
            </div>

            {/* Utilities */}
            <div className="sidebar-group">
                <button onClick={() => window.dispatchEvent(new CustomEvent('wb-zoom-in'))} className="tool-button"><Plus size={20} /></button>
                <button onClick={() => window.dispatchEvent(new CustomEvent('wb-zoom-out'))} className="tool-button"><MinusCircle size={20} /></button>
                <button onClick={() => window.dispatchEvent(new CustomEvent('wb-reset-view'))} className="tool-button"><RotateCcw size={18} /></button>
                {isHost && (
                    <button onClick={onClear} className="tool-button hover:bg-red-50 text-red-500" title="Clear Board"><Trash2 size={20} /></button>
                )}
            </div>
        </div>
    );
};

export default Toolbar;
