import React from "react";

const StylingPanel = ({ tool, color, setColor, size, setSize, opacity, setOpacity, strokeStyle, setStrokeStyle, fill, setFill }) => {
    // Only show for tools that need styling
    const drawingTools = ["pencil", "highlighter", "rect", "circle", "line", "arrow", "eraser"];
    if (!drawingTools.includes(tool)) return null;

    const colors = [
        "#000000", "#475569", "#dc2626", "#ea580c", "#d97706",
        "#65a30d", "#0891b2", "#2563eb", "#7c3aed", "#db2777"
    ];

    const thicknessLevels = [2, 4, 8, 16, 32];

    return (
        <div className="styling-panel left-20 top-[180px]">
            {/* Context Tool (Miro style) */}
            <div className="flex flex-col items-center gap-4 w-full">
                {/* Pen Style Icons (Static for now to match Miro) */}
                <div className="flex flex-col gap-2 pb-2 border-b border-slate-100 w-full items-center">
                    <div className="w-6 h-6 rounded bg-slate-100 flex items-center justify-center text-[10px] font-bold text-slate-400 italic">f</div>
                </div>

                {/* Thickness Dots */}
                <div className="flex flex-col gap-3 py-2 border-b border-slate-100 w-full items-center">
                    {thicknessLevels.map(lvl => (
                        <button
                            key={lvl}
                            onClick={() => setSize(lvl)}
                            className={`rounded-full transition-all ${size === lvl ? 'bg-slate-900 border-2 border-white ring-1 ring-slate-900' : 'bg-slate-300 hover:bg-slate-400'}`}
                            style={{ width: Math.max(4, lvl / 2 + 4), height: Math.max(4, lvl / 2 + 4) }}
                        />
                    ))}
                </div>

                {/* Vertical Color Dots */}
                <div className="flex flex-col gap-2 py-2 w-full items-center">
                    {colors.map(c => (
                        <button
                            key={c}
                            onClick={() => setColor(c)}
                            className={`w-4 h-4 rounded-full border border-slate-200 transition-transform hover:scale-125
                                      ${color === c ? "ring-2 ring-violet-500 ring-offset-1" : ""}`}
                            style={{ backgroundColor: c }}
                        />
                    ))}
                </div>

                {/* Bottom Style Toggle */}
                {["rect", "circle"].includes(tool) && (
                    <div className="pt-2 border-t border-slate-100 w-full flex justify-center">
                        <button
                            onClick={() => setFill(!fill)}
                            className={`w-6 h-6 rounded flex items-center justify-center text-[8px] font-black transition-all ${fill ? 'bg-violet-600 text-white' : 'bg-slate-100 text-slate-400'}`}
                        >
                            F
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default StylingPanel;
