import React from "react";

const Toolbar = ({ activeTool, setTool, isHost, onClear, onUndo, onRedo, onDelete }) => {

    const ToolBtn = ({ id, icon, label, group }) => (
        <button
            onClick={() => setTool(id)}
            className={`tool-button ${activeTool === id ? "tool-button-active" : ""}`}
            title={label}
        >
            <span className="text-xl">{icon}</span>
        </button>
    );

    return (
        <div className="flex flex-col gap-4">
            {/* Group 1: Drawing Tools */}
            <div className="sidebar-group">
                <ToolBtn id="select" icon="üñ±Ô∏è" label="Select (V)" />
                <div className="divider" />
                <ToolBtn id="pencil" icon="‚úèÔ∏è" label="Pencil (P)" />
                <ToolBtn id="highlighter" icon="üñåÔ∏è" label="Highlighter (H)" />
                <ToolBtn id="eraser" icon="üßΩ" label="Eraser (E)" />
                <div className="divider" />
                <ToolBtn id="line" icon="üìè" label="Line (L)" />
                <ToolBtn id="arrow" icon="‚ÜóÔ∏è" label="Arrow (A)" />
                <ToolBtn id="rect" icon="‚¨ú" label="Rectangle (R)" />
                <ToolBtn id="circle" icon="‚≠ï" label="Circle (O)" />
                <div className="divider" />
                <ToolBtn id="text" icon="T" label="Text (T)" />
                <ToolBtn id="sticky" icon="üìù" label="Sticky Note (S)" />
            </div>

            {/* Group 2: Editing Tools */}
            <div className="sidebar-group">
                <button onClick={onUndo} className="tool-button" title="Undo (Ctrl+Z)">‚Ü©Ô∏è</button>
                <button onClick={onRedo} className="tool-button" title="Redo (Ctrl+Y)">‚Ü™Ô∏è</button>
                <div className="divider" />
                <button onClick={onDelete} className="tool-button hover:text-red-600" title="Delete Selected (Del)">üóëÔ∏è</button>
            </div>

            {/* Group 3: Utility */}
            <div className="sidebar-group">
                <button onClick={() => window.dispatchEvent(new CustomEvent('wb-zoom-in'))} className="tool-button">‚ûï</button>
                <button onClick={() => window.dispatchEvent(new CustomEvent('wb-zoom-out'))} className="tool-button">‚ûñ</button>
                <div className="divider" />
                {isHost && (
                    <button onClick={onClear} className="tool-button hover:bg-red-50 text-red-500" title="Clear Board">üß®</button>
                )}
            </div>
        </div>
    );
};

export default Toolbar;
