import React from "react";

const StylingPanel = ({ tool, color, setColor, size, setSize, opacity, setOpacity, strokeStyle, setStrokeStyle, fill, setFill }) => {
    // Only show for tools that need styling
    const drawingTools = ["pencil", "highlighter", "rect", "circle", "line", "arrow", "text", "sticky"];
    if (!drawingTools.includes(tool)) return null;

    const colors = [
        "#000000", "#475569", "#dc2626", "#ea580c", "#d97706",
        "#65a30d", "#0891b2", "#2563eb", "#7c3aed", "#db2777"
    ];

    return (
        <div className="styling-panel left-24 bottom-32">
            {/* Color Grid */}
            <div className="flex flex-col gap-2 border-r border-slate-100 pr-4">
                <span className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Color</span>
                <div className="grid grid-cols-5 gap-1.5">
                    {colors.map(c => (
                        <button
                            key={c}
                            onClick={() => setColor(c)}
                            className={`w-5 h-5 rounded-full border border-slate-200 transition-transform hover:scale-125
                                      ${color === c ? "ring-2 ring-violet-500 ring-offset-1" : ""}`}
                            style={{ backgroundColor: c }}
                        />
                    ))}
                </div>
            </div>

            {/* Size Slider */}
            <div className="flex flex-col gap-2 border-r border-slate-100 pr-4 w-32">
                <div className="flex justify-between items-center">
                    <span className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Thickness</span>
                    <span className="text-xs font-mono font-bold text-slate-600">{size}px</span>
                </div>
                <input
                    type="range"
                    min="1"
                    max="40"
                    value={size}
                    onChange={(e) => setSize(parseInt(e.target.value))}
                    className="w-full accent-violet-600 h-1.5 bg-slate-100 rounded-lg appearance-none cursor-pointer"
                />
            </div>

            {/* Opacity Slider */}
            <div className="flex flex-col gap-2 border-r border-slate-100 pr-4 w-24">
                <div className="flex justify-between items-center">
                    <span className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Opacity</span>
                    <span className="text-xs font-mono font-bold text-slate-600">{Math.round(opacity * 100)}%</span>
                </div>
                <input
                    type="range"
                    min="0.1"
                    max="1"
                    step="0.05"
                    value={opacity}
                    onChange={(e) => setOpacity(parseFloat(e.target.value))}
                    className="w-full accent-violet-600 h-1.5 bg-slate-100 rounded-lg appearance-none cursor-pointer"
                />
            </div>

            {/* Stroke Style */}
            <div className="flex items-center gap-3">
                <button
                    onClick={() => setStrokeStyle('solid')}
                    className={`p-1.5 rounded ${strokeStyle === 'solid' ? 'bg-violet-100' : 'hover:bg-slate-100'}`}
                    title="Solid"
                >
                    <div className="w-6 h-0.5 bg-slate-700"></div>
                </button>
                <button
                    onClick={() => setStrokeStyle('dashed')}
                    className={`p-1.5 rounded ${strokeStyle === 'dashed' ? 'bg-violet-100' : 'hover:bg-slate-100'}`}
                    title="Dashed"
                >
                    <div className="w-6 border-b-2 border-dashed border-slate-700"></div>
                </button>
                {["rect", "circle"].includes(tool) && (
                    <button
                        onClick={() => setFill(!fill)}
                        className={`p-1.5 rounded text-xs font-bold ${fill ? 'bg-violet-600 text-white' : 'bg-slate-100 text-slate-600'}`}
                    >
                        FILL
                    </button>
                )}
            </div>
        </div>
    );
};

export default StylingPanel;
