import React, { useEffect, useRef, useState } from 'react';
import { Mic, MicOff, Video, VideoOff, PhoneOff, Settings } from 'lucide-react';
import toast from 'react-hot-toast';

const WebRTCMeeting = ({ socket, roomId, signalingId, isCaller, username, onLeave }) => {
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const [isMicEnabled, setIsMicEnabled] = useState(true);
    const [isCamEnabled, setIsCamEnabled] = useState(true);
    const [connectionStatus, setConnectionStatus] = useState('Initializing...');

    const pcRef = useRef(null);
    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);

    // Google STUN servers as requested
    const rtcConfig = {
        iceServers: [
            { urls: 'stun:stun.l.google.com:19302' },
            { urls: 'stun:stun1.l.google.com:19302' },
            { urls: 'stun:stun2.l.google.com:19302' },
        ],
    };

    useEffect(() => {
        const initCall = async () => {
            try {
                // 1. Ensure getUserMedia is called before creating offer
                console.log('[WebRTC] Requesting media permissions...');
                const stream = await navigator.mediaDevices.getUserMedia({
                    video: true,
                    audio: true
                });
                setLocalStream(stream);
                if (localVideoRef.current) localVideoRef.current.srcObject = stream;

                // 2. Initialize PeerConnection
                const pc = new RTCPeerConnection(rtcConfig);
                pcRef.current = pc;

                // 3. Add all local stream tracks to RTCPeerConnection
                stream.getTracks().forEach(track => {
                    pc.addTrack(track, stream);
                    console.log(`[WebRTC] Added local ${track.kind} track`);
                });

                // 4. Implement proper ontrack handler to attach remote stream
                pc.ontrack = (event) => {
                    console.log('[WebRTC] Received remote track:', event.track.kind);
                    const [stream] = event.streams;
                    setRemoteStream(stream);
                    if (remoteVideoRef.current) remoteVideoRef.current.srcObject = stream;
                };

                // 5. Log and handle iceConnectionState changes
                pc.oniceconnectionstatechange = () => {
                    console.log('[WebRTC] ICE state:', pc.iceConnectionState);
                    setConnectionStatus(`Connection: ${pc.iceConnectionState}`);

                    if (pc.iceConnectionState === 'failed') {
                        toast.error('Connection failed. Please restart the call.');
                        // 6. Prevent auto disconnect unless connection state is failed (we handle it here)
                    }
                };

                pc.onicecandidate = (event) => {
                    if (event.candidate && signalingId) {
                        socket.emit('call:candidate', {
                            roomId,
                            to: signalingId,
                            candidate: event.candidate
                        });
                    }
                };

                // 7. Ensure offer/answer exchange completes
                if (isCaller && signalingId) {
                    console.log('[WebRTC] Creating offer...');
                    const offer = await pc.createOffer();
                    await pc.setLocalDescription(offer);
                    socket.emit('call:offer', {
                        roomId,
                        to: signalingId,
                        offer: offer
                    });
                }
            } catch (err) {
                console.error('[WebRTC] Initialization error:', err);
                toast.error('Could not access camera/microphone');
                onLeave();
            }
        };

        initCall();

        // ─── SIGNALING LISTENERS ───
        const handleOffer = async ({ from, offer }) => {
            if (!pcRef.current) return;
            console.log('[WebRTC] Handling offer from:', from);
            await pcRef.current.setRemoteDescription(new RTCSessionDescription(offer));
            const answer = await pcRef.current.createAnswer();
            await pcRef.current.setLocalDescription(answer);
            socket.emit('call:answer', { roomId, to: from, answer });
        };

        const handleAnswer = async ({ from, answer }) => {
            if (!pcRef.current) return;
            console.log('[WebRTC] Handling answer from:', from);
            await pcRef.current.setRemoteDescription(new RTCSessionDescription(answer));
        };

        const handleCandidate = async ({ from, candidate }) => {
            if (!pcRef.current) return;
            try {
                await pcRef.current.addIceCandidate(new RTCIceCandidate(candidate));
            } catch (e) {
                console.warn('[WebRTC] Error adding candidate:', e);
            }
        };

        socket.on('call:offer', handleOffer);
        socket.on('call:answer', handleAnswer);
        socket.on('call:candidate', handleCandidate);

        return () => {
            console.log('[WebRTC] Cleaning up call...');
            socket.off('call:offer', handleOffer);
            socket.off('call:answer', handleAnswer);
            socket.off('call:candidate', handleCandidate);
            if (pcRef.current) pcRef.current.close();
            if (localStream) localStream.getTracks().forEach(t => t.stop());
        };
    }, [roomId, signalingId, isCaller, socket, onLeave]);

    const toggleMic = () => {
        if (localStream) {
            const audioTrack = localStream.getAudioTracks()[0];
            if (audioTrack) {
                audioTrack.enabled = !audioTrack.enabled;
                setIsMicEnabled(audioTrack.enabled);
            }
        }
    };

    const toggleCam = () => {
        if (localStream) {
            const videoTrack = localStream.getVideoTracks()[0];
            if (videoTrack) {
                videoTrack.enabled = !videoTrack.enabled;
                setIsCamEnabled(videoTrack.enabled);
            }
        }
    };

    return (
        <div className="wb-meeting-container">
            <div className="wb-v-grid-main">
                {/* Local Video */}
                <div className="wb-video-tile">
                    <video
                        ref={localVideoRef}
                        autoPlay
                        muted
                        playsInline
                        className="wb-video-element"
                    />
                    {!isCamEnabled && (localStream?.getVideoTracks()[0] && (
                        <div className="wb-video-placeholder">
                            <div className="wb-v-avatar">{username[0].toUpperCase()}</div>
                        </div>
                    ))}
                    <div className="wb-video-info">
                        <span className="wb-video-name">{username} (You)</span>
                        {!isMicEnabled && <MicOff size={10} className="text-red-500" />}
                    </div>
                </div>

                {/* Remote Video */}
                <div className={`wb-video-tile ${remoteStream ? 'active-host' : ''}`}>
                    <video
                        ref={remoteVideoRef}
                        autoPlay
                        playsInline
                        className="wb-video-element"
                        style={{ display: remoteStream ? 'block' : 'none' }}
                    />
                    {!remoteStream && (
                        <div className="wb-video-placeholder">
                            <div className="wb-v-avatar">?</div>
                            <p className="text-xs mt-2 text-white/50">Waiting for peer...</p>
                        </div>
                    )}
                    <div className="wb-video-info">
                        <span className="wb-video-name">Remote Participant</span>
                    </div>
                </div>
            </div>

            {/* Controls */}
            <div className="wb-video-controls-bar-fixed">
                <div className="wb-video-controls-bar">
                    <button
                        className={`wb-v-btn ${!isMicEnabled ? 'muted' : ''}`}
                        onClick={toggleMic}
                        title={isMicEnabled ? "Mute Mic" : "Unmute Mic"}
                    >
                        {isMicEnabled ? <Mic size={18} /> : <MicOff size={18} />}
                    </button>

                    <button
                        className={`wb-v-btn ${!isCamEnabled ? 'muted' : ''}`}
                        onClick={toggleCam}
                        title={isCamEnabled ? "Stop Video" : "Start Video"}
                    >
                        {isCamEnabled ? <Video size={18} /> : <VideoOff size={18} />}
                    </button>

                    <button
                        className="wb-v-btn leave"
                        onClick={onLeave}
                        title="Leave Call"
                    >
                        <PhoneOff size={18} />
                    </button>
                </div>
            </div>

            <div className="wb-call-status-pill">
                <div className="wb-status-dot" style={{ backgroundColor: connectionStatus.includes('connected') ? '#10b981' : '#f59e0b' }}></div>
                {connectionStatus}
            </div>
        </div>
    );
};

export default WebRTCMeeting;
