import Chat from "../components/Chat";
import StylingPanel from "../components/StylingPanel";
import toast from "react-hot-toast";

const WhiteboardRoom = () => {
    const { roomId } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const { socket } = useSocket();

    const canvasRef = useRef(null);
    const contextRef = useRef(null);
    const [isDrawing, setIsDrawing] = useState(false);

    // Tools & Styling
    const [tool, setTool] = useState("pencil");
    const [color, setColor] = useState("#000000");
    const [size, setSize] = useState(4);
    const [opacity, setOpacity] = useState(1);
    const [strokeStyle, setStrokeStyle] = useState('solid');
    const [fill, setFill] = useState(false);

    const [room, setRoom] = useState(null);
    const [participants, setParticipants] = useState([]);
    const [messages, setMessages] = useState([]);
    const [sharedFiles, setSharedFiles] = useState([]);
    const [activeTab, setActiveTab] = useState("chat");
    const [loadingFileUpload, setLoadingFileUpload] = useState(false);
    const [screenStream, setScreenStream] = useState(null);
    const screenVideoRef = useRef(null);
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    // History & Dragging
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);
    const [startPos, setStartPos] = useState({ x: 0, y: 0 });
    const [snapshot, setSnapshot] = useState(null);

    useEffect(() => {
        const fetchRoomDetails = async () => {
            try {
                const res = await roomService.getRoom(roomId);
                setRoom(res.data.room);
                setSharedFiles(res.data.room.sharedFiles || []);
            } catch (err) {
                toast.error("Failed to load workspace");
                navigate("/dashboard");
            }
        };

        fetchRoomDetails();

        if (socket) {
            socket.emit("join-room", { roomId, userId: user._id, userName: user.name });

            socket.on("user-joined", (data) => toast.success(`${data.userName} joined`));
            socket.on("online-users", (users) => setParticipants(users));
            socket.on("chat-message", (msg) => setMessages((prev) => [...prev, msg]));
            socket.on("chat-history", (h) => setMessages(h));
            socket.on("drawing", (data) => drawRemote(data));
            socket.on("erase", (data) => eraseRemote(data));
            socket.on("board-cleared", () => {
                clearCanvasLocal();
                setHistory([]);
                setHistoryIndex(-1);
            });
            socket.on("canvas-state", (data) => {
                if (data) {
                    applyCanvasState(data);
                    setHistory([data]);
                    setHistoryIndex(0);
                }
            });
            socket.on("undo", (state) => applyCanvasState(state));
            socket.on("redo", (state) => applyCanvasState(state));
        }

        return () => {
            if (socket) socket.emit("leave-room", { roomId, userId: user._id, userName: user.name });
        };
    }, [roomId, socket, user, navigate]);

    useEffect(() => {
        const canvas = canvasRef.current;
        const container = canvas.parentElement;

        const ResizeCanvas = () => {
            const rect = container.getBoundingClientRect();
            const dpr = window.devicePixelRatio || 1;

            // Save state
            const tempCanvas = document.createElement('canvas');
            const tempCtx = tempCanvas.getContext('2d');
            tempCanvas.width = canvas.width;
            tempCanvas.height = canvas.height;
            tempCtx.drawImage(canvas, 0, 0);

            canvas.width = rect.width * dpr;
            canvas.height = rect.height * dpr;
            canvas.style.width = `${rect.width}px`;
            canvas.style.height = `${rect.height}px`;

            const context = canvas.getContext("2d");
            context.scale(dpr, dpr);
            context.lineCap = "round";
            context.lineJoin = "round";
            context.drawImage(tempCanvas, 0, 0, tempCanvas.width / dpr, tempCanvas.height / dpr, 0, 0, rect.width, rect.height);
            contextRef.current = context;
        };

        const resizeObserver = new ResizeObserver(ResizeCanvas);
        resizeObserver.observe(container);
        ResizeCanvas();

        return () => resizeObserver.disconnect();
    }, []);

    const applyCanvasState = (state) => {
        if (!state) return clearCanvasLocal();
        const image = new Image();
        image.src = state;
        image.onload = () => {
            clearCanvasLocal();
            const canvas = canvasRef.current;
            contextRef.current.drawImage(image, 0, 0, canvas.width / (window.devicePixelRatio || 1), canvas.height / (window.devicePixelRatio || 1));
        };
    };

    const clearCanvasLocal = () => {
        const canvas = canvasRef.current;
        const dpr = window.devicePixelRatio || 1;
        contextRef.current.clearRect(0, 0, canvas.width / dpr, canvas.height / dpr);
    };

    const getMousePos = (e) => {
        const rect = canvasRef.current.getBoundingClientRect();
        return {
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
        };
    };

    const startDrawing = (e) => {
        const pos = getMousePos(e);
        setStartPos(pos);
        setIsDrawing(true);

        // Take snapshot for shape preview
        const canvas = canvasRef.current;
        const tempCanvas = document.createElement('canvas');
        tempCanvas.width = canvas.width;
        tempCanvas.height = canvas.height;
        tempCanvas.getContext('2d').drawImage(canvas, 0, 0);
        setSnapshot(tempCanvas);

        if (tool === "pencil" || tool === "highlighter" || tool === "eraser") {
            contextRef.current.beginPath();
            contextRef.current.moveTo(pos.x, pos.y);
        }
    };

    const draw = (e) => {
        if (!isDrawing) return;
        const pos = getMousePos(e);
        const ctx = contextRef.current;
        const dpr = window.devicePixelRatio || 1;

        ctx.lineWidth = size;
        ctx.strokeStyle = color;
        ctx.globalAlpha = tool === "highlighter" ? (opacity * 0.4) : opacity;
        ctx.setLineDash(strokeStyle === 'dashed' ? [10, 10] : []);

        if (tool === "pencil" || tool === "highlighter") {
            ctx.globalCompositeOperation = "source-over";
            ctx.lineTo(pos.x, pos.y);
            ctx.stroke();
            socket.emit("drawing", { roomId, drawingData: { x: pos.x, y: pos.y, color, size, opacity: ctx.globalAlpha, tool } });
        } else if (tool === "eraser") {
            ctx.globalCompositeOperation = "destination-out";
            ctx.lineTo(pos.x, pos.y);
            ctx.stroke();
            socket.emit("erase", { roomId, eraseData: { x: pos.x, y: pos.y, size: size * 2 } });
        } else if (["rect", "circle", "line", "arrow"].includes(tool)) {
            // Restore snapshot for clean preview
            clearCanvasLocal();
            ctx.drawImage(snapshot, 0, 0, snapshot.width / dpr, snapshot.height / dpr);

            ctx.beginPath();
            if (tool === "line") {
                ctx.moveTo(startPos.x, startPos.y);
                ctx.lineTo(pos.x, pos.y);
            } else if (tool === "rect") {
                if (fill) {
                    ctx.fillStyle = color;
                    ctx.fillRect(startPos.x, startPos.y, pos.x - startPos.x, pos.y - startPos.y);
                }
                ctx.strokeRect(startPos.x, startPos.y, pos.x - startPos.x, pos.y - startPos.y);
            } else if (tool === "circle") {
                const radius = Math.sqrt(Math.pow(pos.x - startPos.x, 2) + Math.pow(pos.y - startPos.y, 2));
                ctx.arc(startPos.x, startPos.y, radius, 0, 2 * Math.PI);
                if (fill) {
                    ctx.fillStyle = color;
                    ctx.fill();
                }
            } else if (tool === "arrow") {
                drawArrow(ctx, startPos.x, startPos.y, pos.x, pos.y);
            }
            ctx.stroke();
        }
    };

    const drawArrow = (ctx, fromx, fromy, tox, toy) => {
        const headlen = size * 3;
        const angle = Math.atan2(toy - fromy, tox - fromx);
        ctx.moveTo(fromx, fromy);
        ctx.lineTo(tox, toy);
        ctx.lineTo(tox - headlen * Math.cos(angle - Math.PI / 6), toy - headlen * Math.sin(angle - Math.PI / 6));
        ctx.moveTo(tox, toy);
        ctx.lineTo(tox - headlen * Math.cos(angle + Math.PI / 6), toy - headlen * Math.sin(angle + Math.PI / 6));
    };

    const stopDrawing = () => {
        if (!isDrawing) return;
        setIsDrawing(false);
        setSnapshot(null);

        const dataURL = canvasRef.current.toDataURL();
        const newHistory = history.slice(0, historyIndex + 1);
        newHistory.push(dataURL);
        setHistory(newHistory);
        setHistoryIndex(newHistory.length - 1);
        socket.emit("save-canvas", { roomId, canvasData: dataURL });
    };

    const handleUndo = () => {
        if (historyIndex > 0) {
            const newIndex = historyIndex - 1;
            const state = history[newIndex];
            setHistoryIndex(newIndex);
            applyCanvasState(state);
            socket.emit("undo", { roomId, canvasState: state });
            socket.emit("save-canvas", { roomId, canvasData: state });
        }
    };

    const handleRedo = () => {
        if (historyIndex < history.length - 1) {
            const newIndex = historyIndex + 1;
            const state = history[newIndex];
            setHistoryIndex(newIndex);
            applyCanvasState(state);
            socket.emit("redo", { roomId, canvasState: state });
            socket.emit("save-canvas", { roomId, canvasData: state });
        }
    };

    const drawRemote = (data) => {
        const { x, y, color, size, opacity, tool } = data;
        const ctx = contextRef.current;
        const prevAlpha = ctx.globalAlpha;
        const prevStroke = ctx.strokeStyle;
        const prevWidth = ctx.lineWidth;

        ctx.globalAlpha = opacity || 1;
        ctx.strokeStyle = color;
        ctx.lineWidth = size;
        ctx.beginPath();
        ctx.arc(x, y, size / 2, 0, Math.PI * 2);
        ctx.fillStyle = color;
        ctx.fill();

        ctx.globalAlpha = prevAlpha;
        ctx.strokeStyle = prevStroke;
        ctx.lineWidth = prevWidth;
    };

    const eraseRemote = (data) => {
        const { x, y, size } = data;
        const ctx = contextRef.current;
        const prevGCO = ctx.globalCompositeOperation;
        ctx.globalCompositeOperation = "destination-out";
        ctx.beginPath();
        ctx.arc(x, y, size, 0, Math.PI * 2);
        ctx.fill();
        ctx.globalCompositeOperation = prevGCO;
    };

    const handleClearBoard = () => {
        if (window.confirm("Clear all?")) socket.emit("clear-board", { roomId, userId: user._id });
    };

    return (
        <div className="wb-layout-container">
            {/* Header */}
            <header className="wb-header-miro">
                <div className="flex items-center gap-4">
                    <div className="w-10 h-10 bg-violet-600 rounded-xl flex items-center justify-center cursor-pointer" onClick={() => navigate('/dashboard')}>
                        <span className="text-white font-black text-xl">C</span>
                    </div>
                    <div>
                        <h1 className="font-bold text-slate-800 leading-tight">{room?.roomName || 'Untitled Board'}</h1>
                        <div className="flex items-center gap-2 text-[10px] text-slate-400 font-bold uppercase tracking-widest">
                            <span>ID: {roomId}</span>
                            <span className="w-1 h-1 rounded-full bg-slate-300"></span>
                            <span className="text-violet-600">{participants.length} Active</span>
                        </div>
                    </div>
                </div>

                <div className="flex items-center gap-3">
                    <button className="btn-secondary-miro" onClick={() => {
                        navigator.clipboard.writeText(window.location.href);
                        toast.success("Link copied!");
                    }}>Invite Collaborators</button>
                    <button className="btn-primary-miro">Share Screen</button>
                    <div className="w-px h-6 bg-slate-200 mx-1"></div>
                    <button className="tool-button" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
                        <span className="text-lg">üí¨</span>
                    </button>
                </div>
            </header>

            {/* Left Sidebar */}
            <aside className="wb-left-panel">
                <Toolbar
                    activeTool={tool}
                    setTool={setTool}
                    isHost={user._id === room?.host}
                    onClear={handleClearBoard}
                    onUndo={handleUndo}
                    onRedo={handleRedo}
                    onDelete={() => { }}
                />
            </aside>

            {/* Viewport & Canvas */}
            <main className="wb-viewport">
                <div className="canvas-miro shadow-2xl rounded-sm border border-slate-100 overflow-hidden">
                    <canvas
                        ref={canvasRef}
                        onMouseDown={startDrawing}
                        onMouseMove={draw}
                        onMouseUp={stopDrawing}
                        onMouseOut={stopDrawing}
                    />
                </div>

                {/* Floating Styling Panel */}
                <StylingPanel
                    tool={tool}
                    color={color} setColor={setColor}
                    size={size} setSize={setSize}
                    opacity={opacity} setOpacity={setOpacity}
                    strokeStyle={strokeStyle} setStrokeStyle={setStrokeStyle}
                    fill={fill} setFill={setFill}
                />

                {/* Zoom Controls Overlay */}
                <div className="absolute bottom-6 right-6 flex items-center gap-2 bg-white p-1 rounded-xl shadow-lg border border-slate-200">
                    <button className="w-8 h-8 hover:bg-slate-50 rounded-lg text-xs font-bold text-slate-600">100%</button>
                    <div className="w-px h-4 bg-slate-100"></div>
                    <button className="w-8 h-8 hover:bg-slate-50 rounded-lg">üñ•Ô∏è</button>
                </div>
            </main>

            {/* Right Sidebar */}
            <aside className={`wb-right-panel ${isSidebarOpen ? 'open' : ''}`}>
                <div className="flex flex-col h-full">
                    <div className="flex p-2 bg-slate-50 border-b border-slate-200">
                        {['chat', 'files', 'users'].map((tab) => (
                            <button
                                key={tab}
                                onClick={() => setActiveTab(tab)}
                                className={`flex-1 py-1.5 rounded-lg text-[10px] font-bold transition-all uppercase tracking-widest
                                          ${activeTab === tab ? "bg-white text-violet-700 shadow-sm border border-slate-200" : "text-slate-500 hover:text-slate-700"}`}
                            >
                                {tab}
                            </button>
                        ))}
                    </div>
                    <div className="flex-1 overflow-hidden">
                        {activeTab === "chat" ? (
                            <Chat messages={messages} onSendMessage={(txt) => socket.emit("chat-message", { roomId, message: txt, userId: user._id, userName: user.name })} currentUser={user} />
                        ) : activeTab === "files" ? (
                            <div className="p-4">
                                <label className="btn-secondary-miro w-full flex justify-center cursor-pointer text-xs mb-4">
                                    <span>Share Asset</span>
                                    <input type="file" className="hidden" />
                                </label>
                                <div className="space-y-2">
                                    {sharedFiles.map((f, i) => (
                                        <div key={i} className="p-3 bg-slate-50 rounded-lg border border-slate-100 flex items-center justify-between">
                                            <span className="text-xs font-medium truncate max-w-[150px]">{f.fileName}</span>
                                            <span className="text-violet-600">‚¨áÔ∏è</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ) : (
                            <div className="p-4 space-y-2">
                                {participants.map((p, i) => (
                                    <div key={i} className="flex items-center gap-3 p-2 hover:bg-slate-50 rounded-lg">
                                        <div className="w-6 h-6 rounded-full bg-violet-100 flex items-center justify-center text-[10px] font-bold text-violet-700">{p.userName[0]}</div>
                                        <span className="text-xs font-medium text-slate-700">{p.userName}</span>
                                        <div className="w-1.5 h-1.5 rounded-full bg-green-500 ml-auto"></div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>
            </aside>
        </div>
    );
};

export default WhiteboardRoom;
