import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { roomService } from "../services/api";
import toast from "react-hot-toast";

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newRoomName, setNewRoomName] = useState("");
  const [joinRoomId, setJoinRoomId] = useState("");

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        const res = await roomService.getUserRooms();
        setRooms(res.data.rooms);
      } catch (err) {
        toast.error("Failed to load boards");
      } finally {
        setLoading(false);
      }
    };
    fetchRooms();
  }, []);

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    if (!newRoomName.trim()) return;
    try {
      const res = await roomService.createRoom({ roomName: newRoomName });
      navigate(`/room/${res.data.room.roomId}`);
      toast.success("Board created!");
    } catch (err) {
      toast.error("Creation failed");
    }
  };

  const handleJoinRoom = async (e) => {
    e.preventDefault();
    if (!joinRoomId.trim()) return;
    try {
      await roomService.joinRoom(joinRoomId);
      navigate(`/room/${joinRoomId}`);
    } catch (err) {
      toast.error("Board not found or access denied");
    }
  };

  return (
    <div className="min-h-screen bg-slate-50 flex flex-col">
      {/* Top Navigation */}
      <nav className="fixed top-0 w-full z-50 bg-white/80 backdrop-blur-md border-b border-slate-200 px-8 py-4 flex items-center justify-between">
        <div className="flex items-center gap-2 cursor-pointer" onClick={() => navigate('/')}>
          <div className="w-8 h-8 rounded-lg bg-violet-600 flex items-center justify-center shadow-lg shadow-violet-200">
            <span className="text-white font-black">C</span>
          </div>
          <span className="text-xl font-black text-slate-900 tracking-tight">CollabBoard</span>
        </div>

        <div className="flex items-center gap-6">
          <div className="flex items-center gap-3 pr-6 border-r border-slate-200">
            <Link to="/profile" className="flex items-center gap-2 group">
              <div className="w-8 h-8 rounded-full bg-slate-100 flex items-center justify-center text-xs font-bold text-slate-600 border border-slate-200 group-hover:bg-violet-600 group-hover:text-white transition-all">
                {user?.name?.[0]}
              </div>
              <span className="text-sm font-semibold text-slate-700 hidden sm:block">{user?.name}</span>
            </Link>
          </div>
          <button onClick={logout} className="text-sm font-bold text-slate-400 hover:text-red-500 transition-colors uppercase tracking-widest">Logout</button>
        </div>
      </nav>

      <main className="flex-1 mt-24 max-w-7xl w-full mx-auto p-8 lg:p-12">
        <header className="mb-12">
          <h1 className="text-4xl font-black text-slate-900 tracking-tight mb-2">Welcome, {user?.name.split(' ')[0]} ðŸ‘‹</h1>
          <p className="text-slate-500 font-medium text-lg">Your collaborative design space is ready.</p>
        </header>

        {/* Dashboard Actions */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-16">
          <div className="bg-white p-8 rounded-3xl border border-slate-200 shadow-sm group hover:border-violet-300 hover:shadow-xl hover:shadow-violet-500/5 transition-all duration-500">
            <div className="w-12 h-12 bg-violet-100 rounded-2xl flex items-center justify-center mb-6 text-2xl group-hover:scale-110 transition-transform">âœ¨</div>
            <h2 className="text-xl font-bold text-slate-900 mb-2">New Workspace</h2>
            <form onSubmit={handleCreateRoom} className="space-y-4">
              <input
                type="text"
                className="w-full bg-slate-50 border border-slate-200 rounded-xl px-4 py-3 focus:outline-none focus:border-violet-500 transition-all font-medium"
                placeholder="E.g. Project brainstorm"
                value={newRoomName}
                onChange={(e) => setNewRoomName(e.target.value)}
              />
              <button type="submit" className="w-full btn-primary-miro py-3">Create Board</button>
            </form>
          </div>

          <div className="bg-white p-8 rounded-3xl border border-slate-200 shadow-sm group hover:border-violet-300 hover:shadow-xl hover:shadow-violet-500/5 transition-all duration-500">
            <div className="w-12 h-12 bg-slate-100 rounded-2xl flex items-center justify-center mb-6 text-2xl group-hover:scale-110 transition-transform">ðŸ”—</div>
            <h2 className="text-xl font-bold text-slate-900 mb-2">Join by ID</h2>
            <form onSubmit={handleJoinRoom} className="space-y-4">
              <input
                type="text"
                className="w-full bg-slate-50 border border-slate-200 rounded-xl px-4 py-3 focus:outline-none focus:border-slate-400 transition-all font-medium"
                placeholder="Past entry code here..."
                value={joinRoomId}
                onChange={(e) => setJoinRoomId(e.target.value)}
              />
              <button type="submit" className="w-full btn-secondary-miro py-3">Enter Room</button>
            </form>
          </div>
        </div>

        {/* Recently Used Section */}
        <div>
          <div className="flex items-center justify-between mb-8">
            <h3 className="text-sm font-black text-slate-400 uppercase tracking-[0.2em]">Recent Boards</h3>
            <span className="text-xs font-bold text-violet-600 cursor-pointer hover:underline">View All</span>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {loading ? (
              [...Array(4)].map((_, i) => <div key={i} className="h-48 bg-slate-100 rounded-3xl animate-pulse"></div>)
            ) : rooms.length === 0 ? (
              <div className="col-span-full py-20 bg-white rounded-3xl border-2 border-dashed border-slate-200 flex flex-col items-center justify-center">
                <span className="text-4xl mb-4">ðŸŽ¨</span>
                <p className="font-bold text-slate-500">No boards yet. Start by creating one!</p>
              </div>
            ) : (
              rooms.map((room) => (
                <Link key={room._id} to={`/room/${room.roomId}`} className="group relative">
                  <div className="bg-white p-6 rounded-3xl border border-slate-200 shadow-sm group-hover:shadow-2xl group-hover:shadow-slate-200 group-hover:-translate-y-1 transition-all duration-300 h-full flex flex-col">
                    <div className="flex-1 h-32 bg-slate-50 rounded-2xl mb-4 overflow-hidden relative">
                      <div className="absolute inset-0 bg-gradient-to-br from-violet-500/5 to-transparent"></div>
                      {/* Placeholder preview grid */}
                      <div className="grid grid-cols-8 grid-rows-8 h-full opacity-10">
                        {[...Array(64)].map((_, i) => <div key={i} className="border-[0.5px] border-slate-300"></div>)}
                      </div>
                    </div>
                    <h4 className="font-bold text-slate-800 text-lg group-hover:text-violet-600 transition-colors uppercase tracking-tight">{room.roomName}</h4>
                    <div className="flex items-center justify-between mt-4">
                      <span className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">ID: {room.roomId.slice(0, 8)}</span>
                      <div className="flex -space-x-2">
                        {[...Array(Math.min(room.participants.length, 3))].map((_, i) => (
                          <div key={i} className="w-6 h-6 rounded-full bg-slate-200 border-2 border-white flex items-center justify-center text-[8px] font-bold text-slate-600">
                            {i === 0 ? 'ðŸ‘¤' : (room.participants.length > 3 && i === 2) ? `+${room.participants.length - 2}` : 'ðŸ‘¥'}
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </Link>
              ))
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
