import React, { useEffect, useRef, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { useSocket } from "../context/SocketContext";
import { roomService } from "../services/api";
import Toolbar from "../components/Toolbar";
import Chat from "../components/Chat";
import StylingPanel from "../components/StylingPanel";
import toast from "react-hot-toast";
import { Plus, Minus } from "lucide-react";

const WhiteboardRoom = () => {
    const { roomId } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const { socket } = useSocket();

    const canvasRef = useRef(null);
    const viewportRef = useRef(null);
    const contextRef = useRef(null);

    // Tools & Styling
    const [tool, setTool] = useState("pencil");
    const [showStyling, setShowStyling] = useState(true);
    const [color, setColor] = useState("#000000");
    const [size, setSize] = useState(4);
    const [opacity, setOpacity] = useState(1);
    const [strokeStyle, setStrokeStyle] = useState('solid');
    const [fill, setFill] = useState(false);

    // Camera / Infinite Canvas States (Refs for performance, state for UI)
    const [cameraScale, setCameraScale] = useState(1);
    const [cameraOffset, setCameraOffset] = useState({ x: -4000 + window.innerWidth / 2, y: -4000 + window.innerHeight / 2 });

    const cameraScaleRef = useRef(1);
    const cameraOffsetRef = useRef({ x: -4000 + window.innerWidth / 2, y: -4000 + window.innerHeight / 2 });
    const isPanningRef = useRef(false);
    const panStartRef = useRef({ x: 0, y: 0 });
    const worldRef = useRef(null);

    // Structured Data State
    const [strokes, setStrokes] = useState([]); // Array of { type, points, color, size, opacity, ... }

    // Performance Refs for Drawing
    const isDrawingRef = useRef(false);
    const currentPointsRef = useRef([]);
    const startPosRef = useRef({ x: 0, y: 0 });
    const lastPosRef = useRef({ x: 0, y: 0 });
    const snapshotCanvasRef = useRef(null);
    const backgroundRef = useRef(null);
    const animationFrameRef = useRef(null);
    const lastCursorEmitRef = useRef(0);
    // Image upload to canvas
    const imageFileRef = useRef(null);
    const imagePlacePosRef = useRef({ x: 0, y: 0 });
    const imageCache = useRef(new Map());

    const colors = [
        "#000000", "#475569", "#dc2626", "#ea580c", "#d97706",
        "#65a30d", "#0891b2", "#2563eb", "#7c3aed", "#db2777"
    ];

    const isDrawingTool = ["pencil", "highlighter", "soft-brush", "marker", "smooth-pencil",
        "rect", "circle", "line", "arrow", "double-arrow", "rounded-rect", "ellipse",
        "triangle", "diamond", "sticky", "speech-bubble", "parallelogram",
        "cylinder", "document", "cloud"
    ].includes(tool);

    // Dynamic canvas cursor based on active tool
    const canvasCursor = {
        select: 'default',
        pan: 'grab',
        eraser: 'cell',
        'precision-eraser': 'cell',
        text: 'text',
        upload: 'copy',
    }[tool] || 'crosshair';

    const handleToolChange = (newTool) => {
        if (tool === newTool) {
            setShowStyling(!showStyling);
        } else {
            setTool(newTool);
            setShowStyling(true);
        }
    };

    const [room, setRoom] = useState(null);
    const [participants, setParticipants] = useState([]);
    const [messages, setMessages] = useState([]);
    const [sharedFiles, setSharedFiles] = useState([]);
    const [activeTab, setActiveTab] = useState("chat");
    const [loadingFileUpload, setLoadingFileUpload] = useState(false);
    const [screenStream, setScreenStream] = useState(null);
    const screenVideoRef = useRef(null);
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);
    const [remoteCursors, setRemoteCursors] = useState({});

    // History & Dragging
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);

    useEffect(() => {
        const fetchRoomDetails = async () => {
            try {
                const res = await roomService.getRoom(roomId);
                setRoom(res.data.room);
                setSharedFiles(res.data.room.sharedFiles || []);
            } catch (err) {
                toast.error("Failed to load workspace");
                navigate("/dashboard");
            }
        };

        fetchRoomDetails();

        if (socket) {
            socket.emit("join-room", { roomId, userId: user._id, userName: user.name });

            socket.on("user-joined", (data) => toast.success(`${data.userName} joined`));
            socket.on("online-users", (users) => setParticipants(users));
            socket.on("chat-message", (msg) => setMessages((prev) => [...prev, msg]));
            socket.on("chat-history", (h) => setMessages(h));
            socket.on("drawing", (data) => drawRemote(data));
            socket.on("erase", (data) => eraseRemote(data));
            socket.on("board-cleared", () => {
                clearCanvasLocal();
                setHistory([]);
                setHistoryIndex(-1);
            });
            socket.on("canvas-state", (data) => {
                if (data) {
                    applyCanvasState(data);
                    setHistory([data]);
                    setHistoryIndex(0);
                }
            });
            socket.on("undo", (state) => applyCanvasState(state));
            socket.on("redo", (state) => applyCanvasState(state));
            socket.on("user-left", (data) => toast(`ðŸ‘‹ ${data.userName} left`));
            socket.on("new-stroke", (stroke) => setStrokes(prev => [...prev, stroke]));
            socket.on("board-state-updated", (updatedStrokes) => setStrokes(updatedStrokes));
            socket.on("new-file", (file) => setSharedFiles(prev => [...prev, file]));
            socket.on("cursor-move", (data) => {
                setRemoteCursors(prev => ({ ...prev, [data.socketId]: data }));
                setTimeout(() => setRemoteCursors(prev => { const n = { ...prev }; delete n[data.socketId]; return n; }), 3000);
            });
        }

        return () => {
            if (socket) {
                socket.emit("leave-room", { roomId, userId: user._id, userName: user.name });
                ["user-joined", "online-users", "chat-message", "chat-history", "drawing", "erase",
                    "board-cleared", "canvas-state", "undo", "redo", "user-left", "new-stroke",
                    "board-state-updated", "new-file", "cursor-move"].forEach(ev => socket.off(ev));
            }
        };
    }, [roomId, socket, user, navigate]);

    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const updateCanvasSize = () => {
            const dpr = window.devicePixelRatio || 1;
            canvas.width = window.innerWidth * dpr;
            canvas.height = window.innerHeight * dpr;

            const context = canvas.getContext("2d", { alpha: true });
            context.lineCap = "round";
            context.lineJoin = "round";
            contextRef.current = context;

            // Re-setup snapshot canvas
            if (!snapshotCanvasRef.current) {
                snapshotCanvasRef.current = document.createElement('canvas');
            }
            snapshotCanvasRef.current.width = canvas.width;
            snapshotCanvasRef.current.height = canvas.height;

            redrawCanvas();
        };

        window.addEventListener('resize', updateCanvasSize);
        updateCanvasSize();

        return () => window.removeEventListener('resize', updateCanvasSize);
    }, []);

    useEffect(() => {
        const handleZoomIn = () => updateZoom(1.1);
        const handleZoomOut = () => updateZoom(1 / 1.1);
        const handleResetZoom = () => {
            const newScale = 1;
            const newOffset = { x: -8192 / 2 + window.innerWidth / 2, y: -8192 / 2 + window.innerHeight / 2 };
            cameraScaleRef.current = newScale;
            cameraOffsetRef.current = newOffset;
            setCameraScale(newScale);
            setCameraOffset(newOffset);
            syncViewDOM();
        };

        window.addEventListener('wb-zoom-in', handleZoomIn);
        window.addEventListener('wb-zoom-out', handleZoomOut);
        window.addEventListener('wb-reset-view', handleResetZoom);

        return () => {
            window.removeEventListener('wb-zoom-in', handleZoomIn);
            window.removeEventListener('wb-zoom-out', handleZoomOut);
            window.removeEventListener('wb-reset-view', handleResetZoom);
        };
    }, []);

    useEffect(() => {
        const viewport = viewportRef.current;
        if (!viewport) return;

        const handleWheel = (e) => {
            e.preventDefault();

            if (e.ctrlKey) {
                // Zoom centered on cursor
                const zoomSpeed = 0.0012;
                const delta = -e.deltaY * zoomSpeed * cameraScaleRef.current;
                const newScale = Math.max(0.05, Math.min(10, cameraScaleRef.current + delta));

                const rect = viewport.getBoundingClientRect();
                const mouseX = e.clientX - rect.left;
                const mouseY = e.clientY - rect.top;

                // Relative point on the world map
                const worldX = (mouseX - cameraOffsetRef.current.x) / cameraScaleRef.current;
                const worldY = (mouseY - cameraOffsetRef.current.y) / cameraScaleRef.current;

                cameraScaleRef.current = newScale;
                cameraOffsetRef.current = {
                    x: mouseX - worldX * newScale,
                    y: mouseY - worldY * newScale
                };

                setCameraScale(newScale);
                setCameraOffset(cameraOffsetRef.current);
                syncViewDOM();
            } else {
                // Precise Panning
                cameraOffsetRef.current = {
                    x: cameraOffsetRef.current.x - e.deltaX,
                    y: cameraOffsetRef.current.y - e.deltaY
                };
                syncViewDOM();
                // Optionally throttle state update for UI
                setCameraOffset(cameraOffsetRef.current);
            }
        };

        viewport.addEventListener('wheel', handleWheel, { passive: false });
        return () => viewport.removeEventListener('wheel', handleWheel);
    }, []);

    const updateZoom = (factor) => {
        const newScale = Math.max(0.05, Math.min(10, cameraScaleRef.current * factor));
        const centerX = window.innerWidth / 2;
        const centerY = window.innerHeight / 2;

        const worldX = (centerX - cameraOffsetRef.current.x) / cameraScaleRef.current;
        const worldY = (centerY - cameraOffsetRef.current.y) / cameraScaleRef.current;

        cameraScaleRef.current = newScale;
        cameraOffsetRef.current = {
            x: centerX - worldX * newScale,
            y: centerY - worldY * newScale
        };

        setCameraScale(newScale);
        setCameraOffset(cameraOffsetRef.current);
        syncViewDOM();
    };

    const syncViewDOM = () => {
        // We no longer scale the container. Panning/Zooming is handled via canvas transform.
        // We still update the background grid for visual feedback.
        if (viewportRef.current) {
            const scale = cameraScaleRef.current;
            const ox = cameraOffsetRef.current.x;
            const oy = cameraOffsetRef.current.y;
            viewportRef.current.style.backgroundSize = `${24 * scale}px ${24 * scale}px`;
            viewportRef.current.style.backgroundPosition = `${ox}px ${oy}px`;
        }
        redrawCanvas();
    };

    const redrawCanvas = () => {
        const canvas = canvasRef.current;
        const ctx = contextRef.current;
        if (!canvas || !ctx) return;

        const dpr = window.devicePixelRatio || 1;

        // 1. Clear the physical canvas
        ctx.setTransform(1, 0, 0, 1, 0, 0);
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Draw background image (applied canvas state) if available
        if (backgroundRef.current) {
            try {
                ctx.drawImage(backgroundRef.current, 0, 0, canvas.width, canvas.height);
            } catch (err) {
                // ignore draw errors
            }
        }

        // 2. Apply Camera Transform (DPR * Scale * Offset)
        ctx.setTransform(
            cameraScaleRef.current * dpr, 0,
            0, cameraScaleRef.current * dpr,
            cameraOffsetRef.current.x * dpr, cameraOffsetRef.current.y * dpr
        );

        // 3. Render all strokes
        strokes.forEach(stroke => {
            renderStroke(ctx, stroke);
        });
    };

    useEffect(() => {
        redrawCanvas();
    }, [strokes]);

    const clearCanvasLocal = () => {
        setStrokes([]);
        redrawCanvas();
    };

    const renderStroke = (ctx, stroke) => {
        const { type, points, color, size, opacity, strokeStyle, fill } = stroke;

        // â”€â”€â”€ Text strokes â”€â”€â”€
        if (type === 'text') {
            ctx.save();
            ctx.font = `bold ${size}px Inter, sans-serif`;
            ctx.fillStyle = color;
            ctx.globalAlpha = opacity;
            ctx.globalCompositeOperation = "source-over";
            ctx.shadowBlur = 0;
            ctx.fillText(stroke.text || '', points[0].x, points[0].y);
            ctx.restore();
            return;
        }

        // â”€â”€â”€ Image strokes â”€â”€â”€
        if (type === 'image') {
            const cached = imageCache.current.get(stroke.src);
            if (cached) {
                ctx.save();
                ctx.globalAlpha = stroke.opacity || 1;
                ctx.drawImage(cached, points[0].x, points[0].y, stroke.imgW || 300, stroke.imgH || 200);
                ctx.restore();
            } else {
                const img = new window.Image();
                img.onload = () => { imageCache.current.set(stroke.src, img); redrawCanvas(); };
                img.src = stroke.src;
            }
            return;
        }

        ctx.save();

        if (type === 'eraser-precision' || type === 'mask') {
            ctx.globalCompositeOperation = "destination-out";
        } else {
            ctx.globalCompositeOperation = "source-over";
            ctx.globalAlpha = opacity;
            ctx.strokeStyle = color;
            ctx.fillStyle = color;
            ctx.lineWidth = size;
            ctx.setLineDash(strokeStyle === 'dashed' ? [10, 10] : []);

            if (type === 'soft-brush') {
                ctx.shadowBlur = size / 2;
                ctx.shadowColor = color;
            }
            if (type === 'marker') {
                ctx.globalAlpha = opacity * 0.6;
            }
        }

        if (['pencil', 'highlighter', 'soft-brush', 'marker', 'smooth-pencil', 'eraser-precision'].includes(type)) {
            if (points.length < 2) {
                ctx.restore();
                return;
            }
            ctx.beginPath();
            ctx.moveTo(points[0].x, points[0].y);
            for (let i = 1; i < points.length; i++) {
                ctx.lineTo(points[i].x, points[i].y);
            }
            ctx.stroke();
        } else {
            drawShape(ctx, type, points, { color, size, opacity, strokeStyle, fill });
        }
        ctx.restore();
    };

    const drawShape = (ctx, type, points, style) => {
        const start = points[0];
        const end = points[points.length - 1];
        const w = end.x - start.x;
        const h = end.y - start.y;

        if (type === 'rect') {
            if (style.fill) ctx.fillRect(start.x, start.y, w, h);
            ctx.strokeRect(start.x, start.y, w, h);
        } else if (type === 'rounded-rect') {
            ctx.beginPath();
            ctx.roundRect(start.x, start.y, w, h, Math.min(Math.abs(w), Math.abs(h)) * 0.2);
            if (style.fill) ctx.fill();
            ctx.stroke();
        } else if (type === 'circle' || type === 'ellipse') {
            ctx.beginPath();
            if (type === 'circle') {
                const radius = Math.sqrt(w * w + h * h);
                ctx.arc(start.x, start.y, radius, 0, 2 * Math.PI);
            } else {
                ctx.ellipse(start.x + w / 2, start.y + h / 2, Math.abs(w / 2), Math.abs(h / 2), 0, 0, 2 * Math.PI);
            }
            if (style.fill) ctx.fill();
            ctx.stroke();
        } else if (type === 'triangle') {
            ctx.beginPath();
            ctx.moveTo(start.x + w / 2, start.y);
            ctx.lineTo(end.x, end.y);
            ctx.lineTo(start.x, end.y);
            ctx.closePath();
            if (style.fill) ctx.fill();
            ctx.stroke();
        } else if (type === 'diamond') {
            ctx.beginPath();
            ctx.moveTo(start.x + w / 2, start.y);
            ctx.lineTo(end.x, start.y + h / 2);
            ctx.lineTo(start.x + w / 2, end.y);
            ctx.lineTo(start.x, start.y + h / 2);
            ctx.closePath();
            if (style.fill) ctx.fill();
            ctx.stroke();
        } else if (type === 'line' || type === 'arrow' || type === 'double-arrow') {
            ctx.beginPath();
            ctx.moveTo(start.x, start.y);
            ctx.lineTo(end.x, end.y);
            ctx.stroke();
            if (type === 'arrow' || type === 'double-arrow') {
                drawArrowHead(ctx, start.x, start.y, end.x, end.y, style.size);
            }
            if (type === 'double-arrow') {
                drawArrowHead(ctx, end.x, end.y, start.x, start.y, style.size);
            }
        } else if (type === 'sticky') {
            ctx.save();
            ctx.fillStyle = style.fill ? style.color : "#fef08a"; // Default yellow sticky
            ctx.shadowBlur = 10;
            ctx.shadowColor = "rgba(0,0,0,0.1)";
            ctx.fillRect(start.x, start.y, w, h);
            ctx.strokeStyle = "rgba(0,0,0,0.05)";
            ctx.strokeRect(start.x, start.y, w, h);
            ctx.restore();
        } else if (type === 'speech-bubble') {
            ctx.beginPath();
            ctx.roundRect(start.x, start.y, w, h * 0.8, 10);
            ctx.moveTo(start.x + w * 0.2, start.y + h * 0.8);
            ctx.lineTo(start.x + w * 0.1, end.y);
            ctx.lineTo(start.x + w * 0.4, start.y + h * 0.8);
            if (style.fill) ctx.fill();
            ctx.stroke();
        } else if (type === 'parallelogram') {
            const shift = w * 0.2;
            ctx.beginPath();
            ctx.moveTo(start.x + shift, start.y);
            ctx.lineTo(end.x, start.y);
            ctx.lineTo(end.x - shift, end.y);
            ctx.lineTo(start.x, end.y);
            ctx.closePath();
            if (style.fill) ctx.fill();
            ctx.stroke();
        } else if (type === 'cylinder') {
            const ry = Math.abs(h * 0.1);
            // Bottom face
            ctx.beginPath();
            ctx.ellipse(start.x + w / 2, end.y - ry, Math.abs(w / 2), ry, 0, 0, Math.PI, false);
            ctx.stroke();
            // Side lines
            ctx.beginPath();
            ctx.moveTo(start.x, start.y + ry);
            ctx.lineTo(start.x, end.y - ry);
            ctx.moveTo(end.x, start.y + ry);
            ctx.lineTo(end.x, end.y - ry);
            ctx.stroke();
            // Top face
            ctx.beginPath();
            ctx.ellipse(start.x + w / 2, start.y + ry, Math.abs(w / 2), ry, 0, 0, 2 * Math.PI);
            if (style.fill) ctx.fill();
            ctx.stroke();
        } else if (type === 'document') {
            const fold = Math.min(w, h) * 0.2;
            ctx.beginPath();
            ctx.moveTo(start.x, start.y);
            ctx.lineTo(end.x - fold, start.y);
            ctx.lineTo(end.x, start.y + fold);
            ctx.lineTo(end.x, end.y);
            ctx.lineTo(start.x, end.y);
            ctx.closePath();
            if (style.fill) ctx.fill();
            ctx.stroke();
            // Fold line
            ctx.beginPath();
            ctx.moveTo(end.x - fold, start.y);
            ctx.lineTo(end.x - fold, start.y + fold);
            ctx.lineTo(end.x, start.y + fold);
            ctx.stroke();
        } else if (type === 'cloud') {
            ctx.beginPath();
            const mw = start.x + w / 2;
            const mh = start.y + h / 2;
            ctx.arc(mw - w * 0.2, mh, Math.abs(h * 0.3), 0, 2 * Math.PI);
            ctx.arc(mw + w * 0.2, mh, Math.abs(h * 0.3), 0, 2 * Math.PI);
            ctx.arc(mw, mh - h * 0.2, Math.abs(h * 0.3), 0, 2 * Math.PI);
            ctx.arc(mw, mh + h * 0.2, Math.abs(h * 0.3), 0, 2 * Math.PI);
            if (style.fill) ctx.fill();
            ctx.stroke();
        }
    };

    const drawArrowHead = (ctx, fromx, fromy, tox, toy, weight) => {
        const headlen = weight * 4;
        const angle = Math.atan2(toy - fromy, tox - fromx);
        ctx.save();
        ctx.translate(tox, toy);
        ctx.rotate(angle);
        ctx.beginPath();
        ctx.moveTo(0, 0);
        ctx.lineTo(-headlen, -headlen / 2);
        ctx.lineTo(-headlen, headlen / 2);
        ctx.closePath();
        ctx.fill();
        ctx.restore();
    };


    const getMousePos = (e) => {
        const viewport = viewportRef.current;
        if (!viewport) return { x: 0, y: 0 };
        const rect = viewport.getBoundingClientRect();

        // Convert screen coordinates to world coordinates
        return {
            x: (e.clientX - rect.left - cameraOffsetRef.current.x) / cameraScaleRef.current,
            y: (e.clientY - rect.top - cameraOffsetRef.current.y) / cameraScaleRef.current
        };
    };

    const startDrawing = (e) => {
        // Handle Panning with Middle Mouse or Pan Tool
        if (e.button === 1 || tool === 'pan') {
            isPanningRef.current = true;
            panStartRef.current = { x: e.clientX - cameraOffsetRef.current.x, y: e.clientY - cameraOffsetRef.current.y };
            return;
        }

        const canvas = canvasRef.current;
        if (!canvas || !contextRef.current) return;

        const pos = getMousePos(e);

        // â”€â”€â”€ Select Tool â”€â”€â”€ (no drawing)
        if (tool === 'select') return;

        // â”€â”€â”€ Upload Image Tool â”€â”€â”€
        if (tool === 'upload') {
            imagePlacePosRef.current = pos;
            imageFileRef.current?.click();
            return;
        }

        if (tool === 'eraser') {
            eraseObjectAt(pos);
            return;
        }

        // â”€â”€â”€ Text Tool â”€â”€â”€
        if (tool === 'text') {
            const textInput = prompt('Enter text:');
            if (!textInput) return;
            const ctx = contextRef.current;
            if (!ctx) return;
            const dpr = window.devicePixelRatio || 1;
            ctx.setTransform(
                cameraScaleRef.current * dpr, 0,
                0, cameraScaleRef.current * dpr,
                cameraOffsetRef.current.x * dpr, cameraOffsetRef.current.y * dpr
            );
            ctx.font = `bold ${size * 4}px Inter, sans-serif`;
            ctx.fillStyle = color;
            ctx.globalAlpha = opacity;
            ctx.globalCompositeOperation = 'source-over';
            ctx.shadowBlur = 0;
            ctx.fillText(textInput, pos.x, pos.y);
            const textStroke = {
                id: Date.now() + Math.random(), type: 'text', text: textInput,
                points: [pos], color, size: size * 4, opacity, strokeStyle, fill
            };
            setStrokes(prev => [...prev, textStroke]);
            socket.emit('new-stroke', { roomId, stroke: textStroke });
            const dataURL = canvasRef.current.toDataURL();
            const nh = history.slice(0, historyIndex + 1); nh.push(dataURL);
            setHistory(nh); setHistoryIndex(nh.length - 1);
            socket.emit('save-canvas', { roomId, canvasData: dataURL });
            return;
        }

        // Initialize drawing refs
        isDrawingRef.current = true;
        currentPointsRef.current = [pos];
        startPosRef.current = pos;
        lastPosRef.current = pos;

        // Efficient Snapshot of current viewport
        const snapCtx = snapshotCanvasRef.current.getContext('2d');
        snapCtx.setTransform(1, 0, 0, 1, 0, 0);
        snapCtx.clearRect(0, 0, canvas.width, canvas.height);
        snapCtx.drawImage(canvas, 0, 0);

        if (isDrawingTool) {
            const ctx = contextRef.current;
            const dpr = window.devicePixelRatio || 1;

            // Ensure context is transformed to world space for the live stroke
            ctx.setTransform(
                cameraScaleRef.current * dpr, 0,
                0, cameraScaleRef.current * dpr,
                cameraOffsetRef.current.x * dpr, cameraOffsetRef.current.y * dpr
            );

            ctx.beginPath();
            ctx.moveTo(pos.x, pos.y);

            // Set styles
            ctx.lineWidth = size;
            ctx.strokeStyle = color;
            ctx.globalAlpha = tool === "highlighter" ? (opacity * 0.4) :
                tool === "marker" ? (opacity * 0.6) : opacity;
            ctx.setLineDash(strokeStyle === 'dashed' ? [10, 10] : []);
            ctx.lineCap = "round";
            ctx.lineJoin = "round";
            ctx.globalCompositeOperation = (tool === "eraser" || tool === "precision-eraser") ? "destination-out" : "source-over";

            // Disable heavy filters during live drawing for performance
            ctx.shadowBlur = 0;
        }
    };

    const handleMouseMove = (e) => {
        // Throttled live cursor broadcast (â‰¤20 fps)
        const now = Date.now();
        if (socket && now - lastCursorEmitRef.current > 50) {
            lastCursorEmitRef.current = now;
            socket.emit('cursor-move', { roomId, cursorData: { x: e.clientX, y: e.clientY, userName: user.name, color } });
        }

        if (isPanningRef.current) {
            cameraOffsetRef.current = {
                x: e.clientX - panStartRef.current.x,
                y: e.clientY - panStartRef.current.y
            };
            syncViewDOM();
            return;
        }

        if (isDrawingRef.current) {
            if (animationFrameRef.current) {
                cancelAnimationFrame(animationFrameRef.current);
            }
            // Extract data from event because React event pooling may nullify 'e' inside rAF
            const clientX = e.clientX;
            const clientY = e.clientY;
            const shiftKey = e.shiftKey;

            animationFrameRef.current = requestAnimationFrame(() => draw({ clientX, clientY, shiftKey }));
        } else if (tool === 'eraser' && e.buttons === 1) {
            eraseObjectAt(getMousePos(e));
        }
    };

    const eraseObjectAt = (pos) => {
        // Distance-based object erasing
        const threshold = 15; // pixels
        const updatedStrokes = strokes.filter(stroke => {
            return !stroke.points.some(p => {
                const dist = Math.sqrt(Math.pow(p.x - pos.x, 2) + Math.pow(p.y - pos.y, 2));
                return dist < threshold;
            });
        });

        if (updatedStrokes.length !== strokes.length) {
            setStrokes(updatedStrokes);
            socket.emit("update-board-state", { roomId, strokes: updatedStrokes });
        }
    };

    const draw = (e) => {
        if (!isDrawingRef.current || !contextRef.current) return;
        const pos = getMousePos(e);
        let currentPos = pos;

        // Smooth Stroke Mode: Apply simple smoothing
        if (tool === 'smooth-pencil' && currentPointsRef.current.length > 0) {
            const lastPoint = currentPointsRef.current[currentPointsRef.current.length - 1];
            currentPos = {
                x: lastPoint.x * 0.8 + pos.x * 0.2, // Linear interpolation for smoothing
                y: lastPoint.y * 0.8 + pos.y * 0.2,
                shiftKey: e.shiftKey
            };
        } else {
            currentPos = { ...pos, shiftKey: e.shiftKey };
        }

        currentPointsRef.current.push(currentPos);
        const ctx = contextRef.current;

        if (["pencil", "highlighter", "soft-brush", "marker", "smooth-pencil"].includes(tool)) {
            // Straight Line Mode (Shift Key)
            if (e.shiftKey) {
                redrawCanvas(); // Reset to static state
                ctx.beginPath();
                ctx.moveTo(startPosRef.current.x, startPosRef.current.y);
                ctx.lineTo(currentPos.x, currentPos.y);
                ctx.stroke();
                return;
            }

            ctx.lineTo(currentPos.x, currentPos.y);
            ctx.stroke();

            // Emit drawing segment for real-time collab
            socket.emit("drawing", {
                roomId,
                drawingData: {
                    x: currentPos.x,
                    y: currentPos.y,
                    prevX: lastPosRef.current.x,
                    prevY: lastPosRef.current.y,
                    color,
                    size,
                    opacity: ctx.globalAlpha,
                    tool
                }
            });

            lastPosRef.current = currentPos;
        } else if (tool === "eraser" || tool === "precision-eraser") {
            const eraserSize = tool === "precision-eraser" ? 10 : size * 2;
            ctx.lineWidth = eraserSize;
            ctx.lineTo(pos.x, pos.y);
            ctx.stroke();

            socket.emit("erase", {
                roomId,
                eraseData: { x: pos.x, y: pos.y, size: eraserSize }
            });
        } else if (["rect", "circle", "line", "arrow", "double-arrow", "rounded-rect", "ellipse", "triangle", "diamond", "sticky", "speech-bubble", "parallelogram", "cylinder", "document", "cloud"].includes(tool)) {
            const dpr = window.devicePixelRatio || 1;
            ctx.setTransform(1, 0, 0, 1, 0, 0);
            ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
            ctx.drawImage(snapshotCanvasRef.current, 0, 0);

            // Re-apply camera transform for shape drawing
            ctx.setTransform(
                cameraScaleRef.current * dpr, 0,
                0, cameraScaleRef.current * dpr,
                cameraOffsetRef.current.x * dpr, cameraOffsetRef.current.y * dpr
            );

            drawShape(ctx, tool, [startPosRef.current, currentPos], { color, size, opacity, strokeStyle, fill });
        }
    };

    const drawArrow = (ctx, fromx, fromy, tox, toy) => {
        const headlen = size * 3;
        const angle = Math.atan2(toy - fromy, tox - fromx);
        ctx.moveTo(fromx, fromy);
        ctx.lineTo(tox, toy);
        ctx.lineTo(tox - headlen * Math.cos(angle - Math.PI / 6), toy - headlen * Math.sin(angle - Math.PI / 6));
        ctx.moveTo(tox, toy);
        ctx.lineTo(tox - headlen * Math.cos(angle + Math.PI / 6), toy - headlen * Math.sin(angle + Math.PI / 6));
    };

    const stopDrawing = () => {
        if (isPanningRef.current) {
            isPanningRef.current = false;
            // Final sync to state for UI consistency
            setCameraOffset(cameraOffsetRef.current);
            return;
        }

        const canvas = canvasRef.current;
        if (!isDrawingRef.current || !canvas) return;

        if (animationFrameRef.current) {
            cancelAnimationFrame(animationFrameRef.current);
        }

        isDrawingRef.current = false;

        // Save current stroke to state
        let points = [...currentPointsRef.current];

        // Straight Line Mode: If shift was held, only keep start and end points
        if (["pencil", "highlighter", "soft-brush", "marker", "smooth-pencil"].includes(tool) && points.length > 1 && points[points.length - 1].shiftKey) {
            points = [points[0], points[points.length - 1]];
        }

        const newStroke = {
            id: Date.now() + Math.random(),
            type: tool === 'precision-eraser' ? 'eraser-precision' : tool,
            points: points,
            color: color,
            size: tool === 'precision-eraser' ? 10 : size,
            opacity: tool === 'highlighter' ? 0.4 :
                tool === 'marker' ? 0.6 : opacity,
            strokeStyle,
            fill
        };

        if (tool !== 'eraser') { // Only save non-eraser strokes to the strokes array
            setStrokes(prev => [...prev, newStroke]);
            socket.emit("new-stroke", { roomId, stroke: newStroke });
        }

        currentPointsRef.current = [];

        // The following block is for canvas state history, which is now separate from strokes
        const dataURL = canvas.toDataURL();
        const newHistory = history.slice(0, historyIndex + 1);
        newHistory.push(dataURL);
        setHistory(newHistory);
        setHistoryIndex(newHistory.length - 1);
        socket.emit("save-canvas", { roomId, canvasData: dataURL });
    };

    const handleUndo = () => {
        if (historyIndex > 0) {
            const newIndex = historyIndex - 1;
            const state = history[newIndex];
            setHistoryIndex(newIndex);
            applyCanvasState(state);
            socket.emit("undo", { roomId, canvasState: state });
            socket.emit("save-canvas", { roomId, canvasData: state });
        }
    };

    const handleRedo = () => {
        if (historyIndex < history.length - 1) {
            const newIndex = historyIndex + 1;
            const state = history[newIndex];
            setHistoryIndex(newIndex);
            applyCanvasState(state);
            socket.emit("redo", { roomId, canvasState: state });
            socket.emit("save-canvas", { roomId, canvasData: state });
        }
    };

    const applyCanvasState = (state) => {
        if (!state) return;
        const canvas = canvasRef.current;
        const ctx = contextRef.current;
        if (!canvas || !ctx) return;

        const img = new Image();
        img.onload = () => {
            // Store background as an offscreen canvas so redraws persist
            try {
                const off = document.createElement('canvas');
                off.width = canvas.width;
                off.height = canvas.height;
                const offCtx = off.getContext('2d');
                offCtx.setTransform(1, 0, 0, 1, 0, 0);
                offCtx.clearRect(0, 0, off.width, off.height);
                offCtx.drawImage(img, 0, 0, off.width, off.height);
                backgroundRef.current = off;
                // Clear vector strokes because the applied state is pixel-based
                setStrokes([]);
                // Draw immediately
                redrawCanvas();
            } catch (err) {
                console.error('Failed to apply canvas state', err);
            }
        };
        img.src = state;
    };

    const drawRemote = (data) => {
        const { x, y, prevX, prevY, color, size, opacity } = data;
        const ctx = contextRef.current;
        if (!ctx) return;

        ctx.save();
        ctx.globalAlpha = opacity || 1;
        ctx.strokeStyle = color;
        ctx.lineWidth = size;
        ctx.lineCap = "round";
        ctx.lineJoin = "round";

        ctx.beginPath();
        if (prevX && prevY) {
            ctx.moveTo(prevX, prevY);
        } else {
            ctx.moveTo(x, y);
        }
        ctx.lineTo(x, y);
        ctx.stroke();
        ctx.restore();
    };

    const eraseRemote = (data) => {
        const { x, y, size } = data;
        const ctx = contextRef.current;
        const prevGCO = ctx.globalCompositeOperation;
        ctx.globalCompositeOperation = "destination-out";
        ctx.beginPath();
        ctx.arc(x, y, size, 0, Math.PI * 2);
        ctx.fill();
        ctx.globalCompositeOperation = prevGCO;
    };

    const handleClearBoard = () => {
        if (window.confirm('Clear all drawings for everyone?'))
            socket.emit('clear-board', { roomId, userId: user._id });
    };

    // â”€â”€â”€ Export canvas as PNG â”€â”€â”€
    const downloadCanvas = () => {
        const canvas = canvasRef.current;
        if (!canvas) return;
        const link = document.createElement('a');
        link.download = `${room?.roomName || 'whiteboard'}-${Date.now()}.png`;
        link.href = canvas.toDataURL('image/png');
        link.click();
        toast.success('Board exported as PNG!');
    };

    // â”€â”€â”€ Handle Image Upload to Canvas â”€â”€â”€
    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (!file) return;
        const reader = new FileReader();
        reader.onload = (ev) => {
            const src = ev.target.result;
            const img = new window.Image();
            img.onload = () => {
                const maxW = 400;
                const scale = Math.min(1, maxW / img.width);
                const imgW = Math.round(img.width * scale);
                const imgH = Math.round(img.height * scale);
                const pos = imagePlacePosRef.current;
                // Cache & draw immediately
                imageCache.current.set(src, img);
                const ctx = contextRef.current;
                if (ctx) {
                    const dpr = window.devicePixelRatio || 1;
                    ctx.save();
                    ctx.setTransform(
                        cameraScaleRef.current * dpr, 0,
                        0, cameraScaleRef.current * dpr,
                        cameraOffsetRef.current.x * dpr, cameraOffsetRef.current.y * dpr
                    );
                    ctx.globalAlpha = opacity;
                    ctx.drawImage(img, pos.x, pos.y, imgW, imgH);
                    ctx.restore();
                }
                // Save to strokes
                const imageStroke = {
                    id: Date.now() + Math.random(),
                    type: 'image', src,
                    points: [pos], imgW, imgH,
                    color, size, opacity, strokeStyle, fill
                };
                setStrokes(prev => [...prev, imageStroke]);
                if (socket) socket.emit('new-stroke', { roomId, stroke: imageStroke });
                // Save canvas history
                const dataURL = canvasRef.current?.toDataURL();
                if (dataURL) {
                    setHistory(prev => { const nh = [...prev.slice(0, historyIndex + 1), dataURL]; setHistoryIndex(nh.length - 1); return nh; });
                    if (socket) socket.emit('save-canvas', { roomId, canvasData: dataURL });
                }
            };
            img.src = src;
        };
        reader.readAsDataURL(file);
        e.target.value = '';
    };

    // â”€â”€â”€ Keyboard Shortcuts (run-once on mount) â”€â”€â”€
    React.useEffect(() => {
        const onKey = (e) => {
            if (['INPUT', 'TEXTAREA'].includes(e.target.tagName)) return;
            if (e.ctrlKey || e.metaKey) {
                if (e.key === 'z') { e.preventDefault(); handleUndo(); }
                if (e.key === 'y') { e.preventDefault(); handleRedo(); }
                return;
            }
            const map = { v: 'select', h: 'pan', p: 'pencil', e: 'eraser', t: 'text' };
            if (map[e.key.toLowerCase()]) setTool(map[e.key.toLowerCase()]);
        };
        window.addEventListener('keydown', onKey);
        return () => window.removeEventListener('keydown', onKey);
    }, []);

    // â”€â”€â”€ Screen Sharing â”€â”€â”€
    const startScreenShare = async () => {
        try {
            if (screenStream) {
                screenStream.getTracks().forEach(t => t.stop());
                setScreenStream(null);
                socket.emit('screen-share-stopped', { roomId });
                toast('Screen sharing stopped');
                return;
            }
            const stream = await navigator.mediaDevices.getDisplayMedia({ video: true, audio: false });
            setScreenStream(stream);
            socket.emit('screen-share-started', { roomId, userId: user._id, userName: user.name });
            stream.getVideoTracks()[0].onended = () => {
                setScreenStream(null);
                socket.emit('screen-share-stopped', { roomId });
                toast('Screen sharing stopped');
            };
            toast.success('Screen sharing started!');
        } catch (err) {
            if (err.name !== 'NotAllowedError') toast.error('Screen sharing failed');
        }
    };

    return (
        <div className="wb-layout-container">
            {/* Header */}
            <header className="wb-header-miro">
                <div className="flex items-center gap-6">
                    <div className="flex items-center gap-3">
                        <div className="w-9 h-9 bg-violet-600 rounded-lg flex items-center justify-center cursor-pointer shadow-lg shadow-violet-200" onClick={() => navigate('/dashboard')}>
                            <span className="text-white font-black text-lg">C</span>
                        </div>
                        <div>
                            <h1 className="font-bold text-slate-800 leading-tight text-sm truncate max-w-[150px]">{room?.roomName || 'Untitled Board'}</h1>
                            <span className="text-[10px] text-slate-400 font-bold uppercase tracking-widest">{participants.length} online</span>
                        </div>
                    </div>

                    <div className="w-px h-8 bg-slate-100"></div>

                    {/* Navbar Styling Controls */}
                    {isDrawingTool && (
                        <div className="flex items-center gap-6 animate-in fade-in slide-in-from-left-4 duration-300">
                            {/* Color Picker */}
                            <div className="flex items-center gap-2">
                                <div className="grid grid-cols-5 gap-1 pt-0.5">
                                    {colors.slice(0, 5).map(c => (
                                        <button
                                            key={c}
                                            onClick={() => setColor(c)}
                                            className={`w-4 h-4 rounded-full border border-slate-200 transition-all ${color === c ? 'ring-2 ring-violet-500 ring-offset-1 scale-110' : 'hover:scale-110'}`}
                                            style={{ backgroundColor: c }}
                                        />
                                    ))}
                                </div>
                                <div className="grid grid-cols-5 gap-1 pt-0.5">
                                    {colors.slice(5).map(c => (
                                        <button
                                            key={c}
                                            onClick={() => setColor(c)}
                                            className={`w-4 h-4 rounded-full border border-slate-200 transition-all ${color === c ? 'ring-2 ring-violet-500 ring-offset-1 scale-110' : 'hover:scale-110'}`}
                                            style={{ backgroundColor: c }}
                                        />
                                    ))}
                                </div>
                            </div>

                            <div className="w-px h-6 bg-slate-100"></div>

                            {/* Size Slider */}
                            <div className="flex items-center gap-3 w-32">
                                <span className="text-[9px] font-bold text-slate-400 uppercase tracking-widest">Size</span>
                                <input
                                    type="range" min="1" max="40" value={size}
                                    onChange={(e) => setSize(parseInt(e.target.value))}
                                    className="w-full accent-violet-600 h-1 bg-slate-100 rounded-lg appearance-none cursor-pointer"
                                />
                                <span className="text-[10px] font-mono font-bold text-slate-600 min-w-[20px]">{size}</span>
                            </div>

                            <div className="w-px h-6 bg-slate-100"></div>

                            {/* Opacity Control */}
                            <div className="flex items-center gap-3 w-32">
                                <span className="text-[9px] font-bold text-slate-400 uppercase tracking-widest">Opacity</span>
                                <input
                                    type="range" min="0.1" max="1" step="0.1" value={opacity}
                                    onChange={(e) => setOpacity(parseFloat(e.target.value))}
                                    className="w-full accent-violet-600 h-1 bg-slate-100 rounded-lg appearance-none cursor-pointer"
                                />
                                <span className="text-[10px] font-mono font-bold text-slate-600 min-w-[25px]">{Math.round(opacity * 100)}%</span>
                            </div>
                        </div>
                    )}
                </div>

                <div className="flex items-center gap-3">
                    <button className="btn-secondary-miro" onClick={downloadCanvas}>Export PNG</button>
                    <button className="btn-secondary-miro" onClick={() => {
                        navigator.clipboard.writeText(window.location.href);
                        toast.success('Link copied!');
                    }}>Invite</button>
                    <button
                        className={`btn-primary-miro ${screenStream ? '!bg-red-500 !shadow-red-200 hover:!bg-red-600' : ''}`}
                        onClick={startScreenShare}
                    >
                        {screenStream ? 'Stop Sharing' : 'Share Screen'}
                    </button>
                    <div className="w-px h-6 bg-slate-200 mx-1"></div>
                    <button className="tool-button" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
                        <span className="text-lg">ðŸ’¬</span>
                    </button>
                </div>
            </header>

            {/* Left Sidebar */}
            <aside className="wb-left-panel">
                <Toolbar
                    activeTool={tool}
                    setTool={handleToolChange}
                    isHost={room?.host?.toString() === user._id || room?.host?._id === user._id}
                    onClear={handleClearBoard}
                    onUndo={handleUndo}
                    onRedo={handleRedo}
                    onDelete={() => { }}
                />
            </aside>

            {/* Viewport & Canvas (Infinite Background) */}
            {/* Viewport & Canvas (Full Page Surface) */}
            <main
                ref={viewportRef}
                className="wb-viewport relative overflow-hidden cursor-default bg-white"
                style={{
                    backgroundImage: 'radial-gradient(#cbd5e1 1px, transparent 1px)',
                    backgroundSize: `${24 * cameraScale}px ${24 * cameraScale}px`,
                    backgroundPosition: `${cameraOffset.x}px ${cameraOffset.y}px`
                }}
            >
                {/* Main Viewport Canvas */}
                <canvas
                    ref={canvasRef}
                    className="absolute inset-0 w-full h-full"
                    style={{ cursor: canvasCursor }}
                    onMouseDown={startDrawing}
                    onMouseMove={handleMouseMove}
                    onMouseUp={stopDrawing}
                    onMouseOut={stopDrawing}
                />

                {/* Floating Styling Panel (Secondary Controls: Stroke & Fill) */}
                {/* Hidden file input for Upload Image tool */}
                <input
                    type="file" accept="image/*" className="hidden"
                    ref={imageFileRef}
                    onChange={handleImageUpload}
                />

                <StylingPanel
                    tool={tool}
                    visible={showStyling}
                    strokeStyle={strokeStyle} setStrokeStyle={setStrokeStyle}
                    fill={fill} setFill={setFill}
                />

                {/* Zoom Controls Overlay */}
                <div className="absolute bottom-6 right-6 flex items-center gap-2 bg-white p-1 rounded-xl shadow-lg border border-slate-200 z-50">
                    <button
                        onClick={() => {
                            cameraScaleRef.current = 1;
                            cameraOffsetRef.current = { x: -4000 + window.innerWidth / 2, y: -4000 + window.innerHeight / 2 };
                            setCameraScale(1); setCameraOffset(cameraOffsetRef.current); syncViewDOM();
                        }}
                        className="w-12 h-8 hover:bg-slate-50 rounded-lg text-xs font-bold text-slate-600 transition-colors"
                    >
                        {Math.round(cameraScale * 100)}%
                    </button>
                    <div className="w-px h-4 bg-slate-100"></div>
                    <button onClick={() => updateZoom(1.1)} className="w-8 h-8 hover:bg-slate-50 rounded-lg flex items-center justify-center text-slate-600">
                        <Plus size={14} />
                    </button>
                    <button onClick={() => updateZoom(1 / 1.1)} className="w-8 h-8 hover:bg-slate-50 rounded-lg flex items-center justify-center text-slate-600">
                        <Minus size={14} />
                    </button>
                </div>

                {/* â”€â”€â”€ Remote Live Cursors â”€â”€â”€ */}
                {Object.values(remoteCursors).map(cursor => (
                    <div
                        key={cursor.socketId}
                        className="fixed pointer-events-none z-[9997] transition-[top,left] duration-75"
                        style={{ left: cursor.x, top: cursor.y }}
                    >
                        <svg width="12" height="16" viewBox="0 0 12 16" xmlns="http://www.w3.org/2000/svg">
                            <path d="M0 0 L0 14 L4 10 L6.5 14.5 L8 13.5 L5.5 9 L10 9 Z"
                                fill={cursor.color || '#7c3aed'} stroke="white" strokeWidth="1" />
                        </svg>
                        <span className="text-[9px] font-bold text-white px-1.5 py-0.5 rounded-full whitespace-nowrap block ml-2 -mt-0.5 shadow-md"
                            style={{ backgroundColor: cursor.color || '#7c3aed' }}>
                            {cursor.userName}
                        </span>
                    </div>
                ))}
            </main>

            {/* Right Sidebar */}
            <aside className={`wb-right-panel ${isSidebarOpen ? 'open' : ''}`}>
                <div className="flex flex-col h-full">
                    <div className="flex p-2 bg-slate-50 border-b border-slate-200">
                        {['chat', 'files', 'users'].map((tab) => (
                            <button
                                key={tab}
                                onClick={() => setActiveTab(tab)}
                                className={`flex-1 py-1.5 rounded-lg text-[10px] font-bold transition-all uppercase tracking-widest
                                          ${activeTab === tab ? "bg-white text-violet-700 shadow-sm border border-slate-200" : "text-slate-500 hover:text-slate-700"}`}
                            >
                                {tab}
                            </button>
                        ))}
                    </div>
                    <div className="flex-1 overflow-hidden">
                        {activeTab === "chat" ? (
                            <Chat messages={messages} onSendMessage={(txt) => socket.emit("chat-message", { roomId, message: txt, userId: user._id, userName: user.name })} currentUser={user} />
                        ) : activeTab === "files" ? (
                            <div className="p-4">
                                <label className={`btn-secondary-miro w-full flex justify-center cursor-pointer text-xs mb-4 ${loadingFileUpload ? 'opacity-50 cursor-not-allowed' : ''}`}>
                                    <span>{loadingFileUpload ? 'Uploading...' : 'ðŸ“Ž Share Asset'}</span>
                                    <input
                                        type="file"
                                        className="hidden"
                                        disabled={loadingFileUpload}
                                        onChange={async (e) => {
                                            const file = e.target.files[0];
                                            if (!file) return;
                                            setLoadingFileUpload(true);
                                            try {
                                                const fd = new FormData();
                                                fd.append('file', file);
                                                const res = await roomService.uploadFile(roomId, fd);
                                                setSharedFiles(prev => [...prev, res.data.file]);
                                                socket.emit('file-shared', { roomId, file: res.data.file });
                                                toast.success(`${file.name} shared!`);
                                            } catch { toast.error('Upload failed'); }
                                            finally { setLoadingFileUpload(false); e.target.value = ''; }
                                        }}
                                    />
                                </label>
                                <div className="space-y-2">
                                    {sharedFiles.map((f, i) => (
                                        <div key={i} className="p-3 bg-slate-50 rounded-lg border border-slate-100 flex items-center justify-between">
                                            <span className="text-xs font-medium truncate max-w-[150px]">{f.fileName}</span>
                                            <span className="text-violet-600">â¬‡ï¸</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ) : (
                            <div className="p-4 space-y-2">
                                {participants.map((p, i) => (
                                    <div key={i} className="flex items-center gap-3 p-2 hover:bg-slate-50 rounded-lg">
                                        <div className="w-6 h-6 rounded-full bg-violet-100 flex items-center justify-center text-[10px] font-bold text-violet-700">{p.userName[0]}</div>
                                        <span className="text-xs font-medium text-slate-700">{p.userName}</span>
                                        <div className="w-1.5 h-1.5 rounded-full bg-green-500 ml-auto"></div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>
            </aside>
        </div>
    );
};

export default WhiteboardRoom;
