import React from "react";

const Toolbar = ({ tool, setTool, color, setColor, size, setSize, onClear, isHost }) => {
    return (
        <div className="flex flex-col gap-6 p-4 glass rounded-2xl border border-white/10 shadow-2xl">
            {/* Tool Selection */}
            <div className="flex flex-col gap-3">
                <span className="text-[10px] uppercase tracking-widest font-bold text-slate-500 ml-1">Tools</span>
                <div className="grid grid-cols-2 gap-2">
                    <button
                        className={`p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl
                                  ${tool === "pencil" ? "bg-primary-600 text-white shadow-lg shadow-primary-900/30 ring-1 ring-white/20" : "bg-white/5 text-slate-400 hover:bg-white/10"}`}
                        onClick={() => setTool("pencil")}
                        title="Pencil"
                    >
                        ‚úèÔ∏è
                    </button>
                    <button
                        className={`p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl
                                  ${tool === "eraser" ? "bg-primary-600 text-white shadow-lg shadow-primary-900/30 ring-1 ring-white/20" : "bg-white/5 text-slate-400 hover:bg-white/10"}`}
                        onClick={() => setTool("eraser")}
                        title="Eraser"
                    >
                        üßΩ
                    </button>
                    <button
                        className={`p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl bg-white/5 text-slate-400
                                  ${isHost ? "hover:bg-red-500/20 hover:text-red-400" : "opacity-30 cursor-not-allowed"}`}
                        onClick={onClear}
                        title={isHost ? "Clear Board" : "Only host can clear"}
                        disabled={!isHost}
                    >
                        üóëÔ∏è
                    </button>
                    <button
                        className="p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl bg-white/5 text-slate-400 hover:bg-white/10"
                        onClick={() => window.dispatchEvent(new CustomEvent('wb-undo'))}
                        title="Undo"
                    >
                        ‚Ü©Ô∏è
                    </button>
                    <button
                        className="p-3 rounded-xl transition-all duration-200 flex items-center justify-center text-xl bg-white/5 text-slate-400 hover:bg-white/10"
                        onClick={() => window.dispatchEvent(new CustomEvent('wb-redo'))}
                        title="Redo"
                    >
                        ‚Ü™Ô∏è
                    </button>
                </div>
            </div>

            {/* Color Picker */}
            <div className="flex flex-col gap-3">
                <span className="text-[10px] uppercase tracking-widest font-bold text-slate-500 ml-1">Color</span>
                <div className="relative group">
                    <input
                        type="color"
                        value={color}
                        onChange={(e) => setColor(e.target.value)}
                        className="w-full h-10 rounded-xl cursor-pointer bg-white/5 border border-white/10 p-1"
                        disabled={tool === "eraser"}
                    />
                    {tool === "eraser" && (
                        <div className="absolute inset-0 bg-slate-900/60 backdrop-blur-[1px] rounded-xl flex items-center justify-center text-[10px] text-white font-bold uppercase tracking-tighter">
                            Ink Off
                        </div>
                    )}
                </div>
            </div>

            {/* Brush Size */}
            <div className="flex flex-col gap-3">
                <div className="flex justify-between items-center ml-1">
                    <span className="text-[10px] uppercase tracking-widest font-bold text-slate-500">Size</span>
                    <span className="text-xs font-mono text-primary-400 font-bold">{size}px</span>
                </div>
                <input
                    type="range"
                    min="1"
                    max="20"
                    value={size}
                    onChange={(e) => setSize(parseInt(e.target.value))}
                    className="w-full accent-primary-600 bg-white/10 rounded-lg h-1.5 cursor-pointer appearance-none"
                />
                <div className="flex justify-between px-1">
                    <div className="w-1 h-1 rounded-full bg-slate-700"></div>
                    <div className="w-2 h-2 rounded-full bg-slate-700"></div>
                    <div className="w-3 h-3 rounded-full bg-slate-700"></div>
                    <div className="w-4 h-4 rounded-full bg-slate-700"></div>
                </div>
            </div>
        </div>
    );
};

export default Toolbar;
