import React, { useState, useEffect, useRef } from "react";

const Chat = ({ messages, onSendMessage, currentUser }) => {
    const [input, setInput] = useState("");
    const chatEndRef = useRef(null);

    const scrollToBottom = () => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (input.trim()) {
            onSendMessage(input.trim());
            setInput("");
        }
    };

    return (
        <div className="flex flex-col h-full overflow-hidden">
            <div className="flex-1 overflow-y-auto p-4 space-y-4 scroll-smooth">
                {messages.length === 0 ? (
                    <div className="h-full flex flex-col items-center justify-center text-center opacity-30 select-none px-4 mt-20">
                        <span className="text-4xl mb-4">ðŸ’¬</span>
                        <p className="text-sm font-medium text-slate-400">Collaborate with your team instantly</p>
                    </div>
                ) : (
                    messages.map((msg, index) => {
                        const isOwn = msg.sender === currentUser._id;
                        return (
                            <div
                                key={index}
                                className={`flex flex-col ${isOwn ? "items-end" : "items-start"}`}
                            >
                                <div className={`flex items-baseline gap-2 mb-1 px-1 ${isOwn ? "flex-row-reverse" : "flex-row"}`}>
                                    <span className="text-[10px] font-bold text-slate-500 uppercase tracking-wider">
                                        {msg.senderName}
                                    </span>
                                    <span className="text-[10px] text-slate-600 font-mono">
                                        {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                    </span>
                                </div>
                                <div
                                    className={`max-w-[85%] px-4 py-2.5 rounded-2xl text-sm shadow-sm
                                              ${isOwn
                                            ? "bg-primary-600 text-white rounded-tr-none shadow-primary-900/20"
                                            : "bg-white/5 text-slate-200 border border-white/5 rounded-tl-none"}`}
                                >
                                    {msg.text}
                                </div>
                            </div>
                        );
                    })
                )}
                <div ref={chatEndRef} />
            </div>

            <div className="p-4 border-t border-white/5">
                <form
                    className="flex bg-white/5 border border-white/10 rounded-2xl overflow-hidden focus-within:ring-2 focus-within:ring-primary-500/50 transition-all duration-300"
                    onSubmit={handleSubmit}
                >
                    <input
                        type="text"
                        placeholder="Type a message..."
                        className="flex-1 bg-transparent px-4 py-3 text-sm text-white focus:outline-none placeholder:text-slate-600"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                    />
                    <button
                        type="submit"
                        disabled={!input.trim()}
                        className="px-4 text-primary-500 hover:text-primary-400 disabled:opacity-30 transition-colors"
                    >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2.5" d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8"></path>
                        </svg>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Chat;
