import React, { useEffect, useRef, useState } from 'react';
import { Mic, MicOff, Video, VideoOff, PhoneOff, Monitor, MonitorOff } from 'lucide-react';
import toast from 'react-hot-toast';

const WebRTCMeeting = ({ socket, roomId, signalingId, isCaller, username, onLeave }) => {
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const [isMicEnabled, setIsMicEnabled] = useState(true);
    const [isCamEnabled, setIsCamEnabled] = useState(true);
    const [isScreenSharing, setIsScreenSharing] = useState(false);
    const [connectionStatus, setConnectionStatus] = useState('Initializing...');

    const pcRef = useRef(null);
    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);
    const candidateBuffer = useRef([]);
    const screenStreamRef = useRef(null);
    const isNegotiating = useRef(false);
    const hasInitialized = useRef(false);
    const currentLocalStream = useRef(null);

    // Google STUN servers
    const rtcConfig = {
        iceServers: [
            { urls: 'stun:stun.l.google.com:19302' },
            { urls: 'stun:stun1.l.google.com:19302' },
            { urls: 'stun:stun2.l.google.com:19302' },
        ],
    };

    // ─── 1. MEDIA & PC LIFECYCLE (Runs once) ───
    useEffect(() => {
        let isStopped = false;

        const initMedia = async () => {
            try {
                console.log('[WebRTC] Requesting media...');
                const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
                if (isStopped) { stream.getTracks().forEach(t => t.stop()); return; }

                setLocalStream(stream);
                currentLocalStream.current = stream;
                if (localVideoRef.current) localVideoRef.current.srcObject = stream;

                const pc = new RTCPeerConnection(rtcConfig);
                pcRef.current = pc;

                stream.getTracks().forEach(track => {
                    pc.addTrack(track, stream);
                });

                pc.ontrack = (event) => {
                    console.log('[WebRTC] Remote track received:', event.track.kind);
                    const rStream = event.streams[0];
                    setRemoteStream(rStream);
                    if (remoteVideoRef.current) remoteVideoRef.current.srcObject = rStream;
                };

                pc.oniceconnectionstatechange = () => {
                    setConnectionStatus(`Status: ${pc.iceConnectionState}`);
                };

                pc.onicecandidate = (event) => {
                    if (event.candidate && signalingId) {
                        socket.emit('call:candidate', { roomId, to: signalingId, candidate: event.candidate });
                    }
                };

                if (isCaller && signalingId && pc.signalingState === 'stable') {
                    isNegotiating.current = true;
                    try {
                        const offer = await pc.createOffer();
                        await pc.setLocalDescription(offer);
                        socket.emit('call:offer', { roomId, to: signalingId, offer });
                    } finally {
                        isNegotiating.current = false;
                    }
                }
            } catch (err) {
                console.error('[WebRTC] Media Init Error:', err);
                toast.error('Camera/Mic access failed');
            }
        };

        if (!hasInitialized.current) {
            hasInitialized.current = true;
            initMedia();
        }

        return () => {
            isStopped = true;
            // Only stop tracks if the component is truly unmounting
            // We use a timeout to check if we are still in a call state in the parent
            // or just rely on onLeave being called.
        };
    }, [roomId, signalingId, isCaller, socket]); // Added socket to dependencies for onicecandidate to work

    // ─── 2. SIGNALING LIFECYCLE (Re-binds on socket changes) ───
    useEffect(() => {
        if (!socket) return;

        const processCandidates = async () => {
            while (candidateBuffer.current.length > 0 && pcRef.current?.remoteDescription) {
                const cand = candidateBuffer.current.shift();
                try { await pcRef.current.addIceCandidate(new RTCIceCandidate(cand)); } catch (e) { }
            }
        };

        const handleOffer = async ({ from, offer }) => {
            const pc = pcRef.current;
            if (!pc || isNegotiating.current || pc.signalingState !== 'stable') return;
            try {
                isNegotiating.current = true;
                await pc.setRemoteDescription(new RTCSessionDescription(offer));
                const answer = await pc.createAnswer();
                await pc.setLocalDescription(answer);
                socket.emit('call:answer', { roomId, to: from, answer });
                processCandidates();
            } catch (e) { console.error('Offer handling failed:', e); }
            finally { isNegotiating.current = false; }
        };

        const handleAnswer = async ({ from, answer }) => {
            const pc = pcRef.current;
            if (!pc || isNegotiating.current || pc.signalingState !== 'have-local-offer') return;
            try {
                isNegotiating.current = true;
                await pc.setRemoteDescription(new RTCSessionDescription(answer));
                processCandidates();
            } catch (e) { console.error('Answer handling failed:', e); }
            finally { isNegotiating.current = false; }
        };

        const handleCandidate = async ({ from, candidate }) => {
            const pc = pcRef.current;
            if (!pc) return;
            if (pc.remoteDescription && pc.remoteDescription.type) {
                try { await pc.addIceCandidate(new RTCIceCandidate(candidate)); } catch (e) { }
            } else {
                candidateBuffer.current.push(candidate);
            }
        };

        const handleCallEnded = () => {
            stopAllTracks();
            onLeave();
        };

        socket.on('call:offer', handleOffer);
        socket.on('call:answer', handleAnswer);
        socket.on('call:candidate', handleCandidate);
        socket.on('call:ended', handleCallEnded);

        return () => {
            socket.off('call:offer', handleOffer);
            socket.off('call:answer', handleAnswer);
            socket.off('call:candidate', handleCandidate);
            socket.off('call:ended', handleCallEnded);
        };
    }, [socket, roomId, signalingId, onLeave]);

    // ─── 3. VIDEO ELEMENT SYNC ───
    useEffect(() => {
        if (localVideoRef.current && localStream) {
            localVideoRef.current.srcObject = localStream;
        }
    }, [localStream]);

    useEffect(() => {
        if (remoteVideoRef.current && remoteStream) {
            remoteVideoRef.current.srcObject = remoteStream;
        }
    }, [remoteStream]);

    // Ensure tracks stop on component unmount
    useEffect(() => {
        return () => stopAllTracks();
    }, []);

    const stopAllTracks = () => {
        if (pcRef.current) {
            pcRef.current.close();
            pcRef.current = null;
        }
        if (localStream) {
            localStream.getTracks().forEach(t => t.stop());
            setLocalStream(null);
        }
        if (screenStreamRef.current) {
            screenStreamRef.current.getTracks().forEach(t => t.stop());
            screenStreamRef.current = null;
        }
        if (localVideoRef.current) localVideoRef.current.srcObject = null;
        if (remoteVideoRef.current) remoteVideoRef.current.srcObject = null;
        setRemoteStream(null);
    };

    const toggleMic = () => {
        if (localStream) {
            const track = localStream.getAudioTracks()[0];
            if (track) {
                track.enabled = !track.enabled;
                setIsMicEnabled(track.enabled);
            }
        }
    };

    const toggleCam = () => {
        if (localStream) {
            const track = localStream.getVideoTracks()[0];
            if (track) {
                track.enabled = !track.enabled;
                setIsCamEnabled(track.enabled);
            }
        }
    };

    const toggleScreenShare = async () => {
        if (!isScreenSharing) {
            try {
                const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
                screenStreamRef.current = screenStream;
                const screenTrack = screenStream.getVideoTracks()[0];

                // Replace video track in PC
                if (pcRef.current) {
                    const sender = pcRef.current.getSenders().find(s => s.track?.kind === 'video');
                    if (sender) sender.replaceTrack(screenTrack);
                }

                // Update local preview
                if (localVideoRef.current) localVideoRef.current.srcObject = screenStream;

                screenTrack.onended = () => stopScreenShare();
                setIsScreenSharing(true);
            } catch (err) {
                console.error('[WebRTC] Screen share failed:', err);
            }
        } else {
            stopScreenShare();
        }
    };

    const stopScreenShare = () => {
        if (screenStreamRef.current) {
            screenStreamRef.current.getTracks().forEach(t => t.stop());
            screenStreamRef.current = null;
        }
        // Replace back with camera track
        if (localStream && pcRef.current) {
            const camTrack = localStream.getVideoTracks()[0];
            const sender = pcRef.current.getSenders().find(s => s.track?.kind === 'video');
            if (sender && camTrack) sender.replaceTrack(camTrack);
            if (localVideoRef.current) localVideoRef.current.srcObject = localStream;
        }
        setIsScreenSharing(false);
    };

    const handleManualLeave = () => {
        socket.emit('call:end', { roomId, to: signalingId });
        stopAllTracks();
        onLeave();
    };

    return (
        <div className="wb-meeting-container">
            <div className="wb-v-grid-main">
                <div className="wb-video-tile">
                    <video ref={localVideoRef} autoPlay muted playsInline className="wb-video-element" />
                    {!isCamEnabled && !isScreenSharing && (
                        <div className="wb-video-placeholder">
                            <div className="wb-v-avatar">{username[0].toUpperCase()}</div>
                        </div>
                    )}
                    <div className="wb-video-info">
                        <span className="wb-video-name">{username} (You)</span>
                        {!isMicEnabled && <MicOff size={10} className="text-red-500" />}
                    </div>
                </div>

                <div className={`wb-video-tile ${remoteStream ? 'active-host' : ''}`}>
                    <video ref={remoteVideoRef} autoPlay playsInline className="wb-video-element" style={{ display: remoteStream ? 'block' : 'none' }} />
                    {!remoteStream && (
                        <div className="wb-video-placeholder">
                            <div className="wb-v-avatar">?</div>
                            <p className="text-xs mt-2 text-white/50">Connecting...</p>
                        </div>
                    )}
                    <div className="wb-video-info"><span className="wb-video-name">Remote Participant</span></div>
                </div>
            </div>

            <div className="wb-video-controls-bar-fixed">
                <div className="wb-video-controls-bar">
                    <button className={`wb-v-btn ${!isMicEnabled ? 'muted' : ''}`} onClick={toggleMic} title="Mute">
                        {isMicEnabled ? <Mic size={18} /> : <MicOff size={18} />}
                    </button>
                    <button className={`wb-v-btn ${!isCamEnabled ? 'muted' : ''}`} onClick={toggleCam} title="Video">
                        {isCamEnabled ? <Video size={18} /> : <VideoOff size={18} />}
                    </button>
                    <button className={`wb-v-btn ${isScreenSharing ? 'active' : ''}`} onClick={toggleScreenShare} title="Share Screen">
                        {isScreenSharing ? <MonitorOff size={18} /> : <Monitor size={18} />}
                    </button>
                    <button className="wb-v-btn leave" onClick={handleManualLeave} title="End Call">
                        <PhoneOff size={18} />
                    </button>
                </div>
            </div>

            <div className="wb-call-status-pill">
                <div className="wb-status-dot" style={{ backgroundColor: connectionStatus.includes('connected') ? '#10b981' : '#f59e0b' }}></div>
                {connectionStatus}
            </div>
        </div>
    );
};

export default WebRTCMeeting;
